INSERT INTO `helpcenter_topic` VALUES ('42002', 'How Airflow in connected with OvalEdge?', '<p><span data-sheets-userformat=\'{\"2\":15293,\"3\":{\"1\":0},\"5\":{\"1\":[{\"1\":2,\"2\":0,\"5\":[null,2,0]},{\"1\":0,\"2\":0,\"3\":3},{\"1\":1,\"2\":0,\"4\":1}]},\"6\":{\"1\":[{\"1\":2,\"2\":0,\"5\":[null,2,0]},{\"1\":0,\"2\":0,\"3\":3},{\"1\":1,\"2\":0,\"4\":1}]},\"7\":{\"1\":[{\"1\":2,\"2\":0,\"5\":[null,2,0]},{\"1\":0,\"2\":0,\"3\":3},{\"1\":1,\"2\":0,\"4\":1}]},\"8\":{\"1\":[{\"1\":2,\"2\":0,\"5\":[null,2,0]},{\"1\":0,\"2\":0,\"3\":3},{\"1\":1,\"2\":0,\"4\":1}]},\"10\":1,\"11\":4,\"12\":0,\"14\":[null,2,16711680],\"15\":\"Arial\",\"16\":12}\' data-sheets-value=\'{\"1\":2,\"2\":\"An out of the box connector is available for Airflow. It provides support for crawling datasets i.e Airflow Dags and tasks and lineage building.\n\nPlease see this article https://support.ovaledge.com/airflow-connector to know more about this connector.\"}\' style=\"font-size: 12pt; font-family: Arial; font-weight: normal; font-style: normal; color: rgb(0, 0, 0); position: static;\">An out of the box connector is available for Airflow. It provides support for crawling datasets i.e. Airflow dags, tasks and lineage building.<br style=\"position: static;\"><br style=\"position: static;\">Please see this article <a href=\"https://support.ovaledge.com/airflow-connector\" rel=\"noopener noreferrer\" target=\"_blank\">Airflow Connector</a> to know more about this connector.</span></p>', 'airflowConnection', '1', '', '', 'admin', '2022-05-30 13:48:22', '2022-05-30 13:48:22', null);
INSERT INTO `helpcenter_topic` VALUES ('42003', 'How AllScripts is connected with OvalEdge?', '<p><span data-sheets-userformat=\'{\"2\":15293,\"3\":{\"1\":0},\"5\":{\"1\":[{\"1\":2,\"2\":0,\"5\":[null,2,0]},{\"1\":0,\"2\":0,\"3\":3},{\"1\":1,\"2\":0,\"4\":1}]},\"6\":{\"1\":[{\"1\":2,\"2\":0,\"5\":[null,2,0]},{\"1\":0,\"2\":0,\"3\":3},{\"1\":1,\"2\":0,\"4\":1}]},\"7\":{\"1\":[{\"1\":2,\"2\":0,\"5\":[null,2,0]},{\"1\":0,\"2\":0,\"3\":3},{\"1\":1,\"2\":0,\"4\":1}]},\"8\":{\"1\":[{\"1\":2,\"2\":0,\"5\":[null,2,0]},{\"1\":0,\"2\":0,\"3\":3},{\"1\":1,\"2\":0,\"4\":1}]},\"10\":1,\"11\":4,\"12\":0,\"14\":[null,2,16711680],\"15\":\"Arial\",\"16\":12}\' data-sheets-value=\'{\"1\":2,\"2\":\"An out of the box connector is available for the AllScripts database. It provides support for crawling database objects, profiling of data. The underlying database is InterSystems Cach&#233; DB. The connectivity to Allscripts is via JDBC driver, which is included in the OvalEdge platform. \n\nPlease see this article https://support.ovaledge.com/allscripts to know more about this connector.\"}\' style=\"font-size: 12pt; font-family: Arial; font-weight: normal; font-style: normal; color: rgb(0, 0, 0); position: static;\">An out of the box connector is available for the AllScripts database. It provides support for crawling database objects, profiling of data. The underlying database is InterSystems Cach&eacute; DB. The connectivity to Allscripts is via JDBC driver, which is included in the OvalEdge platform. <br style=\"position: static;\"><br style=\"position: static;\">Please see this article <a href=\"https://support.ovaledge.com/allscripts\" rel=\"noopener noreferrer\" target=\"_blank\">AllScripts Connector</a> to know more about this connector.</span></p>', 'AllscriptsConnection', '1', '', '', 'admin', '2022-05-30 13:48:22', '2022-05-30 13:48:22', null);
INSERT INTO `helpcenter_topic` VALUES ('42004', 'How Alteryx is connected with OvalEdge?', '<p><span data-sheets-userformat=\'{\"2\":15165,\"3\":{\"1\":0},\"5\":{\"1\":[{\"1\":2,\"2\":0,\"5\":[null,2,0]},{\"1\":0,\"2\":0,\"3\":3},{\"1\":1,\"2\":0,\"4\":1}]},\"6\":{\"1\":[{\"1\":2,\"2\":0,\"5\":[null,2,0]},{\"1\":0,\"2\":0,\"3\":3},{\"1\":1,\"2\":0,\"4\":1}]},\"7\":{\"1\":[{\"1\":2,\"2\":0,\"5\":[null,2,0]},{\"1\":0,\"2\":0,\"3\":3},{\"1\":1,\"2\":0,\"4\":1}]},\"8\":{\"1\":[{\"1\":2,\"2\":0,\"5\":[null,2,0]},{\"1\":0,\"2\":0,\"3\":3},{\"1\":1,\"2\":0,\"4\":1}]},\"11\":4,\"12\":0,\"14\":[null,2,16711680],\"15\":\"Arial\",\"16\":12}\' data-sheets-value=\'{\"1\":2,\"2\":\"An out of the box connector is available for Alteryx. It provides support for crawling datasets i.e. dataflows, datasets, and lineage building. The connectivity to Alteryx is via Created Workflow Files from Alteryx Studio, which is included in the platform. Alteryx Studio is a windows application.\r\n\r\nPlease see this article https://support.ovaledge.com/alteryx to know more about this connector.\"}\' style=\"font-size: 12pt; font-family: Arial; font-weight: normal; font-style: normal; color: rgb(0, 0, 0); position: static;\">An out of the box connector is available for Alteryx. It provides support for crawling datasets i.e. dataflows, datasets, and lineage building. The connectivity to Alteryx is via Created Workflow Files from Alteryx Studio, which is included in the platform. Alteryx Studio is a windows application. <br style=\"position: static;\"> <br style=\"position: static;\">Please see this article <a href=\"https://support.ovaledge.com/alteryx\" rel=\"noopener noreferrer\" target=\"_blank\">Alteryx Connector</a> to know more about this connector.</span></p>', 'alteryxConnection', '1', '', '', 'admin', '2022-05-30 13:48:22', '2022-05-30 13:48:22', null);
