INSERT INTO `helpcenter_topic` VALUES ('42014', 'How Cloudera is connected with OvalEdge?', '<p><span data-sheets-userformat=\'{\"2\":15165,\"3\":{\"1\":0},\"5\":{\"1\":[{\"1\":2,\"2\":0,\"5\":[null,2,0]},{\"1\":0,\"2\":0,\"3\":3},{\"1\":1,\"2\":0,\"4\":1}]},\"6\":{\"1\":[{\"1\":2,\"2\":0,\"5\":[null,2,0]},{\"1\":0,\"2\":0,\"3\":3},{\"1\":1,\"2\":0,\"4\":1}]},\"7\":{\"1\":[{\"1\":2,\"2\":0,\"5\":[null,2,0]},{\"1\":0,\"2\":0,\"3\":3},{\"1\":1,\"2\":0,\"4\":1}]},\"8\":{\"1\":[{\"1\":2,\"2\":0,\"5\":[null,2,0]},{\"1\":0,\"2\":0,\"3\":3},{\"1\":1,\"2\":0,\"4\":1}]},\"11\":4,\"12\":0,\"14\":[null,2,16711680],\"15\":\"Arial\",\"16\":12}\' data-sheets-value=\'{\"1\":2,\"2\":\"Cloudera Navigator console audits includes events collected by Cloudera Manager:\r\n1. Service lifecycle events: activate, create, delete, deploy, download, install, start, stop, update, upgrade etc.\r\n2. User security related events: add / delete user, login fail / success\r\n\r\nOvalEdge will make a request to the Cloudera navigator using Rest API.\r\n\r\nPlease see this article https://support.ovaledge.com/cloudera to know more about this connector.\"}\' style=\"font-size: 12pt; font-family: Arial; font-weight: normal; font-style: normal; color: rgb(0, 0, 0); position: static;\">Cloudera Navigator console audits includes events collected by Cloudera Manager: <br style=\"position: static;\">1. Service lifecycle events: activate, create, delete, deploy, download, install, start, stop, update, upgrade etc. <br style=\"position: static;\">2. User security related events: add / delete user, login fail / success <br style=\"position: static;\"> <br style=\"position: static;\">OvalEdge will make a request to the Cloudera navigator using Rest API. <br style=\"position: static;\"> <br style=\"position: static;\">Please see this article <a href=\"https://support.ovaledge.com/cloudera\" rel=\"noopener noreferrer\" target=\"_blank\">Cloudera Connector</a> to know more about this connector.</span></p>', 'cloudera_navigatorConnection', '1', '', '', 'admin', '2022-05-30 13:48:22', '2022-05-30 13:48:22', null);
INSERT INTO `helpcenter_topic` VALUES ('42015', 'How CouchDB is connected with OvalEdge?', '<p><span data-sheets-userformat=\'{\"2\":15165,\"3\":{\"1\":0},\"5\":{\"1\":[{\"1\":2,\"2\":0,\"5\":[null,2,0]},{\"1\":0,\"2\":0,\"3\":3},{\"1\":1,\"2\":0,\"4\":1}]},\"6\":{\"1\":[{\"1\":2,\"2\":0,\"5\":[null,2,0]},{\"1\":0,\"2\":0,\"3\":3},{\"1\":1,\"2\":0,\"4\":1}]},\"7\":{\"1\":[{\"1\":2,\"2\":0,\"5\":[null,2,0]},{\"1\":0,\"2\":0,\"3\":3},{\"1\":1,\"2\":0,\"4\":1}]},\"8\":{\"1\":[{\"1\":2,\"2\":0,\"5\":[null,2,0]},{\"1\":0,\"2\":0,\"3\":3},{\"1\":1,\"2\":0,\"4\":1}]},\"11\":4,\"12\":0,\"14\":[null,2,16711680],\"15\":\"Arial\",\"16\":12}\' data-sheets-value=\'{\"1\":2,\"2\":\"A CouchDB server hosts named databases, which store documents. Each document is uniquely named in the database, and CouchDB provides a RESTful HTTP API for reading and updating (add, edit, delete) database documents. Ovaledge uses Rest API to make a connection to a running CouchDB instance.\r\n\r\nPlease see this article https://support.ovaledge.com/couchdb to know more about this connector.\"}\' style=\"font-size: 12pt; font-family: Arial; font-weight: normal; font-style: normal; color: rgb(0, 0, 0); position: static;\">A CouchDB server hosts named databases, which store documents. Each document is uniquely named in the database, and CouchDB provides a RESTful HTTP API for reading and updating (add, edit, delete) database documents. Ovaledge uses Rest API to make a connection to a running CouchDB instance. <br style=\"position: static;\"> <br style=\"position: static;\">Please see this article <a href=\"https://support.ovaledge.com/couchdb\" rel=\"noopener noreferrer\" target=\"_blank\">CouchDB Connector</a> to know more about this connector.</span></p>', 'couchdbConnection', '1', '', '', 'admin', '2022-05-30 13:48:22', '2022-05-30 13:48:22', null);
