INSERT INTO `helpcenter_topic` VALUES ('50186', 'How to view the history of Tags?', '<p><span data-sheets-userformat=\'{\"2\":3069,\"3\":{\"1\":0},\"5\":{\"1\":[{\"1\":2,\"2\":0,\"5\":{\"1\":2,\"2\":0}},{\"1\":0,\"2\":0,\"3\":3},{\"1\":1,\"2\":0,\"4\":1}]},\"6\":{\"1\":[{\"1\":2,\"2\":0,\"5\":{\"1\":2,\"2\":0}},{\"1\":0,\"2\":0,\"3\":3},{\"1\":1,\"2\":0,\"4\":1}]},\"7\":{\"1\":[{\"1\":2,\"2\":0,\"5\":{\"1\":2,\"2\":0}},{\"1\":0,\"2\":0,\"3\":3},{\"1\":1,\"2\":0,\"4\":1}]},\"8\":{\"1\":[{\"1\":2,\"2\":0,\"5\":{\"1\":2,\"2\":0}},{\"1\":0,\"2\":0,\"3\":3},{\"1\":1,\"2\":0,\"4\":1}]},\"9\":0,\"10\":0,\"11\":0,\"12\":0,\"14\":{\"1\":2,\"2\":16711680}}\' data-sheets-value=\'{\"1\":2,\"2\":\"To view the history of a particular tag, click the Tag icon which are assigned with Data assets that will bring you to the tag History Page. It shows the list of all tags that have been added/removed so far.\"}\' style=\"font-size: 14px; font-family: Roboto, sans-serif; font-style: normal; color: rgb(0, 0, 0); position: static;\">To view the history of a particular tag, click the Tag icon which are assigned with Data assets that will bring you to the tag History Page. It shows the list of all tags that have been added/removed so far.</span> </p>', 'gettaghistory', '1', '', '', 'admin', '2022-05-30 13:48:22', '2022-05-30 13:48:22', null);
