INSERT INTO `helpcenter_topic` VALUES ('50189', 'How to view All Users in OvalEdge Application?', '<p><span style=\"color: rgb(0, 0, 0); font-family: Roboto, sans-serif; font-size: 14px;\">To view the list of All Users of a particular Schema/Table/Report/Files/Queries, click the Top Users icon that will bring you to the All Users Page. It shows the list of all Users that have been visited particular asset so far along with count of queries performed and collaboration. </span> </p>', 'sq.allusers', '1', '', '', 'admin', '2022-05-30 13:48:22', '2022-05-30 13:48:22', null);
