INSERT INTO `stg_oereportext` VALUES ('1000', 'Top 20 users by lifetime visits', '-1', 'OvalEdge Reports', '\nThis is a User activity report that displays the total number of times a user has visited a table, file and report page. The bar chart lists the top 20 users and their maximum visits.\nSemi-Detail:\nThe details display the user name and the object details.\n Report Drill-Down Details:\nSelecting a user on the bar chart will show the username, each object type and individual count of visits for each data object.', 'select userid as USERID, coalesce(objecttype,\"\") as objecttype, count(*) as \"ObjectTypeViewed\" from\n(select userid,concat(databasename,\".\",objectname) as objectname , objecttype , totalviewed from\n(select O.userid,C.servertype as databasename ,S.schemaname as objectname,\ncase when O.objecttype = \"oeschema\" then \"schema\" end as objecttype,\nnumberlooked as totalviewed from objectuserstats O\njoin user U on U.userid = O.userid\njoin oeschema S on O.objectid = S.oeschemaid\njoin connectioninfo C on C.connectioninfoid=S.connectioninfoid\nwhere O.objecttype=\"oeschema\" and U.status=\"ACTIVE\"\n)A\nunion\nselect userid,concat(databasename,\".\",schemaname,\".\",objectname) as objectname,objecttype,totalviewed from\n(select ta.userid,C.servertype as databasename, S.schemaname , T.tablename as objectname,T.type as objecttype,numberlooked as totalviewed from tableuserstats ta\njoin user U on U.userid = ta.userid\njoin oetable T on T.oetableid=ta.oetableid\njoin oeschema S on S.oeschemaid=T.oeschemaid\njoin connectioninfo C on C.connectioninfoid=S.connectioninfoid\nwhere T.type=\"TABLE\" and U.status=\"ACTIVE\"\n)B\nunion\nselect userid,concat(bucketname,\".\",filefolder,\".\",objectname) as objectname,objecttype,totalviewed from\n(select fi.userid,C.servertype as bucketname,F.location as filefolder,F.filename as objectname, F.filetype as objecttype , numberlooked as totalviewed\nfrom fileuserstats fi\njoin user U on U.userid = fi.userid\njoin file F on F.fileid = fi.fileid\njoin filefolder FF on FF.filefolderid=F.filefolderid\njoin connectioninfo C on C.connectioninfoid=FF.connectioninfoid\nwhere U.status=\"ACTIVE\"\n)X\nunion\nselect userid,concat(databasename,\".\",reportgroup,\".\",objectname) as objectname,objecttype,totalviewed from\n(select ch.userid,ci.servertype as databasename,D.`VALUE` as reportgroup,C.CHARTNAME as objectname, C.GENRE as objecttype,numberlooked as totalviewed\nfrom chartuserstats ch\njoin user U on U.userid = ch.userid\njoin chart C on C.CHARTID=ch.chartid\njoin domain D on D.DOMAINID=C.DOMAINID\njoin connectioninfo ci on ci.connectioninfoid=D.connectioninfoid\nWhere U.status=\"ACTIVE\"\n)Y)Z group by userid , coalesce(objecttype,\"\")', 'select userid as username,objectname,coalesce(objecttype,\"\") as objecttype,totalviewed from\n(select userid,concat(databasename,\".\",objectname) as objectname , objecttype , totalviewed from\n(select O.userid,C.servertype as databasename ,S.schemaname as objectname,\ncase when O.objecttype = \"oeschema\" then \"schema\" end as objecttype,\nnumberlooked as totalviewed from objectuserstats O\njoin user U on U.userid = O.userid\njoin oeschema S on O.objectid = S.oeschemaid\njoin connectioninfo C on C.connectioninfoid=S.connectioninfoid\nwhere O.objecttype=\"oeschema\" and U.status=\"ACTIVE\"\n) A\nunion\nselect userid,concat(databasename,\".\",schemaname,\".\",objectname) as objectname,objecttype,totalviewed from\n(select ta.userid,C.servertype as databasename, S.schemaname , T.tablename as objectname,T.type as objecttype,numberlooked as totalviewed from tableuserstats ta\njoin user U on U.userid = ta.userid\njoin oetable T on T.oetableid=ta.oetableid\njoin oeschema S on S.oeschemaid=T.oeschemaid\njoin connectioninfo C on C.connectioninfoid=S.connectioninfoid\nwhere T.type=\"TABLE\" and U.status=\"ACTIVE\"\n)B\nunion\nselect userid,concat(bucketname,\".\",filefolder,\".\",objectname) as objectname,objecttype,totalviewed from\n(select fi.userid,C.servertype as bucketname,F.location as filefolder,F.filename as objectname, F.filetype as objecttype ,numberlooked as totalviewed\nfrom fileuserstats fi\njoin user U on U.userid = fi.userid\njoin file F on F.fileid = fi.fileid\njoin filefolder FF on FF.filefolderid=F.filefolderid\njoin connectioninfo C on C.connectioninfoid=FF.connectioninfoid\nwhere U.status=\"ACTIVE\"\n)X\nunion\nselect userid,concat(databasename,\".\",reportgroup,\".\",objectname) as objectname,objecttype,totalviewed from\n(select ch.userid,ci.servertype as databasename,D.`VALUE` as reportgroup,C.CHARTNAME as objectname, C.GENRE as objecttype,numberlooked as totalviewed\nfrom chartuserstats ch\njoin user U on U.userid = ch.userid\njoin chart C on C.CHARTID=ch.chartid\njoin domain D on D.DOMAINID=C.DOMAINID\njoin connectioninfo ci on ci.connectioninfoid=D.connectioninfoid\nWhere U.status=\"ACTIVE\"\n)Y)Z\nwhere Z.userid=?', 'select Z.userid,sum(COALESCE(Z.totalviewed,0)) as totalviewed from\n(select userid,concat(databasename,\".\",objectname) as objectname , objecttype , totalviewed from\n(select O.userid,C.servertype as databasename ,S.schemaname as objectname,\ncase when O.objecttype = \"oeschema\" then \"schema\" end as objecttype,\nnumberlooked as totalviewed from objectuserstats O\njoin user U on U.userid = O.userid\njoin oeschema S on O.objectid = S.oeschemaid\njoin connectioninfo C on C.connectioninfoid=S.connectioninfoid\nwhere O.objecttype=\"oeschema\" and U.status=\"ACTIVE\"\n) A\nunion\nselect userid,concat(databasename,\".\",schemaname,\".\",objectname) as objectname,objecttype,totalviewed from\n(select ta.userid,C.servertype as databasename, S.schemaname , T.tablename as objectname,T.type as objecttype,numberlooked as totalviewed from tableuserstats ta\njoin user U on U.userid = ta.userid\njoin oetable T on T.oetableid=ta.oetableid\njoin oeschema S on S.oeschemaid=T.oeschemaid\njoin connectioninfo C on C.connectioninfoid=S.connectioninfoid\nwhere T.type=\"TABLE\" and U.status=\"ACTIVE\"\n)B\nunion\nselect userid,concat(bucketname,\".\",filefolder,\".\",objectname) as objectname,objecttype,totalviewed from\n(select fi.userid,C.servertype as bucketname,F.location as filefolder,F.filename as objectname, F.filetype as objecttype , numberlooked as totalviewed\nfrom fileuserstats fi\njoin user U on U.userid = fi.userid\njoin file F on F.fileid = fi.fileid\njoin filefolder FF on FF.filefolderid=F.filefolderid\njoin connectioninfo C on C.connectioninfoid=FF.connectioninfoid\nWhere U.status=\"ACTIVE\"\n)X\nunion\nselect userid,concat(databasename,\".\",reportgroup,\".\",objectname) as objectname,objecttype,totalviewed from\n(select ch.userid,ci.servertype as databasename,D.`VALUE` as reportgroup,C.CHARTNAME as objectname, C.GENRE as objecttype,numberlooked as totalviewed\nfrom chartuserstats ch\njoin user U on U.userid = ch.userid\njoin chart C on C.CHARTID=ch.chartid\njoin domain D on D.DOMAINID=C.DOMAINID\njoin connectioninfo ci on ci.connectioninfoid=D.connectioninfoid\nWhere U.status=\"ACTIVE\"\n)Y)Z\ngroup by Z.userid\nOrder By totalviewed DESC\nlimit 20', '0', 'Invalid input', null);
INSERT INTO `stg_oereportext` VALUES ('1001', 'Top Content Editors', '-1', 'OvalEdge Reports', 'This is a Content Management activity report. This report displays the total number of times each user has authored/updated business and technical descriptions of tables, and columns. Additionally, it shows a count of glossary terms that the user has authored/updated with each table or column in the OvalEdge application.', 'select u.userid as Username ,COALESCE(max(d.TableBD),0) as \"# of Tables BD\",COALESCE(max(d.TableTD),0) as \"# of Tables TD\",COALESCE(max(c.ColumnBD),0) \"# of Columns BD\",COALESCE(max(c.ColumnTD),0) as \"# of Columns TD\",\nCOALESCE(max(A.countoftableterm),0) as \"# of Tables with Term Description\" ,COALESCE(max(B.Columntermcount),0) \"# of Columns with Term Description\" from  user u\nleft join (select user.userid,coalesce(count( case when oterm.action=\"ADD\" then objectid  end)-count( case when oterm.action=\"DELETE\" then objectid end),0) \"countoftableterm\" from a_objectterm oterm  \ninner join businessglossary bg on (oterm. businessglossaryid=bg.businessglossaryid)\ninner join user on oterm.audituser=user.USERID\ninner join oetable t on (t.oetableid=oterm.objectid)\ninner join oeschema s on (s.oeschemaid=t.oeschemaid)\ninner join connectioninfo ci on (ci.connectioninfoid=s.connectioninfoid)\nwhere objecttype=\"oetable\" and objectid in\n(select t.oetableid\nfrom  oetable t\njoin  oeschema s on (t.oeschemaid = s.oeschemaid)\njoin connectioninfo ci on (s.connectioninfoid = ci.connectioninfoid))\nAND bg.status = \"PUBLISHED\"\ngroup by user.userid\n) A on (u.userid=A.userid)  left join\n(select user.userid, coalesce(count(case when oterm.action=\"ADD\" then objectid end)-count(case when oterm.action=\"DELETE\" then objectid end),0) \"Columntermcount\" from a_objectterm oterm\ninner join businessglossary bg on (oterm. businessglossaryid=bg.businessglossaryid)  \ninner join user on oterm.audituser=user.USERID\ninner join oecolumn c on (c.oecolumnid=oterm.objectid)\ninner join oetable t on (t.oetableid=c.oetableid)\ninner join oeschema s on (s.oeschemaid=t.oeschemaid)\ninner join connectioninfo ci on (ci.connectioninfoid=s.connectioninfoid)\nwhere objecttype=\"oecolumn\" and objectid in\n(select c.oecolumnid\nfrom oecolumn c\njoin oetable t on (c.oetableid = t.oetableid )\njoin oeschema s on (t.oeschemaid = s.oeschemaid)\njoin connectioninfo ci on (s.connectioninfoid = ci.connectioninfoid))\nAND bg.status = \"PUBLISHED\"\ngroup by user.userid\n)  B on u.userid=B.userid left join\n(select  user.userid,\nsum(case when wikitext is not null   then 1  end ) \"ColumnBD\",\n0 ColumnTD\nfrom wiki wiki   inner join user on wiki.lastupdateuser =user.USERID\ninner join oecolumn c on (c.oecolumnid=wiki.wikiobjectid)\ninner join oetable t on (t.oetableid=c.oetableid)\ninner join oeschema s on (s.oeschemaid=t.oeschemaid)\ninner join connectioninfo ci on (ci.connectioninfoid=s.connectioninfoid)\nwhere wikiobject=\"oecolumn\" and  wikiobjectid not in (select objectid from a_objectterm where objecttype = \"oecolumn\" and audituser=`user`.userid ) and (wikitext <> \" \" ) and wikiobjectid in\n(select c.oecolumnid\nfrom oecolumn c\njoin oetable t on (c.oetableid = t.oetableid )\njoin oeschema s on (t.oeschemaid = s.oeschemaid)\njoin connectioninfo ci on (s.connectioninfoid = ci.connectioninfoid))\ngroup by user.userid\nUNION\nselect  user.userid,\n0 AS ColumnBD,\nsum(case when techtext is not null  then 1  end ) \"ColumnTD\"\nfrom wiki wiki   inner join user on wiki.techlastupdateuser =user.USERID\ninner join oecolumn c on (c.oecolumnid=wiki.wikiobjectid)\ninner join oetable t on (t.oetableid=c.oetableid)\ninner join oeschema s on (s.oeschemaid=t.oeschemaid)\ninner join connectioninfo ci on (ci.connectioninfoid=s.connectioninfoid)\nwhere wikiobject=\"oecolumn\" and ( techtext <>\" \") and wikiobjectid in\n(select c.oecolumnid\nfrom oecolumn c\njoin oetable t on (c.oetableid = t.oetableid )\njoin oeschema s on (t.oeschemaid = s.oeschemaid)\njoin connectioninfo ci on (s.connectioninfoid = ci.connectioninfoid))\ngroup by user.userid\n) c on (u.userid=c.userid) left join\n(select user.userid,count(wikiid) TableBD,0 as \"TableTD\" from wiki wiki  inner join user on wiki.lastupdateuser=user.USERID\ninner join oetable t on (t.oetableid=wiki.wikiobjectid)\ninner join oeschema s on (s.oeschemaid=t.oeschemaid)\ninner join connectioninfo ci on  (ci.connectioninfoid=s.connectioninfoid)\nwhere wikiobject=\"oetable\"\nand wikitext is not null and wikitext <> \"\"\nand  wikiobjectid not in (select objectid from a_objectterm where objecttype = \"oetable\" and audituser=`user`.userid )\nand wikiobjectid in\n(select t.oetableid\nfrom oetable t\njoin oeschema s on (t.oeschemaid = s.oeschemaid)\njoin connectioninfo ci on (s.connectioninfoid = ci.connectioninfoid))\ngroup by user.userid\nunion \nselect user.userid,0 TableBD,count(wikiid) as \"TableTD\" from wiki wiki  inner join user on wiki.techlastupdateuser=user.USERID\ninner join oetable t on (t.oetableid=wiki.wikiobjectid)\ninner join oeschema s on (s.oeschemaid=t.oeschemaid)\ninner join connectioninfo ci on  (ci.connectioninfoid=s.connectioninfoid)\nwhere wikiobject=\"oetable\"\nand techtext is not null and techtext <> \" \"\nand  wikiobjectid not in (select objectid from a_objectterm where objecttype = \"oetable\" and audituser=`user`.userid )\nand wikiobjectid in\n(select t.oetableid\nfrom oetable t\njoin oeschema s on (t.oeschemaid = s.oeschemaid)\njoin connectioninfo ci on (s.connectioninfoid = ci.connectioninfoid))\ngroup by user.userid\n) d on (u.userid=d.userid)\ngroup by u.USERID\nhaving sum(d.TableBD) is not NULL\nor sum(c.ColumnBD) is not null\nor sum(c.ColumnTD) is not NULL\nor sum(A.countoftableterm) is not null\nor sum(B.Columntermcount) is not null', 'select userid as Username,Schemaname as `Schema`,Tablename as `Table`,Columnname as `Column`,max(Tbusinessglossaryname)  as `TableTerm`,max(Cbusinessglossaryname) as `ColumnTerm`,max(COALESCE(wikitext,\"\")) as `Business Description`,max(COALESCE(techtext,\"\")) as `Technical Description`  from\n(select userid,Schemaname,tablename,columnname,Tbusinessglossaryname,cbusinessglossaryname,wikitext,techtext from\n(select  user.userid,Schemaname,tablename,columnname,\n\"\" as Tbusinessglossaryname,\"\" as Cbusinessglossaryname,wiki.wikitext,\" \" as techtext\nfrom wiki wiki   inner join user on wiki.lastupdateuser =user.USERID\ninner join oecolumn c on (c.oecolumnid=wiki.wikiobjectid)\ninner join oetable t on (t.oetableid=c.oetableid)\ninner join oeschema s on (s.oeschemaid=t.oeschemaid)\ninner join connectioninfo ci on (ci.connectioninfoid=s.connectioninfoid)\nwhere wikiobject=\"oecolumn\" and (wikitext <> \" \" and wikitext is not null ) \nand c.oecolumnid not in (select oecolumnid from oecolumn inner join businessglossary on (oecolumn.businessglossaryid=businessglossary.businessglossaryid) where businessglossary.`status`=\"DRAFT\" )\nand wikiobjectid in\n(select c.oecolumnid\nfrom oecolumn c\njoin oetable t on (c.oetableid = t.oetableid )\njoin oeschema s on (t.oeschemaid = s.oeschemaid)\njoin connectioninfo ci on (s.connectioninfoid = ci.connectioninfoid))\nUNION\nselect  user.userid,Schemaname,tablename,columnname,\n\"\" as Tbusinessglossaryname,\"\" as Cbusinessglossaryname,\" \" as wikitext,wiki.techtext\nfrom wiki wiki   inner join user on wiki.techlastupdateuser =user.USERID\ninner join oecolumn c on (c.oecolumnid=wiki.wikiobjectid)\ninner join oetable t on (t.oetableid=c.oetableid)\ninner join oeschema s on (s.oeschemaid=t.oeschemaid)\ninner join connectioninfo ci on (ci.connectioninfoid=s.connectioninfoid)\nleft join businessglossary b on (b.businessglossaryid=c.businessglossaryid)\nwhere wikiobject=\"oecolumn\" and ( techtext <>\" \")  \nand wikiobjectid in\n(select c.oecolumnid\nfrom oecolumn c\njoin oetable t on (c.oetableid = t.oetableid )\njoin oeschema s on (t.oeschemaid = s.oeschemaid)\njoin connectioninfo ci on (s.connectioninfoid = ci.connectioninfoid))\n) A\nunion\nselect userid,Schemaname,tablename,columnname,Tbusinessglossaryname,Cbusinessglossaryname,wikitext,techtext from\n(select user.userid,Schemaname,tablename,\ncolumnname,\"\" as Tbusinessglossaryname,\nb.name as Cbusinessglossaryname,\"\" as wikitext,\"\" as techtext from a_objectterm oterm\ninner join user on oterm.audituser=user.USERID\ninner join oecolumn c on (c.oecolumnid=oterm.objectid)\ninner join oetable t on (t.oetableid=c.oetableid)\ninner join oeschema s on (s.oeschemaid=t.oeschemaid)\ninner join connectioninfo ci on (ci.connectioninfoid=s.connectioninfoid)\ninner join businessglossary b on (b.businessglossaryid=oterm.businessglossaryid)\nwhere objecttype=\"oecolumn\"  AND b.status = \"PUBLISHED\" and objectid in\n(select c.oecolumnid\nfrom oecolumn c\njoin oetable t on (c.oetableid = t.oetableid )\njoin oeschema s on (t.oeschemaid = s.oeschemaid)\njoin connectioninfo ci on (s.connectioninfoid = ci.connectioninfoid))\n and   (oterm.businessglossaryid,auditdate)  in (select oterm.businessglossaryid,max(auditdate) from a_objectterm  oterm \nwhere  oterm.objecttype=\"oecolumn\" and oterm.audituser=`user`.userid  group by businessglossaryid,objectid order by auditdate desc   )\nand action=\"ADD\"\nunion\nselect user.userid,Schemaname,tablename,\"\" as columnname,b.name as Tbusinessglossaryname,\n\"\" as Cbusinessglossaryname,\"\" as wikitext, \"\" as techtext from a_objectterm oterm  \ninner join user on oterm.audituser=user.USERID\ninner join oetable t on (t.oetableid=oterm.objectid)\ninner join oeschema s on (s.oeschemaid=t.oeschemaid)\ninner join connectioninfo ci on (ci.connectioninfoid=s.connectioninfoid)\ninner join businessglossary b on (b.businessglossaryid=oterm.businessglossaryid)\nwhere objecttype=\"oetable\" \n AND b.status = \"PUBLISHED\" \nand objectid in\n(select t.oetableid\nfrom  oetable t\njoin  oeschema s on (t.oeschemaid = s.oeschemaid)\njoin connectioninfo ci on (s.connectioninfoid = ci.connectioninfoid))\nand   (oterm.businessglossaryid,auditdate)  in (select oterm.businessglossaryid,max(auditdate) from a_objectterm  oterm \nwhere  oterm.objecttype=\"oetable\" and oterm.audituser=`user`.userid  group by businessglossaryid,objectid order by auditdate desc   )\nand action=\"ADD\"\n)B\nunion\nselect userid,Schemaname,tablename,columnname,Tbusinessglossaryname,Cbusinessglossaryname,wikitext,techtext from\n(select user.userid,Schemaname,tablename,\"\" as columnname,\n\"\" as Tbusinessglossaryname,\"\" as Cbusinessglossaryname,wiki.wikitext,\" \" as  techtext from wiki wiki  \ninner join user on wiki.lastupdateuser=user.USERID\ninner join oetable t on (t.oetableid=wiki.wikiobjectid)\ninner join oeschema s on (s.oeschemaid=t.oeschemaid)\ninner join connectioninfo ci on  (ci.connectioninfoid=s.connectioninfoid)\nleft join businessglossary b on (b.businessglossaryid=t.businessglossaryid)\nwhere wikiobject=\"oetable\" and t.oetableid not in (select oetableid from oetable inner join businessglossary on (oetable.businessglossaryid=businessglossary.businessglossaryid) where businessglossary.`status`=\"DRAFT\" )\nand wikitext is not null and wikitext <> \"\"\nand wikiobjectid in\n(select t.oetableid\nfrom oetable t\njoin oeschema s on (t.oeschemaid = s.oeschemaid)\njoin connectioninfo ci on (s.connectioninfoid = ci.connectioninfoid))\nunion ALL\nselect user.userid,Schemaname,tablename,\"\" as columnname,\n\"\" as Tbusinessglossaryname,\"\" as Cbusinessglossaryname,\" \" as wikitext,wiki.techtext as  techtext from wiki wiki  \ninner join user on wiki.techlastupdateuser=user.USERID\ninner join oetable t on (t.oetableid=wiki.wikiobjectid)\ninner join oeschema s on (s.oeschemaid=t.oeschemaid)\ninner join connectioninfo ci on  (ci.connectioninfoid=s.connectioninfoid)\nleft join businessglossary b on (b.businessglossaryid=t.businessglossaryid)\nwhere wikiobject=\"oetable\" \nand techtext is not null and techtext <> \" \"\nand wikiobjectid in\n(select t.oetableid\nfrom oetable t\njoin oeschema s on (t.oeschemaid = s.oeschemaid)\njoin connectioninfo ci on (s.connectioninfoid = ci.connectioninfoid))\n)C\n)X\nwhere X.userid=? group by userid,Schemaname,tablename,columnname', 'select userid as Username,sum(count) as Count from (select userid,sum(count) \"Count\" from\n(SELECT userid,sum(ColumnBD)+sum(ColumnTD) \"count\" from (select  user.userid,\nsum(case when wikitext is not null   then 1  end ) \"ColumnBD\",\n0 ColumnTD\nfrom wiki wiki   inner join user on wiki.lastupdateuser =user.USERID\ninner join oecolumn c on (c.oecolumnid=wiki.wikiobjectid)\ninner join oetable t on (t.oetableid=c.oetableid)\ninner join oeschema s on (s.oeschemaid=t.oeschemaid)\ninner join connectioninfo ci on (ci.connectioninfoid=s.connectioninfoid)\nwhere wikiobject=\"oecolumn\" and  wikiobjectid not in (select objectid from a_objectterm where objecttype = \"oecolumn\" and audituser=`user`.userid ) and (wikitext <> \" \" ) and wikiobjectid in\n(select c.oecolumnid\nfrom oecolumn c\njoin oetable t on (c.oetableid = t.oetableid )\njoin oeschema s on (t.oeschemaid = s.oeschemaid)\njoin connectioninfo ci on (s.connectioninfoid = ci.connectioninfoid))\ngroup by user.userid\nUNION\nselect  user.userid,\n0 AS ColumnBD,\nsum(case when techtext is not null  then 1  end ) \"ColumnTD\"\nfrom wiki wiki   inner join user on wiki.techlastupdateuser =user.USERID\ninner join oecolumn c on (c.oecolumnid=wiki.wikiobjectid)\ninner join oetable t on (t.oetableid=c.oetableid)\ninner join oeschema s on (s.oeschemaid=t.oeschemaid)\ninner join connectioninfo ci on (ci.connectioninfoid=s.connectioninfoid)\nwhere wikiobject=\"oecolumn\" and ( techtext <>\" \") and wikiobjectid in\n(select c.oecolumnid\nfrom oecolumn c\njoin oetable t on (c.oetableid = t.oetableid )\njoin oeschema s on (t.oeschemaid = s.oeschemaid)\njoin connectioninfo ci on (s.connectioninfoid = ci.connectioninfoid))\ngroup by user.userid) A\ngroup by userid\nunion all\nselect userid,sum(count) from (select user.userid,coalesce(count(case when oterm.action=\"ADD\" then objectid end)-count(case when oterm.action=\"DELETE\" then objectid end),0) \"count\"\nfrom a_objectterm oterm  \ninner join businessglossary bg on (oterm. businessglossaryid=bg.businessglossaryid)\ninner join user on oterm.audituser=user.USERID\nwhere objecttype=\"oecolumn\"\nAND bg.status = \"PUBLISHED\"\nand objectid in\n(select c.oecolumnid\nfrom oecolumn c\njoin oetable t on (c.oetableid = t.oetableid )\njoin oeschema s on (t.oeschemaid = s.oeschemaid)\njoin connectioninfo ci on (s.connectioninfoid = ci.connectioninfoid)\nwhere c.active=1\n)  group by `user`.userid) A\ngroup by userid\nunion all\nselect userid,sum(count) from (select user.userid,coalesce(count(  case when oterm.action=\"ADD\" then objectid  end)-count( case when oterm.action=\"DELETE\" then objectid end),0) \"count\"\nfrom a_objectterm oterm  \ninner join businessglossary bg on (oterm. businessglossaryid=bg.businessglossaryid)\ninner join user on oterm.audituser=user.USERID\nwhere objecttype=\"oetable\" AND bg.status = \"PUBLISHED\" and  objectid in\n(select t.oetableid\nfrom  oetable t\njoin  oeschema s on (t.oeschemaid = s.oeschemaid)\njoin connectioninfo ci on (s.connectioninfoid = ci.connectioninfoid)\nwhere t.active=1\n)  group by `user`.userid) A\ngroup by userid\n)B\ngroup by userid\nunion All\nselect userid,sum(count) as Count from\n(select user.userid,count(wikiid) \"Count\" from wiki wiki  inner join user on wiki.lastupdateuser=user.USERID\nwhere wikiobject=\"oetable\"\nand wikitext is not null and wikitext <> \"\"\nand  wikiobjectid not in (select objectid from a_objectterm where objecttype = \"oetable\" and audituser=`user`.userid )\nand wikiobjectid in\n(select t.oetableid\nfrom oetable t\njoin oeschema s on (t.oeschemaid = s.oeschemaid)\njoin connectioninfo ci on (s.connectioninfoid = ci.connectioninfoid))\ngroup by user.userid\n) C\ngroup by userid\nunion ALL\nselect userid,sum(count) as Count from\n(select user.userid,count(wikiid) \"Count\" from wiki wiki  inner join user on wiki.techlastupdateuser=user.USERID\nwhere wikiobject=\"oetable\"\nand techtext is not null and techtext <> \" \"\nand  wikiobjectid not in (select objectid from a_objectterm where objecttype = \"oetable\" and audituser=`user`.userid )\nand wikiobjectid in\n(select t.oetableid\nfrom oetable t\njoin oeschema s on (t.oeschemaid = s.oeschemaid)\njoin connectioninfo ci on (s.connectioninfoid = ci.connectioninfoid))\ngroup by user.userid\n) C\ngroup by userid\n) X\ngroup by userid\nOrder by Count DESC', '0', 'Invalid input', null);
INSERT INTO `stg_oereportext` VALUES ('1002', 'Tables tagged by schema', '-1', 'OvalEdge Reports', 'This is a Tag activity report. This report is used to find the total number of tables that are tagged(organized) in each schema. ', 'SELECT  table_tag_augumented.databasetype as `Database Type` ,table_tag_augumented.name as `Database Name`,all_schema.schemaname as `Schema`,table_tag_augumented.tablename as `Table`,TableTagged as \"#Tags\"\n FROM \n(SELECT DISTINCT schemaname FROM  oeschema s \nJOIN connectioninfo ci ON  (s.connectioninfoid = ci.connectioninfoid) ) all_schema\nLEFT JOIN \n(SELECT  ci.servertype as databasetype,ci.`name`, s.schemaname,oetable.tablename , count(distinct tabletag.tagid) AS \"TableTagged\" FROM tags\nINNER JOIN tabletag ON  tags.tagid=tabletag.tagid                 \nINNER JOIN oetable ON  oetable.oetableid=tabletag.oetableid                          \nJOIN  oeschema s  ON (oetable.oeschemaid = s.oeschemaid)                        \nJOIN  connectioninfo ci  ON (s.connectioninfoid = ci.connectioninfoid)                                                                          \nGROUP BY  ci.servertype ,ci.`name`,s.schemaname,oetable.tablename) table_tag_augumented                                              \nON (all_schema.schemaname=table_tag_augumented.schemaname)                    \nGROUP BY  table_tag_augumented.databasetype,table_tag_augumented.name,all_schema.Schemaname,table_tag_augumented.tablename, TableTagged                  \nHAVING sum(table_tag_augumented.tabletagged) IS NOT NULL', 'SELECT  table_tag_augumented.databasetype as `Database Type` ,table_tag_augumented.name as `Database Name` ,all_schema.schemaname as `Schema`,table_tag_augumented.tablename as `Table`,table_tag_augumented.tagname as AssociatedTag,table_tag_augumented.tagcreatedby as Tagcreatedby,\nDATE_FORMAT(table_tag_augumented.tagcreateddate ,\"%Y-%m-%d %h:%i %p\") as Tagcreateddate,table_tag_augumented.tagusedby as TagAssociatedby,DATE_FORMAT(table_tag_augumented.taguseddate ,\"%Y-%m-%d %h:%i %p\") as TagAssociateddate FROM \n(SELECT DISTINCT schemaname FROM  oeschema s                                             \nJOIN connectioninfo ci ON  (s.connectioninfoid = ci.connectioninfoid) ) all_schema\nLEFT JOIN \n(SELECT  ci.servertype as databasetype,ci.name,s.schemaname,oetable.tablename,tags.tagname,tags.userid as tagcreatedby,tags.createdate as tagcreateddate,tabletag.createdby as tagusedby, tabletag.createdate as taguseddate , count(DISTINCT oetable.tablename) AS \"TableTagged\" FROM tags\nINNER JOIN tabletag ON  tags.tagid=tabletag.tagid                 \nINNER JOIN oetable ON  oetable.oetableid=tabletag.oetableid                          \nJOIN  oeschema s  ON (oetable.oeschemaid = s.oeschemaid)                        \nJOIN  connectioninfo ci  ON (s.connectioninfoid = ci.connectioninfoid)\n  Where s.schemaname=?\nGROUP BY  tags.userid ,ci.servertype ,ci.name,s.schemaname,oetable.tablename,tags.tagname,tabletag.createdby,tags.createdate,tabletag.createdate) table_tag_augumented                                              \nON (all_schema.schemaname=table_tag_augumented.schemaname)                    \nGROUP BY  table_tag_augumented.databasetype,table_tag_augumented.name,all_schema.Schemaname,table_tag_augumented.tablename,table_tag_augumented.tagname,table_tag_augumented.tagcreatedby,\ntable_tag_augumented.tagcreateddate,table_tag_augumented.tagusedby,table_tag_augumented.taguseddate                    \nHAVING sum(table_tag_augumented.tabletagged) IS NOT NULL', 'SELECT  all_schema.Schemaname,Sum(COALESCE(table_tag_augumented.tabletagged,0)) AS \"#Tablestagged\" FROM \n(SELECT DISTINCT schemaname FROM  oeschema s \nJOIN connectioninfo ci ON  (s.connectioninfoid = ci.connectioninfoid) ) all_schema\nLEFT JOIN \n(SELECT  s.schemaname,tags.tagname, count(DISTINCT oetable.tablename) AS \"TableTagged\" FROM tags\nINNER JOIN tabletag ON  tags.tagid=tabletag.tagid                 \nINNER JOIN oetable ON  oetable.oetableid=tabletag.oetableid                          \nJOIN  oeschema s  ON (oetable.oeschemaid = s.oeschemaid)                        \nJOIN  connectioninfo ci  ON (s.connectioninfoid = ci.connectioninfoid)                                                                           \nGROUP BY  s.schemaname,tags.tagname) table_tag_augumented                                              \nON (all_schema.schemaname=table_tag_augumented.schemaname)                    \nGROUP BY  all_schema.schemaname                   \nHAVING sum(table_tag_augumented.tabletagged) IS NOT NULL\nOrder By Sum(COALESCE(table_tag_augumented.tabletagged,0)) DESC', '0', 'Invalid input', null);
INSERT INTO `stg_oereportext` VALUES ('1003', 'Columns Tagged by Schema ', '-1', 'OvalEdge Reports', 'This is a Tag activity report. This report is used to find the total number of columns tagged(organized) in each schema.', 'SELECT Column_Tag_Augumneted.`Databasetype`,Column_Tag_Augumneted.name as `Database Name` ,All_Schema.Schemaname as `Schema`,Column_Tag_Augumneted.tablename as `Table`,Column_Tag_Augumneted.columnname as `Column`,ColumnTagged as `# of Tags`\nFROM(SELECT DISTINCT schemaname  FROM  oeschema s JOIN connectioninfo ci           \nON (s.connectioninfoid = ci.connectioninfoid)) All_Schema \nLEFT JOIN (SELECT ci.servertype as `Databasetype`,ci.name,s.schemaname,oetable.tablename,oecolumn.columnname,Count(DISTINCT columntag.tagid) AS \"ColumnTagged\" FROM  tags                        \nINNER JOIN columntag ON tags.tagid=columntag.tagid                          \nINNER JOIN oecolumn ON(oecolumn.oecolumnid=columntag.columnid)                          \nINNER JOIN oetable ON(oetable.oetableid=oecolumn.oetableid)\nJOIN  oeschema s  ON(oetable.oeschemaid = s.oeschemaid)                          \nJOIN  connectioninfo ci ON (s.connectioninfoid = ci.connectioninfoid)                                                          \nGROUP BY ci.servertype,ci.name,s.schemaname,oetable.tablename,oecolumn.columnname) Column_Tag_Augumneted \nON(All_Schema.schemaname=Column_Tag_Augumneted.schemaname)           \nGROUP BY  Column_Tag_Augumneted.`Databasetype`,Column_Tag_Augumneted.name,All_Schema.Schemaname,Column_Tag_Augumneted.tablename,Column_Tag_Augumneted.columnname,ColumnTagged\nHAVING  Sum(columntagged) IS NOT NULL', 'SELECT Column_Tag_Augumneted.`Databasetype`,Column_Tag_Augumneted.name as `Database Name`,All_Schema.Schemaname as `Schema`,Column_Tag_Augumneted.tablename as `Table`,\nColumn_Tag_Augumneted.columnname as `Column`,Column_Tag_Augumneted.Tagname as AssociatedTag,Column_Tag_Augumneted.Tagcreatedby,\nDATE_FORMAT(Column_Tag_Augumneted.Tagcreateddate ,\"%Y-%m-%d %h:%i %p\") as Tagcreateddate ,Column_Tag_Augumneted.Tagusedby as TagAssociatedby,DATE_FORMAT(Column_Tag_Augumneted.Taguseddate ,\"%Y-%m-%d %h:%i %p\")  as TagAssociateddate\nFROM(SELECT DISTINCT schemaname  FROM  oeschema s JOIN connectioninfo ci           \nON (s.connectioninfoid = ci.connectioninfoid)) All_Schema \nLEFT JOIN (SELECT ci.servertype as `Databasetype`,ci.name,s.schemaname,oetable.tablename,oecolumn.columnname,tags.tagname as Tagname,tags.userid as Tagcreatedby,\ntags.createdate as Tagcreateddate,columntag.createdby as Tagusedby, columntag.createdate as Taguseddate,\nCount(DISTINCT columntag.tagid) AS \"ColumnTagged\" FROM  tags                        \nINNER JOIN columntag ON tags.tagid=columntag.tagid                          \nINNER JOIN oecolumn ON(oecolumn.oecolumnid=columntag.columnid)                          \nINNER JOIN oetable ON(oetable.oetableid=oecolumn.oetableid)\nJOIN  oeschema s  ON(oetable.oeschemaid = s.oeschemaid)                          \nJOIN  connectioninfo ci ON (s.connectioninfoid = ci.connectioninfoid)   \n where s.schemaname=?\nGROUP BY ci.servertype,ci.name,s.schemaname,oetable.tablename,oecolumn.columnname,tags.tagname,tags.userid,tags.createdate,columntag.createdby,columntag.createdate)Column_Tag_Augumneted \nON(All_Schema.schemaname=Column_Tag_Augumneted.schemaname)           \nGROUP BY  Column_Tag_Augumneted.`Databasetype`,Column_Tag_Augumneted.name,All_Schema.Schemaname,Column_Tag_Augumneted.tablename,Column_Tag_Augumneted.columnname,Column_Tag_Augumneted.Tagname,Column_Tag_Augumneted.Tagcreatedby,\nColumn_Tag_Augumneted.Tagcreateddate,Column_Tag_Augumneted.Tagusedby, Column_Tag_Augumneted.Taguseddate\nHAVING  Sum(columntagged) IS NOT NULL', 'SELECT All_Schema.Schemaname,Sum(columntagged) as `# of tagged columns`\nFROM(SELECT DISTINCT schemaname  FROM  oeschema s JOIN connectioninfo ci           \nON (s.connectioninfoid = ci.connectioninfoid)) All_Schema \nLEFT JOIN (SELECT s.schemaname,tags.tagname,Count(DISTINCT oecolumn.oecolumnid) AS \"ColumnTagged\" FROM  tags                        \nINNER JOIN columntag ON tags.tagid=columntag.tagid                          \nINNER JOIN oecolumn ON(oecolumn.oecolumnid=columntag.columnid)                          \nINNER JOIN oetable ON(oetable.oetableid=oecolumn.oetableid)\nJOIN  oeschema s  ON(oetable.oeschemaid = s.oeschemaid)                          \nJOIN  connectioninfo ci ON (s.connectioninfoid = ci.connectioninfoid)                                                          \nGROUP BY s.schemaname,tags.tagname) Column_Tag_Augumneted \nON(All_Schema.schemaname=Column_Tag_Augumneted.schemaname)           \nGROUP BY  schemaname \nHAVING    Sum(columntagged) IS NOT NULL\nOrder By Sum(columntagged) DESC', '0', 'Invalid input', null);
INSERT INTO `stg_oereportext` VALUES ('1004', 'Schemas Tagged', '-1', 'OvalEdge Reports', ' \nThis is a Tag activity report. This report is used to find the total number of schemas that are tagged(organized).\nSemi-Details:\nThe details of the summary report display the individual schema name with the total number of associated tags.\n\nReport Drill down details:\nSelecting a schema name in the bar chart lists the details of the individual connection name, schema name within each connection, and the tag names that are associated with each schema. ', 'SELECT Schema_tag_Augumented.databasename as `Database`,All_Schema.schemaname as `Schema`,Sum(schematagged) AS \"#Tags\" FROM\n(SELECT DISTINCT schemaname FROM oeschema s          \nJOIN connectioninfo ci ON(s.connectioninfoid = ci.connectioninfoid) ) All_Schema       \nLEFT JOIN(SELECT ci.servertype as databasename,schemaname,Count(schemaname) AS \"SchemaTagged\" FROM oeschema s                             \nJOIN connectioninfo ci ON(s.connectioninfoid = ci.connectioninfoid)                           \nJOIN schematag ON(schematag.oeschemaid=s.oeschemaid)                           \nJOIN tags ON(tags.tagid=schematag.tagid)                           \nGROUP BY ci.servertype,schemaname)Schema_tag_Augumented                                                          \nON(All_Schema.schemaname=Schema_tag_Augumented.schemaname) \nGROUP BY  Schema_tag_Augumented.databasename,All_Schema.schemaname \nHAVING Sum(schematagged) IS NOT NULL', 'SELECT Schema_tag_Augumented.Databasename  as `Database`,All_Schema.Schemaname as `Schema`,Schema_tag_Augumented.Tagname as AssociatedTag,Schema_tag_Augumented.Tagcreatedby,\nDATE_FORMAT(Schema_tag_Augumented.Tagcreateddate ,\"%Y-%m-%d %h:%i %p\") as Tagcreateddate,Schema_tag_Augumented.Tagusedby as TagAssociatedby,DATE_FORMAT(Schema_tag_Augumented.Taguseddate ,\"%Y-%m-%d %h:%i %p\") as TagAssociateddate FROM\n(SELECT DISTINCT schemaname FROM oeschema s          \nJOIN connectioninfo ci ON(s.connectioninfoid = ci.connectioninfoid) ) All_Schema       \nLEFT JOIN(SELECT ci.servertype as Databasename,s.schemaname,tags.tagname as Tagname,tags.userid as Tagcreatedby,tags.createdate as Tagcreateddate,\nschematag.createdby as Tagusedby, schematag.createdate as Taguseddate,Count(DISTINCT schemaname) AS \"SchemaTagged\" FROM oeschema s                             \nJOIN connectioninfo ci ON(s.connectioninfoid = ci.connectioninfoid)                           \nJOIN schematag ON(schematag.oeschemaid=s.oeschemaid)                           \nJOIN tags ON(tags.tagid=schematag.tagid)                          \nGROUP BY ci.servertype,s.schemaname,tags.tagname,tags.userid,tags.createdate,schematag.createdby,schematag.createdate)Schema_tag_Augumented                                                          \nON(All_Schema.schemaname=Schema_tag_Augumented.schemaname) \nwhere All_Schema.schemaname=?\nGROUP BY  `Database`,All_Schema.schemaname,Schema_tag_Augumented.Tagname,Schema_tag_Augumented.Tagcreatedby,\nSchema_tag_Augumented.Tagcreateddate,Schema_tag_Augumented.Tagusedby,Schema_tag_Augumented.Taguseddate\nHAVING  Sum(schematagged) IS NOT NULL', 'SELECT All_Schema.Schemaname,Sum(schematagged) AS \"#SchemaTagged\" FROM\n(SELECT DISTINCT schemaname FROM oeschema s          \nJOIN connectioninfo ci ON(s.connectioninfoid = ci.connectioninfoid) ) All_Schema       \nLEFT JOIN(SELECT schemaname,tags.tagname,Count(DISTINCT schemaname) AS \"SchemaTagged\" FROM oeschema s                             \nJOIN connectioninfo ci ON(s.connectioninfoid = ci.connectioninfoid)                           \nJOIN schematag ON(schematag.oeschemaid=s.oeschemaid)                           \nJOIN tags ON(tags.tagid=schematag.tagid)                           \nGROUP BY schemaname,tags.tagname)Schema_tag_Augumented                                                          \nON(All_Schema.schemaname=Schema_tag_Augumented.schemaname) \nGROUP BY  All_Schema.schemaname \nHAVING    Sum(schematagged) IS NOT NULL\nOrder by Sum(schematagged) DESC', '0', 'Invalid input', null);
INSERT INTO `stg_oereportext` VALUES ('1005', 'Report Tagged by Report Group', '-1', 'OvalEdge Reports', 'This is a Tag activity report. This report is used to find the total number of Reports that are tagged(organized) in each functional Report Group.', 'SELECT Chart_Tag_Augumeneted.Databasename as `Database`,All_Domains.value AS `Reportgroup`,Chart_Tag_Augumeneted.chartname as `Report Name`,Sum(charttagged) as \"#Reportstagged\" FROM \n(SELECT DISTINCT value FROM domain D\nJOIN connectioninfo ci ON(D.connectioninfoid = ci.connectioninfoid) ) All_Domains           \nLEFT JOIN(SELECT ci.servertype as Databasename,D.value,chart.CHARTNAME as chartname,Count( charttag.TAGID) AS \"ChartTagged\" FROM tags  \nINNER JOIN charttag ON tags.tagid=charttag.tagid                          \nINNER JOIN chart ON(chart.chartid=charttag.chartid)                            \nJOIN domain D ON(chart.domainid = D.domainid)                          \nJOIN connectioninfo ci ON(D.connectioninfoid = ci.connectioninfoid)                            \nGROUP BY ci.servertype,D.value,chart.CHARTNAME)Chart_Tag_Augumeneted                                                            \nON(All_Domains.value=Chart_Tag_Augumeneted.value)\nGROUP BY  Chart_Tag_Augumeneted.Databasename,All_Domains.value,Chart_Tag_Augumeneted.chartname \nHAVING Sum(charttagged) IS NOT NULL', 'SELECT Chart_Tag_Augumeneted.databasename as `Database`,All_Domains.value AS Reportgroup,Chart_Tag_Augumeneted.chartname as ReportName,Chart_Tag_Augumeneted.tagname as AssociatedTags,\nChart_Tag_Augumeneted.tagcreatedby as Tagcreatedby,DATE_FORMAT(Chart_Tag_Augumeneted.tagcreateddate ,\"%Y-%m-%d %h:%i %p\") as Tagcreateddate,Chart_Tag_Augumeneted.tagusedby as TagAssociatedby,\nDATE_FORMAT(Chart_Tag_Augumeneted.taguseddated,\"%Y-%m-%d %h:%i %p\") as TagAssociateddate FROM \n(SELECT DISTINCT value FROM domain D\nJOIN connectioninfo ci ON(D.connectioninfoid = ci.connectioninfoid) ) All_Domains           \nLEFT JOIN(SELECT ci.servertype as databasename,D.value,chart.chartname,tags.tagname,tags.userid as tagcreatedby,tags.createdate as tagcreateddate,\ncharttag.createdby as tagusedby,charttag.createdate as taguseddated, Count(charttag.tagid) AS \"ChartTagged\" FROM tags  \nINNER JOIN charttag ON tags.tagid=charttag.tagid                          \nINNER JOIN chart ON(chart.chartid=charttag.chartid)                            \nJOIN domain D ON(chart.domainid = D.domainid)                          \nJOIN connectioninfo ci ON(D.connectioninfoid = ci.connectioninfoid)                            \nGROUP BY ci.servertype,D.value,chart.chartname,tags.tagname,tags.userid,tags.createdate,charttag.createdby,charttag.createdate)Chart_Tag_Augumeneted                          \nON(All_Domains.value=Chart_Tag_Augumeneted.value)  \nwhere  All_Domains.value=?       \nGROUP BY  Chart_Tag_Augumeneted.databasename,Chart_Tag_Augumeneted.value,Chart_Tag_Augumeneted.chartname,\nChart_Tag_Augumeneted.tagname,Chart_Tag_Augumeneted.tagcreatedby,Chart_Tag_Augumeneted.tagcreateddate,Chart_Tag_Augumeneted.tagusedby,\nChart_Tag_Augumeneted.taguseddated\nHAVING    Sum(charttagged) IS NOT NULL', 'SELECT All_Domains.value AS Domainname,Sum(charttagged) as \"#Chartstagged\" FROM \n(SELECT DISTINCT value FROM domain D\nJOIN connectioninfo ci ON(D.connectioninfoid = ci.connectioninfoid) ) All_Domains           \nLEFT JOIN(SELECT D.value,tags.tagname,Count(DISTINCT chart.chartid) AS \"ChartTagged\" FROM tags  \nINNER JOIN charttag ON tags.tagid=charttag.tagid                          \nINNER JOIN chart ON(chart.chartid=charttag.chartid)                            \nJOIN domain D ON(chart.domainid = D.domainid)                          \nJOIN connectioninfo ci ON(D.connectioninfoid = ci.connectioninfoid)                            \nGROUP BY D.value,tags.tagname)Chart_Tag_Augumeneted                                                            \nON(All_Domains.value=Chart_Tag_Augumeneted.value)           \nGROUP BY  All_Domains.value \nHAVING  Sum(charttagged) IS NOT NULL\nOrder By Sum(charttagged) DESC ', '0', 'Invalid input', null);
INSERT INTO `stg_oereportext` VALUES ('1006', 'Count of files tagged by Folder', '-1', 'OvalEdge Reports', '\nThis is a Tag activity report. This report displays the total number of files that are tagged(organized) in each folder.\nSemi-Detail:\nThe details of the summary report display the individual file name with the total number of associated tags.\nReport Drill-down details:\nSelecting a Folder name in the bar chart lists the details of the Folder name, file name,  and the tag name that is associated with each file. \n', 'SELECT File_Tag_Augumeneted.name as `Database`,All_Folders.filefolder as `Folder`,File_Tag_Augumeneted.filename as `File`,\nFile_Tag_Augumeneted.FilesTagged  as FileTagged FROM\n(SELECT DISTINCT filefolder FROM filefolder F \nJOIN connectioninfo ci ON(F.connectioninfoid = ci.connectioninfoid)) All_Folders           \nLEFT JOIN(SELECT ci.servertype as name,F.filefolder,file.filename,Count(DISTINCT filetag.TAGID) AS \"FilesTagged\" FROM tags \nINNER JOIN filetag ON tags.tagid=filetag.TAGID                          \nINNER JOIN file  ON(file.fileid=filetag.fileid)                           \nJOIN filefolder F ON(file.filefolderid = F.filefolderid)                           \nJOIN connectioninfo ci ON(F.connectioninfoid = ci.connectioninfoid)                          \nGROUP BY ci.servertype,F.filefolder,file.filename)File_Tag_Augumeneted                                                          \nON(All_Folders.filefolder=File_Tag_Augumeneted.filefolder)  \nGROUP BY  File_Tag_Augumeneted.name,All_Folders.filefolder,File_Tag_Augumeneted.filename,File_Tag_Augumeneted.FilesTagged\nHAVING Sum(filestagged) IS NOT NULL', 'SELECT File_Tag_Augumeneted.databasename as `database`,All_Folders.filefolder,File_Tag_Augumeneted.filename,File_Tag_Augumeneted.AssociatedTags,\nFile_Tag_Augumeneted.tagcreatedby as Tagcreatedby,DATE_FORMAT(File_Tag_Augumeneted.tagcreateddate ,\"%Y-%m-%d %h:%i %p\") as Tagcreateddate,File_Tag_Augumeneted.TagAssociatedby,\nDATE_FORMAT(File_Tag_Augumeneted.TagAssociateddate ,\"%Y-%m-%d %h:%i %p\") as TagAssociateddate FROM\n(SELECT DISTINCT filefolder FROM filefolder F \nJOIN connectioninfo ci ON(F.connectioninfoid = ci.connectioninfoid)) All_Folders           \nLEFT JOIN(SELECT ci.servertype as databasename,F.filefolder,file.filename,tags.tagname as AssociatedTags,tags.userid as tagcreatedby,tags.createdate as tagcreateddate,\nfiletag.createdby as TagAssociatedby,filetag.createdate as TagAssociateddate,Count(DISTINCT filetag.TAGID) AS \"FilesTagged\"  FROM tags \nINNER JOIN filetag ON tags.tagid=filetag.TAGID                          \nINNER JOIN file  ON(file.fileid=filetag.fileid)                           \nJOIN filefolder F ON(file.filefolderid = F.filefolderid)                           \nJOIN connectioninfo ci ON(F.connectioninfoid = ci.connectioninfoid)                          \nGROUP BY ci.servertype,F.filefolder,file.filename,tags.tagname,tags.userid,tags.createdate,filetag.createdby,filetag.createdate)File_Tag_Augumeneted                                                          \nON(All_Folders.filefolder=File_Tag_Augumeneted.filefolder)\n where   All_Folders.filefolder = ?\nGROUP BY File_Tag_Augumeneted.databasename,All_Folders.filefolder,File_Tag_Augumeneted.filename,File_Tag_Augumeneted.AssociatedTags,\nFile_Tag_Augumeneted.tagcreatedby,File_Tag_Augumeneted.tagcreateddate,File_Tag_Augumeneted.TagAssociatedby,File_Tag_Augumeneted.TagAssociateddate\nHAVING Sum(FilesTagged) IS NOT NULL', 'SELECT All_Folders.Filefolder,Sum(filestagged) as \"#Filestagged\" FROM\n(SELECT DISTINCT filefolder FROM filefolder F \nJOIN connectioninfo ci ON(F.connectioninfoid = ci.connectioninfoid)) All_Folders           \nLEFT JOIN(SELECT F.filefolder,tags.tagname,Count(DISTINCT file.fileid) AS \"FilesTagged\" FROM tags \nINNER JOIN filetag ON tags.tagid=filetag.tagid                          \nINNER JOIN file  ON(file.fileid=filetag.fileid)                           \nJOIN filefolder F ON(file.filefolderid = F.filefolderid)                           \nJOIN connectioninfo ci ON(F.connectioninfoid = ci.connectioninfoid)                          \nGROUP BY F.filefolder,tags.tagname)File_Tag_Augumeneted                                                          \nON(All_Folders.filefolder=File_Tag_Augumeneted.filefolder)  \nGROUP BY  All_Folders.filefolder \nHAVING    Sum(filestagged) IS NOT NULL\nOrder By Sum(filestagged) DESC', '0', 'Invalid input', null);
INSERT INTO `stg_oereportext` VALUES ('1007', 'Count of File Columns Tagged by folder', '-1', 'OvalEdge Reports', '   \nThis is a Tag activity report. This report is used to find the total number of columns in a file that are tagged in each folder.\nSemi-Detail:\nThe details of the summary report display the individual file column name with the total number of associated tags.\n\nReport Drill down details:\nSelecting a Folder name in the bar chart lists the details of the Folder name, file name, file column name and the tag name that is associated with each file. \n', 'SELECT ci.servertype as databasename,F.filefolder,file.filename,filecolumn.columnname,\nCount(DISTINCT filecolumntag.tagid) AS \"FileColumnsTagged\" FROM tags\nINNER JOIN filecolumntag ON tags.tagid=filecolumntag.tagid\nINNER JOIN filecolumn ON(filecolumn.filecolumnid=filecolumntag.filecolumnid)                            \nINNER JOIN file ON(file.fileid=filecolumn.fileid)                     \nJOIN filefolder F ON(file.filefolderid = F.filefolderid)   \nJOIN connectioninfo ci ON(F.connectioninfoid = ci.connectioninfoid) \nGROUP BY ci.servertype,F.filefolder,file.filename,filecolumn.columnname', 'SELECT ci.servertype as databasename,F.filefolder,file.filename,filecolumn.columnname,group_concat(distinct tags.tagname) as Tagname,tags.userid,DATE_FORMAT(tags.createdate,\"%Y-%m-%d %h:%i %p\") as createdate,\nfilecolumntag.createdby,DATE_FORMAT(filecolumntag.createdate,\"%Y-%m-%d %h:%i %p\")as taguseddate,Count(DISTINCT filecolumntag.tagid) AS FileColumnsTagged FROM tags\nINNER JOIN filecolumntag ON tags.tagid=filecolumntag.tagid\nINNER JOIN filecolumn ON(filecolumn.filecolumnid=filecolumntag.filecolumnid)                            \nINNER JOIN file ON(file.fileid=filecolumn.fileid)                     \nleft JOIN filefolder F ON(file.filefolderid = F.filefolderid)   \nJOIN connectioninfo ci ON(F.connectioninfoid = ci.connectioninfoid) \nwhere F.filefolder = ?\nGROUP BY ci.servertype,F.filefolderid,file.filename,filecolumn.columnname,tags.userid,tags.createdate,\nfilecolumntag.createdby,filecolumntag.createdate', 'select A.filefolder,sum(FileColumnsTagged) as \"FileColumnsTagged\"  from(SELECT F.filefolder,tags.tagname,\nCount(DISTINCT filecolumntag.tagid) AS \"FileColumnsTagged\" FROM tags\nINNER JOIN filecolumntag ON tags.tagid=filecolumntag.tagid\nINNER JOIN filecolumn ON(filecolumn.filecolumnid=filecolumntag.filecolumnid)                            \nINNER JOIN file ON(file.fileid=filecolumn.fileid)                     \nJOIN filefolder F ON(file.filefolderid = F.filefolderid)   \nJOIN connectioninfo ci ON(F.connectioninfoid = ci.connectioninfoid) \nGROUP BY F.filefolder,tags.tagname )  A  \ngroup by A.filefolder             \nOrder By FileColumnsTagged DESC', '0', 'Invalid input', null);
INSERT INTO `stg_oereportext` VALUES ('1008', 'Count of Tags to Each Term', '-1', 'OvalEdge Reports', 'This is a Tag activity report. This report displays the total number of tags that are associated with each term.', 'select globaldomain.domain as Domain,Coalesce(cat.name,\" \") as Category,Coalesce(subcat.name,\" \") as Subcategory,businessglossary.name as Term,count(distinct objecttag.tagid) as \"#AssociatedTags\" from tags \ninner join objecttag  on objecttag.tagid=tags.tagid\ninner join businessglossary on objecttag.objectid=businessglossaryid\ninner join globaldomain on globaldomain.globaldomainid=businessglossary.globaldomainid\nleft outer join category cat on cat.categoryid=businessglossary.category1id \nleft outer join category subcat on subcat.categoryid=businessglossary.category2id \nwhere objecttag.objecttype=\"glossary\"\ngroup by globaldomain.domain,Coalesce(cat.name,\" \"),Coalesce(subcat.name,\" \"),businessglossary.name', 'select globaldomain.domaintype as Domaintype,globaldomain.domain as Domain,Coalesce(cat.name,\" \") as Category,Coalesce(subcat.name,\" \") as Subcategory,businessglossary.name as Term,tags.tagname,tags.userid as Tagcreatedby,\nDATE_FORMAT(tags.createdate, \"%Y-%m-%d %h:%i %p\") as TagCreateddate,objecttag.createdby as TagAssociatedby,DATE_FORMAT(objecttag.createdate, \"%Y-%m-%d %h:%i %p\") as TagAssociateddate from tags \ninner join objecttag  on objecttag.tagid=tags.tagid\ninner join businessglossary on objecttag.objectid=businessglossaryid\ninner join globaldomain on globaldomain.globaldomainid=businessglossary.globaldomainid\nleft outer join category cat on cat.categoryid=businessglossary.category1id \nleft outer join category subcat on subcat.categoryid=businessglossary.category2id\nwhere objecttag.objecttype=\"glossary\" and businessglossary.name=?', 'select businessglossary.name as \"Termname\",\ncount(distinct tags.tagname) as \"#Termstagged\" from tags \ninner join objecttag  on objecttag.tagid=tags.tagid\ninner join businessglossary on objecttag.objectid=businessglossaryid\nwhere objecttag.objecttype=\"glossary\"\ngroup by businessglossary.name\nOrder by count(distinct tags.tagname) desc ', '0', 'Invalid input', null);
INSERT INTO `stg_oereportext` VALUES ('1009', 'Data Story Tags by Story Zone', '-1', 'OvalEdge Reports', 'This is a Tag activity report. This report displays the total number of data stories that are tagged in each story domain. Each data story can be associated either with a parent or child data story.', 'select  globaldomain.Domain as StoryZone,Stories.Parentstory,COALESCE(ptag,0) as Parenttag,\n    COALESCE(childstory.childstory,\"Not Available\") as Childstory,COALESCE(ctag,0) as Childtag from \n(select distinct globaldomain.domain from globaldomain where domaintype=\"DATA_STORY\") globaldomain \ninner join \n(select storyline.name as parentstory ,globaldomain.domain from storyline \ninner join datastorytag on storyline.id=datastorytag.STORYID\ninner join globaldomain on globaldomain.globaldomainid=storyline.domainid\ninner join tags on tags.TAGID=datastorytag.tagid\nwhere storyline.parentid=0\nand domaintype=\"DATA_STORY\"\ngroup by storyline.name,globaldomain.domain \nunion \nselect p.name as parentstory,globaldomain.domain  from storyline \ninner join datastorytag on storyline.id=datastorytag.STORYID\ninner join tags on tags.TAGID=datastorytag.tagid\ninner join globaldomain on globaldomain.globaldomainid=storyline.domainid\ninner join  storyline p on storyline.parentid=p.id\nwhere domaintype=\"DATA_STORY\"\ngroup by p.name ,storyline.name,globaldomain.domain) Stories on (globaldomain.domain=Stories.domain)\nleft join \n(select storyline.name as parentstory ,globaldomain.domain,count(datastorytag.tagid ) ptag from storyline \ninner join datastorytag on storyline.id=datastorytag.STORYID\ninner join tags on tags.TAGID=datastorytag.tagid\ninner join globaldomain on globaldomain.globaldomainid=storyline.domainid\nwhere storyline.parentid=0 and domaintype=\"DATA_STORY\"\ngroup by storyline.name,globaldomain.domain ) Parentstory on (Stories.parentstory=Parentstory.parentstory) \nand (globaldomain.domain=Parentstory.domain)\nleft join \n(select p.name as parentstory ,COALESCE(storyline.name,\"\") as childstory,globaldomain.domain,count(datastorytag.tagid ) ctag from storyline \ninner join datastorytag on storyline.id=datastorytag.STORYID\ninner join tags on tags.TAGID=datastorytag.tagid\ninner join globaldomain on globaldomain.globaldomainid=storyline.domainid\ninner join  storyline p on storyline.parentid=p.id\nwhere domaintype=\"DATA_STORY\"\ngroup by p.name ,storyline.name,globaldomain.domain\n) childstory on (Stories.parentstory=childstory.parentstory) \nand (globaldomain.domain=childstory.domain)', 'select globaldomain.Domain as StoryZone,Stories.Parentstory,COALESCE(Parentstory.tagname,\"\") as Parenttagname,COALESCE(Parentstory.userid,\"\") as Parenttagcreatedby,COALESCE(DATE_FORMAT(Parentstory.createdate, \"%Y-%m-%d %h:%i %p\"),\"\") as Parenttagcreateddate,\nCOALESCE(Parentstory.tagusedby,\"\") as ParenttagAssociatedby,COALESCE(DATE_FORMAT(Parentstory.taguseddate, \"%Y-%m-%d %h:%i %p\"),\"\") as ParenttagAssociateddate,COALESCE(childstory.childstory,\"Not Available\") as Childstory,\nCOALESCE(childstory.tagname,\"\") as childtagname,COALESCE(childstory.userid,\"\") as childtagcreatedby,COALESCE(DATE_FORMAT(childstory.createdate, \"%Y-%m-%d %h:%i %p\"),\"\") as childtagcreateddate,\nCOALESCE(childstory.tagusedby,\"\") as ChildtagAssociatedby,COALESCE(DATE_FORMAT(childstory.taguseddate, \"%Y-%m-%d %h:%i %p\"),\"\") as ChildtagAssociateddate from \n(select distinct globaldomain.domain from globaldomain where domaintype=\"DATA_STORY\") globaldomain \ninner join \n(select storyline.name as parentstory ,globaldomain.domain from storyline \ninner join datastorytag on storyline.id=datastorytag.STORYID\ninner join globaldomain on globaldomain.globaldomainid=storyline.domainid\ninner join tags on tags.TAGID=datastorytag.tagid\nwhere storyline.parentid=0\nand domaintype=\"DATA_STORY\"\ngroup by storyline.name,globaldomain.domain \nunion \nselect p.name as parentstory,globaldomain.domain  from storyline \ninner join datastorytag on storyline.id=datastorytag.STORYID\ninner join tags on tags.TAGID=datastorytag.tagid\ninner join globaldomain on globaldomain.globaldomainid=storyline.domainid\ninner join  storyline p on storyline.parentid=p.id\nwhere domaintype=\"DATA_STORY\"\ngroup by p.name ,storyline.name,globaldomain.domain) Stories on (globaldomain.domain=Stories.domain)\nleft join \n(select storyline.name as parentstory ,globaldomain.domain,count(datastorytag.tagid ) ptag,tags.tagname,tags.userid,tags.createdate,\ndatastorytag.createdby as tagusedby,datastorytag.createdate as taguseddate from storyline \ninner join datastorytag on storyline.id=datastorytag.STORYID\ninner join tags on tags.TAGID=datastorytag.tagid\ninner join globaldomain on globaldomain.globaldomainid=storyline.domainid\nwhere storyline.parentid=0 and domaintype=\"DATA_STORY\"\ngroup by storyline.name,globaldomain.domain,tags.tagname,tags.userid,tags.createdate,\ndatastorytag.createdby,datastorytag.createdate) Parentstory on (Stories.parentstory=Parentstory.parentstory) \nand (globaldomain.domain=Parentstory.domain)\nleft join \n(select p.name as parentstory ,COALESCE(storyline.name,\"\") as childstory,globaldomain.domain,count(datastorytag.tagid ) ctag,tags.tagname,tags.userid,\ntags.createdate,datastorytag.createdby as tagusedby,datastorytag.createdate as taguseddate from storyline \ninner join datastorytag on storyline.id=datastorytag.STORYID\ninner join tags on tags.TAGID=datastorytag.tagid\ninner join globaldomain on globaldomain.globaldomainid=storyline.domainid\ninner join  storyline p on storyline.parentid=p.id\nwhere domaintype=\"DATA_STORY\"\ngroup by p.name ,storyline.name,globaldomain.domain,tags.tagname,tags.userid,tags.createdate,datastorytag.createdby,datastorytag.createdate\n) childstory on (Stories.parentstory=childstory.parentstory) \nand (globaldomain.domain=childstory.domain)\nwhere globaldomain.Domain=?', 'select A.Domain as StoryZone,sum(Parenttag+Childtag) as Count from (\nselect  globaldomain.domain,Stories.parentstory,COALESCE(ptag,0) as Parenttag,childstory.childstory,\nCOALESCE(ctag,0) as Childtag from \n(select distinct globaldomain.domain from globaldomain where domaintype=\"DATA_STORY\") globaldomain \ninner join \n(select storyline.name as parentstory ,globaldomain.domain from storyline \ninner join datastorytag on storyline.id=datastorytag.STORYID\ninner join globaldomain on globaldomain.globaldomainid=storyline.domainid\ninner join tags on tags.TAGID=datastorytag.tagid\nwhere storyline.parentid=0\nand domaintype=\"DATA_STORY\"\ngroup by storyline.name,globaldomain.domain \nunion \nselect p.name as parentstory,globaldomain.domain  from storyline \ninner join datastorytag on storyline.id=datastorytag.STORYID\ninner join tags on tags.TAGID=datastorytag.tagid\ninner join globaldomain on globaldomain.globaldomainid=storyline.domainid\ninner join  storyline p on storyline.parentid=p.id\nwhere domaintype=\"DATA_STORY\"\ngroup by p.name ,storyline.name,globaldomain.domain) Stories on (globaldomain.domain=Stories.domain)\nleft join \n(select storyline.name as parentstory ,globaldomain.domain,count(tags.tagname ) ptag from storyline \ninner join datastorytag on storyline.id=datastorytag.STORYID\ninner join tags on tags.TAGID=datastorytag.tagid\ninner join globaldomain on globaldomain.globaldomainid=storyline.domainid\nwhere storyline.parentid=0 and domaintype=\"DATA_STORY\"\ngroup by storyline.name,globaldomain.domain ) Parentstory on (Stories.parentstory=Parentstory.parentstory) \nand (globaldomain.domain=Parentstory.domain)\nleft join \n(select p.name as parentstory ,storyline.name as childstory,globaldomain.domain,count(tags.tagname ) ctag from storyline \ninner join datastorytag on storyline.id=datastorytag.STORYID\ninner join tags on tags.TAGID=datastorytag.tagid\ninner join globaldomain on globaldomain.globaldomainid=storyline.domainid\ninner join  storyline p on storyline.parentid=p.id\nwhere domaintype=\"DATA_STORY\"\ngroup by p.name ,storyline.name,globaldomain.domain\n) childstory on (Stories.parentstory=childstory.parentstory) \nand (globaldomain.domain=childstory.domain))A\ngroup by A.domain\nOrder by sum(Parenttag+Childtag) DESC', '0', 'Invalid input', null);
INSERT INTO `stg_oereportext` VALUES ('1010', 'Queries Tags Summary Report', '-1', 'OvalEdge Reports', '   \nThis is a Tag activity report. This report displays the total number of tags associated with each query in the OvalEdge application. The queries can be created in OvalEdge or extracted from other BI tools.\nSemi_Detail:\nThe details display the individual count of tags for each dataset.\nReport Drill down details:\nSelecting a query name in the bar chart lists the details of the query name, and the tag name associated with each query.\n', 'select dataset.userselecteddb as `Database`,dataset.Datasetname,dataset.jobtype as Jobtype,count(distinct querytag.tagid) as \"#Tags\" from tags \ninner join querytag  on querytag.tagid=tags.tagid\ninner join dataset on dataset.datasetid=querytag.datasetid\njoin connectioninfo ci on (dataset.connectioninfoid = ci.connectioninfoid)\ngroup by dataset.userselecteddb,dataset.Datasetname,dataset.jobtype', 'select dataset.userselecteddb as `Database`,dataset.Datasetname,dataset.jobtype as Jobtype,dataset.userid as datasetcreateduser,tags.Tagname,tags.userid as tagcreatedby,DATE_FORMAT(tags.createdate, \"%Y-%m-%d %h:%i %p\") as tagcreateddate, \nquerytag.createdby as TagAssociatedby,DATE_FORMAT(querytag.createdate, \"%Y-%m-%d %h:%i %p\") as TagAssociatedate from tags \ninner join querytag  on querytag.tagid=tags.tagid\ninner join dataset on dataset.datasetid=querytag.datasetid\njoin connectioninfo ci on (dataset.connectioninfoid = ci.connectioninfoid)\n where dataset.Datasetname =?\ngroup by dataset.userselecteddb,dataset.Datasetname,dataset.jobtype,dataset.userid,\ntags.Tagname,tags.userid,tags.createdate,querytag.createdby,querytag.createdate', 'select Datasetname ,count(distinct querytag.tagid) as \"#Tags\" from tags \ninner join querytag  on querytag.tagid=tags.tagid\ninner join dataset on dataset.datasetid=querytag.datasetid\njoin connectioninfo ci on (dataset.connectioninfoid = ci.connectioninfoid)\ngroup by datasetname\n        Order by count(distinct querytag.tagid) DESC', '0', 'Invalid input', null);
INSERT INTO `stg_oereportext` VALUES ('1011', 'Monitoring connections grouped by Database Type ', '-1', 'OvalEdge Reports', 'This is a Connection activity report. With this report, a user can find how many database connections are created in each database type.', 'select \"RDBMS\" as Type,c.Servertype as DatabaseType,schemaname as SchemaName,(case when s.crawled=1 then \"Y\" else  \"N\" end ) Iscrawled from connectioninfo c\nleft join oeschema s on c.connectioninfoid=s.connectioninfoid\nwhere conntype=0 and s.crawled=1\nand c.connectioninfoid not like \"-1\"\nunion ALL\nselect \"NFS\" as type,c.servertype as DatabaseType , filefolder, \"N/A\" as IsCrawled from connectioninfo c\nleft join filefolder f on c.connectioninfoid=f.connectioninfoid\nwhere conntype=1\nunion ALL\nselect \"Reports\" as type,c.servertype as DatabaseType ,d.value, (case when d.crawled=1 then \"Y\" else  \"N\" end ) as IsCrawled from connectioninfo c\nleft join domain d on c.connectioninfoid=d.connectioninfoid\nwhere conntype=2 and d.crawled=1', 'select * from(select \"RDBMS\" as Type,c.Servertype as DatabaseType, name as  DatabaseName,schemaname as Name,(case when s.crawled=1 then \"Y\" else  \"N\" end ) Iscrawled from connectioninfo c\nleft join oeschema s on c.connectioninfoid=s.connectioninfoid\nwhere conntype=0 and s.crawled=1\nand c.connectioninfoid not like \"-1\"\nunion ALL\nselect \"NFS\" as type,c.servertype as DatabaseType, name as DatabaseName,filefolder, \"N/A\" as IsCrawled from connectioninfo c\nleft join filefolder f on c.connectioninfoid=f.connectioninfoid\nwhere conntype=1\nunion ALL\nselect \"Reports\" as type,c.servertype as DatabaseType, name as  DatabaseName,d.value, (case when d.crawled=1 then \"Y\" else  \"N\" end ) as IsCrawled from connectioninfo c\nleft join domain d on c.connectioninfoid=d.connectioninfoid\nwhere conntype=2 and d.crawled=1)A\nwhere A.type=?', 'select * from (select \"RDBMS\" as Type, count(*) as Count from connectioninfo c\nwhere conntype=0\nand c.connectioninfoid not like \"-1\"\nunion ALL\nselect \"NFS\" as type,count(*) as Count from connectioninfo c\nwhere conntype=1\nunion ALL\nselect \"Reports\" as type,count(*) as Count from connectioninfo c\nwhere conntype=2)A\norder by A.Count DESC', '0', 'Invalid input', null);
INSERT INTO `stg_oereportext` VALUES ('1012', 'Role Based Access Report', '-1', 'OvalEdge Reports', ' \nThis is a User activity report. This report displays the total number of user roles that each user is associated with in the OvalEdge application.\nSemi_Detail:\nThe detail displays the user name , their assigned user role and the metadata and data permissions they have for each associated role.\nReport Drill-down details:\nSelecting a user name in the bar chart lists their name,complete details of that user, their assigned user role and the metadata and data permissions they have for each associated role.\n', 'select  u.userid as Username, r.AUTHORITY as Role,  \ncase\n	when r.defaultpermission = 1 then \"META_READ_DATA_NOPERMISSION\"\n	when r.defaultpermission = 2 then \"META_WRITE_DATA_NOPERMISSION\"\n	when r.defaultpermission = 3 then \"META_READ_DATA_PREVIEW\"\n	when r.defaultpermission = 6 then \"META_WRITE_DATA_PREVIEW\"\n	when r.defaultpermission = 5 then \"META_READ_DATA_READ\"\n	when r.defaultpermission = 10 then \"META_WRITE_DATA_READ\"\n	when r.defaultpermission = 7 then \"META_READ_DATA_WRITE\"\n	when r.defaultpermission = 14 then \"META_WRITE_DATA_WRITE\"\n	when r.defaultpermission = 18 then \"ADMIN\" \nelse \"No Permission Granted\"\nend as Permission\nfrom user u\nInner join user_role as ur on u.userid=ur.userid\nLeft join roles as r on ur.roleid=r.roleid', 'select  u.userid as Username,u.FNAME as Firstname,u.LNAME as Lastname,u.EMAIL as Mailid,coalesce(u.GENDER, \"\") as GENDER,coalesce(u.city_state, \"\") as city_state,coalesce(u.zip, \"\") as zip,u.PERSONAL_PHONE,coalesce(u.ADDRESS, \"\") as ADDRESS,coalesce(u.usertype, \"\") as USERTYPE, r.AUTHORITY as Role,  \ncase\n	when r.defaultpermission = 1 then \"META_READ_DATA_NOPERMISSION\"\n	when r.defaultpermission = 2 then \"META_WRITE_DATA_NOPERMISSION\"\n	when r.defaultpermission = 3 then \"META_READ_DATA_PREVIEW\"\n	when r.defaultpermission = 6 then \"META_WRITE_DATA_PREVIEW\"\n	when r.defaultpermission = 5 then \"META_READ_DATA_READ\"\n	when r.defaultpermission = 10 then \"META_WRITE_DATA_READ\"\n	when r.defaultpermission = 7 then \"META_READ_DATA_WRITE\"\n	when r.defaultpermission = 14 then \"META_WRITE_DATA_WRITE\"\n	when r.defaultpermission = 18 then \"ADMIN\" \nelse \"No Permission Granted\"\nend as Permission\nfrom user u\nInner join user_role as ur on u.userid=ur.userid\nLeft join roles as r on ur.roleid=r.roleid\nwhere  u.userid=?', 'select  u.userid as Username,count(r.AUTHORITY) as Role\nfrom user u\nInner join user_role as ur on u.userid=ur.userid\nLeft join roles as r on ur.roleid=r.roleid\ngroup by u.userid	\norder by count(r.AUTHORITY) DESC ', '0', 'Invalid input', null);
INSERT INTO `stg_oereportext` VALUES ('1013', 'Track Number of Reports by Report Group', '-1', 'OvalEdge Reports', '\nThis out of box report in OvalEdge tracks the total number of reports that are crawled from various BI tools and displayed by the report group name. Multiple Charts and dashboards can be grouped under a single Report group to easily manage its security permissions.\nSemi_Detail:\nThe Detail displays the domain type , report group , count of reports  \nReport Drill-down details:\nSelecting a report group name in the bar chart lists the Domain of the report group, report group name, and the chart names in the report group.', 'Select Distinct D.Domaintype as Reportgrouptype,D.value as ReportGroup,count(distinct chart.CHARTID) as \"#Reports\"\nFrom chart\nInner Join \ndomain D on (D.Domainid = chart.DOMAINID)\nJoin connectioninfo ci ON(D.connectioninfoid = ci.connectioninfoid)\nGroup BY D.Domaintype,D.value', 'Select D.Domaintype as Reportgrouptype,D.value as ReportGroup,chart.CHARTNAME as ReportName,chart.CHARTTYPE as ReportType\nFrom chart\nInner Join domain D on (D.Domainid = chart.DOMAINID)\nJoin connectioninfo ci ON(D.connectioninfoid = ci.connectioninfoid)\nwhere D.value=?', 'Select D.value as ReportGroup,Count(chart.CHARTNAME) as Count \nFrom chart\nInner Join domain D on (D.Domainid = chart.DOMAINID)\nJoin connectioninfo ci ON(D.connectioninfoid = ci.connectioninfoid)\nGroup BY D.value\nOrder by Count(chart.CHARTNAME) DESC', '0', 'Invalid input', null);
INSERT INTO `stg_oereportext` VALUES ('1014', 'Alert Audit Log Report ', '-1', 'OvalEdge Reports', 'This is a Time activity report that is used to manage the alerts. The alert audit log report includes the date, a user associated with the activity, and the alert query.', 'SELECT Alertname, \n       Message,\n       Datasetname, \n       Count(Distinct alert.alertid) Alerts \nFROM   alert \n       INNER JOIN alertuser \n               ON ( alert.alertid = alertuser.alertid ) \n       INNER JOIN dataset \n               ON ( alert.datasetid = dataset.datasetid ) \n       INNER JOIN message \n               ON ( alertuser.userid = message.hashtagoruserid ) \n\nGROUP  BY alertname, \n          message, \n          datasetname', 'SELECT Distinct Alertname, \n       Message, \n       Helptext, \n       Datasetname, \n      DATE_FORMAT(message.LASTMODDATE, \"%Y-%m-%d %h:%i %p\") timeraised, \n       alertuser.userid     Alertuser \nFROM   alert \n       INNER JOIN alertuser \n               ON ( alert.alertid = alertuser.alertid ) \n       INNER JOIN dataset \n               ON ( alert.datasetid = dataset.datasetid ) \n       INNER JOIN message \n               ON ( alertuser.userid = message.hashtagoruserid )\n       INNER JOIN comment\n              ON (comment.commentid = message.commentid and alert.datasetid = comment.objectid) \nwhere comment.commenttype = \"SYSTEM_ALERT\" and comment.objecttype is null \nand Cast(Cast(message.LASTMODDATE  AS DATE)as char)=?', 'SELECT Cast(Cast(message.LASTMODDATE AS DATE)as char) as Date , \n         Count(Distinct alert.alertid) as Count\nFROM     alert \n       INNER JOIN alertuser \n               ON ( alert.alertid = alertuser.alertid ) \n       INNER JOIN dataset \n               ON ( alert.datasetid = dataset.datasetid )\n       INNER JOIN message \n               ON ( alertuser.userid = message.hashtagoruserid ) \n	   INNER JOIN comment\n			   ON (comment.commentid = message.commentid) \nwhere  comment.objecttype is NULL and comment.commenttype = \"SYSTEM_ALERT\"\nGROUP BY Date\nOrder By Count DESC', '0', 'Invalid input', null);
INSERT INTO `stg_oereportext` VALUES ('1015', 'Count of OvalEdge Authors and Readers', '-1', 'OvalEdge Reports', 'This report allows the OvalEdge administrator to keep track on the number of subscribed users and their Metadata contribution in the application. The report is split based on the number of Author(Meta write permissions) and Reader(Meta Read Permission) subscribers using the system on the object level.\n	\nNote: This report accounts users highest metadata permission on the objects.', 'select Type as \"User Type\",Users from (select \"Authors\" as Type ,count(distinct  usr.userid) as \"Users\" from acl_object_identity oi\njoin acl_class class on (oi.object_id_class = class.id)\njoin acl_entry entry on (entry.acl_object_identity = oi.id )\njoin acl_sid sid on (entry.sid = sid.id) join roles role on (sid.sid = role.authority)\njoin user_role usr on (usr.roleid = role.roleid)\nWHERE class.class in ( \"com.ovaledge.oasis.domain.OeSchema\", \"com.ovaledge.oasis.domain.OeTable\", \"com.ovaledge.oasis.domain.Domain\", \"com.ovaledge.oasis.domain.Chart\", \"com.ovaledge.oasis.domain.FileFolder\")\nand (entry.mask = 2 or entry.mask = 6 or entry.mask = 10 or entry.mask = 14 or entry.mask = 18)\nunion ALL\nselect \"Readers\" as Type ,\ncount(distinct  usr.userid) as \"Users\"\nfrom acl_object_identity oi\njoin acl_class class on (oi.object_id_class = class.id)\njoin acl_entry entry on (entry.acl_object_identity = oi.id )\njoin acl_sid sid on (entry.sid = sid.id) join roles role on (sid.sid = role.authority)\njoin user_role usr on (usr.roleid = role.roleid)\nWHERE class.class in ( \"com.ovaledge.oasis.domain.OeSchema\", \"com.ovaledge.oasis.domain.OeTable\", \"com.ovaledge.oasis.domain.Domain\", \"com.ovaledge.oasis.domain.Chart\", \"com.ovaledge.oasis.domain.FileFolder\")\nand (entry.mask = 1 or entry.mask = 3 or entry.mask = 5 or entry.mask = 7) and usr.userid not in (select usr.userid from acl_object_identity oi\njoin acl_class class on (oi.object_id_class = class.id)\njoin acl_entry entry on (entry.acl_object_identity = oi.id )\njoin acl_sid sid on (entry.sid = sid.id) join roles role on (sid.sid = role.authority)\njoin user_role usr on (usr.roleid = role.roleid)\nWHERE class.class in ( \"com.ovaledge.oasis.domain.OeSchema\", \"com.ovaledge.oasis.domain.OeTable\", \"com.ovaledge.oasis.domain.Domain\", \"com.ovaledge.oasis.domain.Chart\", \"com.ovaledge.oasis.domain.FileFolder\")\nand (entry.mask = 2 or entry.mask = 6 or entry.mask = 10 or entry.mask = 14 or entry.mask = 18) )) A\nOrder by Users desc', 'select userid,title,GROUP_CONCAT(distinct User_role) as User_role,GROUP_CONCAT(distinct Permission) as \"Permission\" from (select  \"Readers\" as Type, usr.userid,u.title,role.AUTHORITY as \"User_role\",\n(case when mask=1 then \"MRDN\"\nwhen mask=2 then \"MWDN\"\nwhen mask=3 then \"MRDP\"\nwhen mask=6 then \"MWDP\"\nwhen  mask=5 then \"MRDR\"\nwhen mask=10 then \"MWDR\"\nwhen mask=7 then \"MRDW\"\nwhen  mask=14 then \"MWDW\"\nwhen mask=18 then \"ADM\" end ) as \"Permission\"\n from acl_object_identity oi\njoin acl_class class on (oi.object_id_class = class.id)\njoin acl_entry entry on (entry.acl_object_identity = oi.id )\njoin acl_sid sid on (entry.sid = sid.id) join roles role on (sid.sid = role.authority)\njoin user_role usr on (usr.roleid = role.roleid)\njoin user u on (usr.userid=u.userid)\nWHERE class.class in ( \"com.ovaledge.oasis.domain.OeSchema\", \"com.ovaledge.oasis.domain.OeTable\", \"com.ovaledge.oasis.domain.Domain\", \"com.ovaledge.oasis.domain.Chart\", \"com.ovaledge.oasis.domain.FileFolder\")\nand (entry.mask = 1 or entry.mask = 3 or entry.mask = 5 or entry.mask = 7) and usr.userid not in (select usr.userid from acl_object_identity oi\njoin acl_class class on (oi.object_id_class = class.id)\njoin acl_entry entry on (entry.acl_object_identity = oi.id )\njoin acl_sid sid on (entry.sid = sid.id) join roles role on (sid.sid = role.authority)\njoin user_role usr on (usr.roleid = role.roleid)\nWHERE class.class in ( \"com.ovaledge.oasis.domain.OeSchema\", \"com.ovaledge.oasis.domain.OeTable\", \"com.ovaledge.oasis.domain.Domain\", \"com.ovaledge.oasis.domain.Chart\", \"com.ovaledge.oasis.domain.FileFolder\")\nand (entry.mask = 2 or entry.mask = 6 or entry.mask = 10 or entry.mask = 14 or entry.mask = 18) )\nunion ALL\nselect  \"Authors\" as Type, usr.userid,u.title,role.AUTHORITY as \"User_role\",\n(case when mask=1 then \"MRDN\"\nwhen mask=2 then \"MWDN\"\nwhen mask=3 then \"MRDP\"\nwhen mask=6 then \"MWDP\"\nwhen  mask=5 then \"MRDR\"\nwhen mask=10 then \"MWDR\"\nwhen mask=7 then \"MRDW\"\nwhen  mask=14 then \"MWDW\"\nwhen mask=18 then \"ADM\" end ) as \"Permission\"\n from acl_object_identity oi\njoin acl_class class on (oi.object_id_class = class.id)\njoin acl_entry entry on (entry.acl_object_identity = oi.id )\njoin acl_sid sid on (entry.sid = sid.id) join roles role on (sid.sid = role.authority)\njoin user_role usr on (usr.roleid = role.roleid)\njoin user u on (usr.userid=u.userid)\nWHERE class.class in ( \"com.ovaledge.oasis.domain.OeSchema\", \"com.ovaledge.oasis.domain.OeTable\", \"com.ovaledge.oasis.domain.Domain\", \"com.ovaledge.oasis.domain.Chart\", \"com.ovaledge.oasis.domain.FileFolder\")\nand (entry.mask = 2 or entry.mask = 6 or entry.mask = 10 or entry.mask = 14 or entry.mask = 18)\n) A\n where type = ?\n group by userid,title', 'select Type as \"User Type\",Users from (select \"Authors\" as Type ,count(distinct  usr.userid) as \"Users\" from acl_object_identity oi\njoin acl_class class on (oi.object_id_class = class.id)\njoin acl_entry entry on (entry.acl_object_identity = oi.id )\njoin acl_sid sid on (entry.sid = sid.id) join roles role on (sid.sid = role.authority)\njoin user_role usr on (usr.roleid = role.roleid)\nWHERE class.class in ( \"com.ovaledge.oasis.domain.OeSchema\", \"com.ovaledge.oasis.domain.OeTable\", \"com.ovaledge.oasis.domain.Domain\", \"com.ovaledge.oasis.domain.Chart\", \"com.ovaledge.oasis.domain.FileFolder\")\nand (entry.mask = 2 or entry.mask = 6 or entry.mask = 10 or entry.mask = 14 or entry.mask = 18)\nunion ALL\nselect \"Readers\" as Type ,\ncount(distinct  usr.userid) as \"Users\"\nfrom acl_object_identity oi\njoin acl_class class on (oi.object_id_class = class.id)\njoin acl_entry entry on (entry.acl_object_identity = oi.id )\njoin acl_sid sid on (entry.sid = sid.id) join roles role on (sid.sid = role.authority)\njoin user_role usr on (usr.roleid = role.roleid)\nWHERE class.class in ( \"com.ovaledge.oasis.domain.OeSchema\", \"com.ovaledge.oasis.domain.OeTable\", \"com.ovaledge.oasis.domain.Domain\", \"com.ovaledge.oasis.domain.Chart\", \"com.ovaledge.oasis.domain.FileFolder\")\nand (entry.mask = 1 or entry.mask = 3 or entry.mask = 5 or entry.mask = 7) and usr.userid not in (select usr.userid from acl_object_identity oi\njoin acl_class class on (oi.object_id_class = class.id)\njoin acl_entry entry on (entry.acl_object_identity = oi.id )\njoin acl_sid sid on (entry.sid = sid.id) join roles role on (sid.sid = role.authority)\njoin user_role usr on (usr.roleid = role.roleid)\nWHERE class.class in ( \"com.ovaledge.oasis.domain.OeSchema\", \"com.ovaledge.oasis.domain.OeTable\", \"com.ovaledge.oasis.domain.Domain\", \"com.ovaledge.oasis.domain.Chart\", \"com.ovaledge.oasis.domain.FileFolder\")\nand (entry.mask = 2 or entry.mask = 6 or entry.mask = 10 or entry.mask = 14 or entry.mask = 18) )) A\nOrder by Users desc', '0', 'Invalid input', null);
INSERT INTO `stg_oereportext` VALUES ('1016', 'Data Popularity-Queries', '-1', 'OvalEdge Reports', 'This report is a good way to track the most frequently queried data tables in OvalEdge. Count of Queries on single table are displayed in the semi detail level. All cataloged queries are counted and represented in the drill down level. Join queries that are only executed and not cataloged are not included in this report.', 'select os.schemaname,ot.tablename ,sum(tus.numberqueried) as \"TotalNumberofQueries\"              \nfrom  tableuserstats tus inner join oetable ot on (tus.oetableid=ot.oetableid)\ninner join oeschema os on (ot.oeschemaid=os.oeschemaid)\ninner join connectioninfo ci on (os.connectioninfoid=ci.connectioninfoid)\nwhere os.active=1\ngroup by os.schemaname,ot.tablename \nhaving sum(tus.numberqueried)<>0\nOrder by TotalNumberofQueries desc ', 'select A.Connectionname as \"Database Name\",A.schemaname,A.NumberofQueries as \"Number of Queries\",A.Cataloged as \"Number of Queries Cataloged\" from (select NumberofQueries.DataObject,NumberofQueries.Connectionname,NumberofQueries.schemaname,NumberofQueries.NumberofQueries,Cataloged.Cataloged\nfrom\n(select \"Table\" as DataObject,ci.`name` as Connectionname,os.schemaname as schemaname,sum(tus.numberqueried) as \"NumberofQueries\" ,0 as \"Cataloged\"         \nfrom  tableuserstats tus inner join oetable ot on (tus.oetableid=ot.oetableid)\ninner join oeschema os on (ot.oeschemaid=os.oeschemaid)\ninner join connectioninfo ci on (os.connectioninfoid=ci.connectioninfoid)\nwhere os.active=1\ngroup by ci.`name`,os.schemaname\nhaving sum(tus.numberqueried)<>0 ) NumberofQueries inner join \n(select  \"Table\" as DataObject,ci.name as Connectionname,os.schemaname as schemaname, 0 as \"NumberofQueries\",count(DISTINCT ds.datasetid) as \"Cataloged\"       \nfrom  tableuserstats tus inner join oetable ot on (tus.oetableid=ot.oetableid)\ninner join oeschema os on (ot.oeschemaid=os.oeschemaid)\ninner join connectioninfo ci on (os.connectioninfoid=ci.connectioninfoid)\nINNER JOIN dataset ds on (ds.connectioninfoid = ci.connectioninfoid and ds.userselecteddb = os.schemaname)\nwhere os.active=1 and ds.saved = 1 AND ds.jobtype IN (\"SQL\")\ngroup by ci.name,os.schemaname) Cataloged on NumberofQueries.Connectionname=Cataloged.Connectionname and NumberofQueries.schemaname=Cataloged.schemaname) A\nwhere A.DataObject=?\norder by A.NumberofQueries desc', 'select \"Table\" as DataObject,sum(tus.numberqueried) as \"TotalNumberofQueries\"              \nfrom  tableuserstats tus inner join oetable ot on (tus.oetableid=ot.oetableid)\ninner join oeschema os on (ot.oeschemaid=os.oeschemaid)\ninner join connectioninfo ci on (os.connectioninfoid=ci.connectioninfoid)\nwhere os.active=1\nOrder by TotalNumberofQueries desc ', '0', 'Invalid input', null);
INSERT INTO `stg_oereportext` VALUES ('1017', 'DataPopularity By Views', '-1', 'OvalEdge Reports', 'This report is a good way to track the most frequently accessed data object in OvalEdge.', 'select * from (select UPPER(objecttype) as DataObjects, sum(totalviewed) as \"NumberofViews\" from \n(select (case when O.objecttype = \"oeschema\" then \"schema\" end) as objecttype,\nnumberlooked as totalviewed from objectuserstats O\njoin oeschema S on O.objectid = S.oeschemaid\njoin connectioninfo C on C.connectioninfoid=S.connectioninfoid\nwhere O.objecttype=\"oeschema\"\n)A\ngroup by objecttype\nUNION ALL\nselect UPPER(objecttype) as DataObjects, sum(totalviewed) as \"Number of Views\" from\n(select T.type as objecttype,numberlooked as totalviewed from tableuserstats ta\njoin oetable T on T.oetableid=ta.oetableid\njoin oeschema S on S.oeschemaid=T.oeschemaid\njoin connectioninfo C on C.connectioninfoid=S.connectioninfoid\nwhere T.type=\"TABLE\"\n)B\ngroup by objecttype\nUNION ALL\nselect UPPER(objecttype) as DataObjects,SUM(totalviewed) AS \"Number of Views\" from \n(select  \"FILE\" as objecttype , numberlooked as totalviewed\nfrom fileuserstats fi\njoin file F on F.fileid = fi.fileid\njoin filefolder FF on FF.filefolderid=F.filefolderid\njoin connectioninfo C on C.connectioninfoid=FF.connectioninfoid\n)C\ngroup by objecttype\nUNION ALL\nselect objecttype as DataObjects,SUM(totalviewed) AS \"Number of Views\" from\n(select  \"REPORTS\" as objecttype,numberlooked as totalviewed\nfrom chartuserstats ch\njoin chart C on C.CHARTID=ch.chartid\njoin domain D on D.DOMAINID=C.DOMAINID\njoin connectioninfo ci on ci.connectioninfoid=D.connectioninfoid\n) D group by objecttype\n) E order by NumberofViews desc', 'select * from (select C.servertype as \"DATABASE NAME\",\n( case when conntype=0 then \"DBMS\" when conntype=1 then \"NFS\" when conntype=2 then \"Reports\" end )  as \"Connectiontype\",\n S.schemaname as \"Schemaname/Folder name/Report group\",\n\"\" as \"Tablename/Filename/Reportname\",\n(case when O.objecttype = \"oeschema\" then \"schema\" end ) as DataObjects,\nsum(numberlooked) as \"NumberofViews\" from objectuserstats O\njoin oeschema S on O.objectid = S.oeschemaid\njoin connectioninfo C on C.connectioninfoid=S.connectioninfoid\nwhere O.objecttype=\"oeschema\"\ngroup by \nC.servertype,\n( case when conntype=0 then \"DBMS\" when conntype=1 then \"NFS\" when conntype=2 then \"Reports\" end ),\n S.schemaname,\n(case when O.objecttype = \"oeschema\" then \"schema\" end )\nunion ALL\nselect C.servertype as \"DATABASE NAME\",\n( case when conntype=0 then \"DBMS\" when conntype=1 then \"NFS\" when conntype=2 then \"Reports\" end )  as \"Connectiontype\", \nS.schemaname  as \"Schemaname/Folder name/Report group\", \nT.tablename as \"Tablename/Filename/Reportname\",T.type as DataObjects,sum(numberlooked) as \"NumberofViews\" from tableuserstats ta\njoin oetable T on T.oetableid=ta.oetableid\njoin oeschema S on S.oeschemaid=T.oeschemaid\njoin connectioninfo C on C.connectioninfoid=S.connectioninfoid\nwhere T.type=\"TABLE\"\ngroup by \nC.servertype,\n( case when conntype=0 then \"DBMS\" when conntype=1 then \"NFS\" when conntype=2 then \"Reports\" end ),\n S.schemaname,\nT.tablename,\nT.type\nUNION ALL\nselect C.servertype as bucketname,\n( case when conntype=0 then \"DBMS\" when conntype=1 then \"NFS\" when conntype=2 then \"Reports\" end )  as \"Connectiontype\",\nF.location as \"Schemaname/Folder name/Report group\",\nF.filename as \"Tablename/Filename/Reportname\", \n\"FILE\" as DataObjects ,\nsum(numberlooked) as \"NumberofViews\"\nfrom fileuserstats fi\njoin file F on F.fileid = fi.fileid\njoin filefolder FF on FF.filefolderid=F.filefolderid\njoin connectioninfo C on C.connectioninfoid=FF.connectioninfoid\ngroup by \nC.servertype,\n( case when conntype=0 then \"DBMS\" when conntype=1 then \"NFS\" when conntype=2 then \"Reports\" end ),\nF.location ,\nF.filename \nunion ALL\nselect ci.servertype as \"DATABASE NAME\",\n( case when conntype=0 then \"DBMS\" when conntype=1 then \"NFS\" when conntype=2 then \"Reports\" end ) as \"Connectiontype\",\nD.`VALUE` as \"Schemaname/Folder name/Report group\",\nC.CHARTNAME \"Tablename/Filename/Reportname\", \"REPORTS\" as DataObjects,sum(numberlooked) as \"NumberofViews\"\nfrom chartuserstats ch\njoin chart C on C.CHARTID=ch.chartid\njoin domain D on D.DOMAINID=C.DOMAINID\njoin connectioninfo ci on ci.connectioninfoid=D.connectioninfoid\ngroup by \nci.servertype,\n( case when conntype=0 then \"DBMS\" when conntype=1 then \"NFS\" when conntype=2 then \"Reports\" end ),\nD.`VALUE` ,\nC.CHARTNAME \n) A\nwhere A.DataObjects=?\norder by NumberofViews desc', 'select * from (select UPPER(objecttype) as DataObjects, sum(totalviewed) as \"NumberofViews\" from \n(select (case when O.objecttype = \"oeschema\" then \"schema\" end) as objecttype,\nnumberlooked as totalviewed from objectuserstats O\njoin oeschema S on O.objectid = S.oeschemaid\njoin connectioninfo C on C.connectioninfoid=S.connectioninfoid\nwhere O.objecttype=\"oeschema\"\n)A\ngroup by objecttype\nUNION ALL\nselect UPPER(objecttype) as DataObjects, sum(totalviewed) as \"NumberofViews\" from\n(select T.type as objecttype,numberlooked as totalviewed from tableuserstats ta\njoin oetable T on T.oetableid=ta.oetableid\njoin oeschema S on S.oeschemaid=T.oeschemaid\njoin connectioninfo C on C.connectioninfoid=S.connectioninfoid\nwhere T.type=\"TABLE\"\n)B\ngroup by objecttype\nUNION ALL\nselect UPPER(objecttype) as DataObjects,SUM(totalviewed) AS \"NumberofViews\" from \n(select  \"FILE\" as objecttype , numberlooked as totalviewed\nfrom fileuserstats fi\njoin file F on F.fileid = fi.fileid\njoin filefolder FF on FF.filefolderid=F.filefolderid\njoin connectioninfo C on C.connectioninfoid=FF.connectioninfoid\n)C\ngroup by objecttype\nUNION ALL\nselect objecttype as DataObjects,SUM(totalviewed) AS \"NumberofViews\" from\n(select  \"REPORTS\" as objecttype,numberlooked as totalviewed\nfrom chartuserstats ch\njoin chart C on C.CHARTID=ch.chartid\njoin domain D on D.DOMAINID=C.DOMAINID\njoin connectioninfo ci on ci.connectioninfoid=D.connectioninfoid\n) D \n) E order by NumberofViews desc', '0', 'Invalid input', null);
INSERT INTO `stg_oereportext` VALUES ('1018', 'Number Of Database Connections ', '-1', 'OvalEdge Reports', 'This report tracks the total number of database connections that is included in this system to manage the pricing related to the contract.', 'select Type as \"Connection Type\",sum(Connections) \"Number of Database Connections\" from (select Type,databasename,cnt \"#Tables\",\n(case when (cnt-1)/500-truncate((cnt-1)/500,0)=truncate((cnt-1)/500,0) then truncate((cnt-1)/500,0) else truncate((cnt-1)/500,0)+1 end ) \"Connections\"\n from \n(select \"NOSQL\" as Type, \n concat(servertype,\"-\",ipaddress) Databasename ,\ncount(oetableid) cnt\nfrom oetable \ninner join oeschema on (oetable.oeschemaid=oeschema.oeschemaid)\ninner join connectioninfo c on (oeschema.connectioninfoid=c.connectioninfoid)\nwhere conntype=0 and servertype=\"MongoDB\"\nand oeschema.crawled=1\nand c.connectioninfoid not like \"-1\"\ngroup by concat(servertype,\"-\",ipaddress)\n) A\nunion ALL\nselect \"RDBMS\" as Type,concat(servertype,\"-\",ipaddress) Databasename,count(oetableid) as\"#Tables\", count(distinct ipaddress) as \"Connections\" \nfrom oetable \ninner join oeschema on (oetable.oeschemaid=oeschema.oeschemaid)\ninner join connectioninfo c on (oeschema.connectioninfoid=c.connectioninfoid)\nwhere conntype=0 and servertype<>\"MongoDB\"\nand oeschema.crawled=1\nand c.connectioninfoid not like \"-1\"\ngroup by concat(servertype,\"-\",ipaddress)\nunion ALL\nselect \"Reports\" as type,concat(servertype,\"-\",ipaddress) Databasename,count(chartid) as\"#Reports\",count(distinct ipaddress ) as \"Connections\" \nfrom connectioninfo c inner join domain d on (c.connectioninfoid=d.connectioninfoid)\ninner join chart on (chart.DOMAINID=d.DOMAINID)\nwhere conntype=2 and d.crawled=1 and chart.active=1\ngroup by concat(servertype,\"-\",ipaddress)\n) A\ngroup by Type', 'select Type as \"Connection Type\",databasename as \"Server Type|IP Address\",cnt as \"#Tables/#Reports\",con as \"Number of Connections\" from (select Type ,databasename ,cnt,\n(case when (cnt-1)/500-truncate((cnt-1)/500,0)=truncate((cnt-1)/500,0) then truncate((cnt-1)/500,0) else truncate((cnt-1)/500,0)+1 end ) con\n from \n(select \"NOSQL\" as Type, \n concat(servertype,\"-\",ipaddress) Databasename ,\ncount(oetableid) cnt\nfrom oetable \ninner join oeschema on (oetable.oeschemaid=oeschema.oeschemaid)\ninner join connectioninfo c on (oeschema.connectioninfoid=c.connectioninfoid)\nwhere conntype=0 and servertype=\"MongoDB\"\nand oeschema.crawled=1\nand c.connectioninfoid not like \"-1\"\ngroup by concat(servertype,\"-\",ipaddress)\n) A\nunion ALL\nselect \"RDBMS\" as Type,concat(servertype,\"-\",ipaddress) Databasename,count(oetableid) as\"#Tables/#Reports\", count(distinct ipaddress) as \"Number of Connections\" \nfrom oetable \ninner join oeschema on (oetable.oeschemaid=oeschema.oeschemaid)\ninner join connectioninfo c on (oeschema.connectioninfoid=c.connectioninfoid)\nwhere conntype=0 and servertype<>\"MongoDB\"\nand oeschema.crawled=1\nand c.connectioninfoid not like \"-1\"\ngroup by concat(servertype,\"-\",ipaddress)\nunion ALL\nselect \"Reports\" as Type,concat(servertype,\"-\",ipaddress) Databasename,count(chartid) as\"#Tables/#Reports\",count(distinct ipaddress ) as \"Number of Connections\" \nfrom connectioninfo c inner join domain d on (c.connectioninfoid=d.connectioninfoid)\ninner join chart on (chart.DOMAINID=d.DOMAINID)\nwhere conntype=2 and d.crawled=1 and chart.active=1\ngroup by concat(servertype,\"-\",ipaddress)\n\n) A\nwhere Type=?', 'select Type as \"Connection Type\",sum(Connections) \"Number of Database Connections\" from (select Type,databasename,cnt \"#Tables\",\n(case when (cnt-1)/500-truncate((cnt-1)/500,0)=truncate((cnt-1)/500,0) then truncate((cnt-1)/500,0) else truncate((cnt-1)/500,0)+1 end ) \"Connections\"\n from \n(select \"NOSQL\" as Type, \n concat(servertype,\"-\",ipaddress) Databasename ,\ncount(oetableid) cnt\nfrom oetable \ninner join oeschema on (oetable.oeschemaid=oeschema.oeschemaid)\ninner join connectioninfo c on (oeschema.connectioninfoid=c.connectioninfoid)\nwhere conntype=0 and servertype=\"MongoDB\"\nand oeschema.crawled=1\nand c.connectioninfoid not like \"-1\"\ngroup by concat(servertype,\"-\",ipaddress)\n) A\nunion ALL\nselect \"RDBMS\" as Type,concat(servertype,\"-\",ipaddress) Databasename,count(oetableid) as\"#Tables\", count(distinct ipaddress) as \"Connections\" \nfrom oetable \ninner join oeschema on (oetable.oeschemaid=oeschema.oeschemaid)\ninner join connectioninfo c on (oeschema.connectioninfoid=c.connectioninfoid)\nwhere conntype=0 and servertype<>\"MongoDB\"\nand oeschema.crawled=1\nand c.connectioninfoid not like \"-1\"\ngroup by concat(servertype,\"-\",ipaddress)\nunion ALL\nselect \"Reports\" as type,concat(servertype,\"-\",ipaddress) Databasename,count(chartid) as\"#Reports\",count(distinct ipaddress ) as \"Connections\" \nfrom connectioninfo c inner join domain d on (c.connectioninfoid=d.connectioninfoid)\ninner join chart on (chart.DOMAINID=d.DOMAINID)\nwhere conntype=2 and d.crawled=1 and chart.active=1\ngroup by concat(servertype,\"-\",ipaddress)\n) A\ngroup by Type', '0', 'Invalid input', null);
INSERT INTO `stg_oereportext` VALUES ('1019', 'User Adoption Report', '-1', 'OvalEdge Reports', 'The User Adoption Report allows OvalEdge administrators to run a real-time report on how often users are logging into OvalEdge and helps to  improve the ways of onboarding users into the system.\nThis weekly report, lists all account users, their profile details, total number of login sessions, and the total hours of usage for the past 4 weeks, week 1 being the latest.', 'select FNAME as \"FIRST NAME\",LNAME \"LAST NAME\",EMAIL as \"USER EMAIL\",title as TITLE,group_concat(distinct Userrole) as Userrole ,DATE_FORMAT(createdate, \"%Y-%m-%d %h:%i %p\") as \"USER CREATED DATE\",DATE_FORMAT(max(lastlogin), \"%Y-%m-%d %h:%i %p\") as \" LAST LOGIN\" ,\ncoalesce(sum(case when  date>=date_add(curdate(), interval  -WEEKDAY(curdate())-1 day)\nand date <=date_add(date_add(curdate(), interval  -WEEKDAY(curdate())-1 day), interval 6 day) then cnt end ),0) \"USER SESSIONS WEEK1\",\ncoalesce(sum(case when  date>=date_add(date_add(curdate(), interval  -WEEKDAY(curdate())-1 day), interval -1 week)\nand date < date_add(curdate(), interval  -WEEKDAY(curdate())-1 day) then cnt end ),0) \"USER SESSIONS WEEK2\",\ncoalesce(sum(case when  date>=date_add(date_add(curdate(), interval  -WEEKDAY(curdate())-1 day), interval -2 week)\nand date < date_add(date_add(curdate(), interval  -WEEKDAY(curdate())-1 day), interval -1 week) then cnt end ),0) \"USER SESSIONS WEEK3\",\ncoalesce(sum(case when  date>=date_add(date_add(curdate(), interval  -WEEKDAY(curdate())-1 day), interval -3 week)\nand date < date_add(date_add(curdate(), interval  -WEEKDAY(curdate())-1 day), interval -2 week) then cnt end ),0) \"USER SESSIONS WEEK4\",\nround(coalesce(sum(case when  date>=date_add(curdate(), interval  -WEEKDAY(curdate())-1 day)\nand date <=date_add(date_add(curdate(), interval  -WEEKDAY(curdate())-1 day), interval 6 day) then timestampdiff(second,starttime,endtime)/3600 end ),0),1) as \"HOURS LOGGED WEEK1\",\nround(coalesce(sum(case when  date>=date_add(date_add(curdate(), interval  -WEEKDAY(curdate())-1 day), interval -1 week)\nand date < date_add(curdate(), interval  -WEEKDAY(curdate())-1 day) then timestampdiff(second,starttime,endtime)/3600 end ),0),1) as \"HOURS LOGGED WEEK2\",\nround(coalesce(sum(case when  date>=date_add(date_add(curdate(), interval  -WEEKDAY(curdate())-1 day), interval -2 week)\nand date < date_add(date_add(curdate(), interval  -WEEKDAY(curdate())-1 day), interval -1 week) then timestampdiff(second,starttime,endtime)/3600 end ),0),1) as \"HOURS LOGGED WEEK3\",\nround(coalesce(sum(case when  date>=date_add(date_add(curdate(), interval  -WEEKDAY(curdate())-1 day), interval -3 week)\nand date < date_add(date_add(curdate(), interval  -WEEKDAY(curdate())-1 day), interval -2 week) then timestampdiff(second,starttime,endtime)/3600 end ),0),1) as \"HOURS LOGGED WEEK4\",\nround((coalesce(sum(case when  date>=date_add(curdate(), interval  -WEEKDAY(curdate())-1 day)\nand date <=date_add(date_add(curdate(), interval  -WEEKDAY(curdate())-1 day), interval 6 day) then cnt end ),0) +\ncoalesce(sum(case when  date>=date_add(date_add(curdate(), interval  -WEEKDAY(curdate())-1 day), interval -1 week)\nand date < date_add(curdate(), interval  -WEEKDAY(curdate())-1 day) then cnt end ),0) +\ncoalesce(sum(case when  date>=date_add(date_add(curdate(), interval  -WEEKDAY(curdate())-1 day), interval -2 week)\nand date < date_add(date_add(curdate(), interval  -WEEKDAY(curdate())-1 day), interval -1 week) then cnt end ),0) +\ncoalesce(sum(case when  date>=date_add(date_add(curdate(), interval  -WEEKDAY(curdate())-1 day), interval -3 week)\nand date < date_add(date_add(curdate(), interval  -WEEKDAY(curdate())-1 day), interval -2 week) then cnt end ),0)) /4 ,0) as \"AVERAGE USER SESSION PER WEEK\",\nround((coalesce(sum(case when  date>=date_add(curdate(), interval  -WEEKDAY(curdate())-1 day)\nand date <=date_add(date_add(curdate(), interval  -WEEKDAY(curdate())-1 day), interval 6 day) then timestampdiff(second,starttime,endtime)/3600 end ),0) +\ncoalesce(sum(case when  date>=date_add(date_add(curdate(), interval  -WEEKDAY(curdate())-1 day), interval -1 week)\nand date < date_add(curdate(), interval  -WEEKDAY(curdate())-1 day) then timestampdiff(second,starttime,endtime)/3600 end ),0) +\ncoalesce(sum(case when  date>=date_add(date_add(curdate(), interval  -WEEKDAY(curdate())-1 day), interval -2 week)\nand date < date_add(date_add(curdate(), interval  -WEEKDAY(curdate())-1 day), interval -1 week) then timestampdiff(second,starttime,endtime)/3600 end ),0) +\ncoalesce(sum(case when  date>=date_add(date_add(curdate(), interval  -WEEKDAY(curdate())-1 day), interval -3 week)\nand date < date_add(date_add(curdate(), interval  -WEEKDAY(curdate())-1 day), interval -2 week) then timestampdiff(second,starttime,endtime)/3600 end ),0) \n) /4 ,1) as \"AVERAGE USAGE PER WEEK\"\n from (select date,createdate,sum(cnt) as cnt,FNAME,LNAME,EMAIL,title,Userrole,max(lastlogin) as  lastlogin,starttime,endtime from \n(SELECT  date_range.date as date,`user`.createdate,\ncount(usersession.usersessionid) cnt,`user`.FNAME,`user`.LNAME,`user`.EMAIL,user.title,roles.authority as Userrole ,max(usersession.starttime) as lastlogin,usersession.starttime,usersession.endtime FROM (\nSELECT a.date\nFROM (\n    SELECT Curdate() - INTERVAL (a.a + (10 * b.a) + (100 * c.a)) day\n                AS date\n        FROM (\n            SELECT 0 AS a\n                UNION ALL\n                SELECT 1\n                UNION ALL\n                SELECT 2\n                UNION ALL\n                SELECT 3\n                UNION ALL\n                SELECT 4\n                UNION ALL\n                SELECT 5\n                UNION ALL\n                SELECT 6\n                UNION ALL\n                SELECT 7\n                UNION ALL\n                SELECT 8\n               UNION ALL\n                SELECT 9\n               \n                \n            ) AS a\n            CROSS JOIN (\n                SELECT 0 AS a\n                    UNION ALL\n                SELECT 1\n                UNION ALL\n                SELECT 2\n                UNION ALL\n                SELECT 3\n                UNION ALL\n                SELECT 4\n                UNION ALL\n                SELECT 5\n                UNION ALL\n                SELECT 6\n                UNION ALL\n                SELECT 7\n                UNION ALL\n                SELECT 8\n               UNION ALL\n                SELECT 9\n                                      \n            ) AS b\n           CROSS JOIN (\n                SELECT 0 AS a\n                    UNION ALL\n                SELECT 1\n                UNION ALL\n                SELECT 2\n                UNION ALL\n                SELECT 3\n                UNION ALL\n                SELECT 4\n                UNION ALL\n                SELECT 5\n                UNION ALL\n                SELECT 6\n                UNION ALL\n                SELECT 7\n                UNION ALL\n                SELECT 8\n               UNION ALL\n                SELECT 9\n                  \n                ) AS c\n            ) AS a\n  ) AS date_range\n   LEFT JOIN  usersession \non date_range.date =cast(usersession.starttime as date)\n join user on (`user`.userid= usersession.userid)\njoin user_role ur on (ur.userid=`user`.userid)\njoin roles  on (roles.roleid=ur.roleid)\nwhere usersession.authenticated=1\ngroup by date_range.date,`user`.FNAME,`user`.LNAME,`user`.EMAIL,user.title,roles.authority,`user`.createdate,usersession.starttime,usersession.endtime\n) A\ngroup by date,FNAME,LNAME,EMAIL,title,Userrole,createdate,starttime,endtime\n) X\ngroup by FNAME,LNAME,EMAIL,title,createdate', 'select count(*) from usersession', 'select date as week ,sum(cnt) as \"#Usersessions\" from ((SELECT  concat(DATE_ADD(cast(date_range.date as date),INTERVAL(1-DAYOFWEEK(cast(date_range.date as date)))DAY),\" to \",DATE_ADD(cast(date_range.date as date),INTERVAL(7-DAYOFWEEK(cast(date_range.date as date)))DAY)) AS date,\ncount(case when authenticated=1 then usersession.usersessionid end ) cnt FROM (\nSELECT a.date\nFROM (\n    SELECT Curdate() - INTERVAL (a.a + (10 * b.a) + (100 * c.a)) day\n                AS date\n        FROM (\n            SELECT 0 AS a\n                UNION ALL\n                SELECT 1\n                UNION ALL\n                SELECT 2\n                UNION ALL\n                SELECT 3\n                UNION ALL\n                SELECT 4\n                UNION ALL\n                SELECT 5\n                UNION ALL\n                SELECT 6\n                UNION ALL\n                SELECT 7\n                UNION ALL\n                SELECT 8\n               UNION ALL\n                SELECT 9\n            ) AS a\n            CROSS JOIN (\n                SELECT 0 AS a\n                    UNION ALL\n                    SELECT 1\n                    UNION ALL\n                    SELECT 2\n                    UNION ALL\n                    SELECT 3\n                    UNION ALL\n                    SELECT 4\n                    UNION ALL\n                    SELECT 5\n                    UNION ALL\n                    SELECT 6\n                    UNION ALL\n                    SELECT 7\n                    UNION ALL\n                    SELECT 8\n                    UNION ALL\n                    SELECT 9\n            ) AS b\n           CROSS JOIN (\n                SELECT 0 AS a\n                    UNION ALL\n                    SELECT 1\n                    UNION ALL\n                    SELECT 2\n                    UNION ALL\n                    SELECT 3\n                  UNION ALL\n                    SELECT 4\n                    UNION ALL\n                    SELECT 5\n                    UNION ALL\n                    SELECT 6\n                    UNION ALL\n                    SELECT 7\n                    UNION ALL\n                    SELECT 8\n                    UNION ALL\n                    SELECT 9\n                ) AS c\n            ) AS a\n  ) AS date_range\n   LEFT JOIN  usersession \non date_range.date =cast(usersession.starttime as date)\ngroup by concat(DATE_ADD(cast(date_range.date as date),INTERVAL(1-DAYOFWEEK(cast(date_range.date as date)))DAY),\" to \",DATE_ADD(cast(date_range.date as date),INTERVAL(7-DAYOFWEEK(cast(date_range.date as date)))DAY)))\n) A\ngroup by date \norder by date desc \nlimit 4', '0', 'Invalid input', null);
INSERT INTO `stg_oereportext` VALUES ('1020', 'Week Over Week Growth for BusinessGlossary', '-1', 'OvalEdge Reports', 'This report allows the data management team to know the incremental volume change of the terms that are associated with the data object when compared to the previous week. This report shows the count of objects associated with the terms for the past 4 weeks from the report incision date.', 'select * from (select DataAssetsType as \"DataAssetsType\", \ncoalesce(sum(case when  lastupdatetime>=date_add(curdate(), interval  -WEEKDAY(curdate())-1 day)\nand lastupdatetime <=date_add(date_add(curdate(), interval  -WEEKDAY(curdate())-1 day), interval 6 day) then cnt end ),0) \"week1\",\ncoalesce(sum(case when  lastupdatetime>=date_add(date_add(curdate(), interval  -WEEKDAY(curdate())-1 day), interval -1 week)\nand lastupdatetime <date_add(curdate(), interval  -WEEKDAY(curdate())-1 day) then cnt end ),0) \"week2\",\ncoalesce(sum(case when  lastupdatetime>=date_add(date_add(curdate(), interval  -WEEKDAY(curdate())-1 day), interval -2 week)\nand lastupdatetime < date_add(date_add(curdate(), interval  -WEEKDAY(curdate())-1 day), interval -1 week) then cnt end ),0) \"week3\",\ncoalesce(sum(case when  lastupdatetime>=date_add(date_add(curdate(), interval  -WEEKDAY(curdate())-1 day), interval -3 week)\nand lastupdatetime < date_add(date_add(curdate(), interval  -WEEKDAY(curdate())-1 day), interval -2 week) then cnt end ),0) \"week4\",\nround((coalesce(sum(case when  lastupdatetime>=date_add(curdate(), interval  -WEEKDAY(curdate())-1 day)\nand lastupdatetime <=date_add(date_add(curdate(), interval  -WEEKDAY(curdate())-1 day), interval 6 day) then cnt end ),0) +\ncoalesce(sum(case when  lastupdatetime>=date_add(date_add(curdate(), interval  -WEEKDAY(curdate())-1 day), interval -1 week)\nand lastupdatetime <date_add(curdate(), interval  -WEEKDAY(curdate())-1 day) then cnt end ),0) +\ncoalesce(sum(case when  lastupdatetime>=date_add(date_add(curdate(), interval  -WEEK