INSERT INTO `stg_oereportext` VALUES ('1022', 'Week Over Week Growth for Tags', '-1', 'OvalEdge Reports', 'This report allows the data management team to know the incremental volume change of the tags that are associated with the data object when compared to the previous week. This report shows the count of objects associated with the tags for the past 4 weeks from the report incision date.', '\nselect * from (select DataAssetsType \"Data Source Detail\", \ncoalesce(sum(case when  lastupdatetime>=date_add(curdate(), interval  -WEEKDAY(curdate())-1 day)\nand lastupdatetime <=date_add(date_add(curdate(), interval  -WEEKDAY(curdate())-1 day), interval 6 day) then cnt end ),0) \"week1\",\ncoalesce(sum(case when  lastupdatetime>=date_add(date_add(curdate(), interval  -WEEKDAY(curdate())-1 day), interval -1 week)\nand lastupdatetime <date_add(curdate(), interval  -WEEKDAY(curdate())-1 day) then cnt end ),0) \"week2\",\ncoalesce(sum(case when  lastupdatetime>=date_add(date_add(curdate(), interval  -WEEKDAY(curdate())-1 day), interval -2 week)\nand lastupdatetime < date_add(date_add(curdate(), interval  -WEEKDAY(curdate())-1 day), interval -1 week) then cnt end ),0) \"week3\",\ncoalesce(sum(case when  lastupdatetime>=date_add(date_add(curdate(), interval  -WEEKDAY(curdate())-1 day), interval -3 week)\nand lastupdatetime < date_add(date_add(curdate(), interval  -WEEKDAY(curdate())-1 day), interval -2 week) then cnt end ),0) \"week4\",\nround((coalesce(sum(case when  lastupdatetime>=date_add(curdate(), interval  -WEEKDAY(curdate())-1 day)\nand lastupdatetime <=date_add(date_add(curdate(), interval  -WEEKDAY(curdate())-1 day), interval 6 day) then cnt end ),0) +\ncoalesce(sum(case when  lastupdatetime>=date_add(date_add(curdate(), interval  -WEEKDAY(curdate())-1 day), interval -1 week)\nand lastupdatetime <date_add(curdate(), interval  -WEEKDAY(curdate())-1 day) then cnt end ),0) +\ncoalesce(sum(case when  lastupdatetime>=date_add(date_add(curdate(), interval  -WEEKDAY(curdate