ableid)\ninner join oeschema on (oetable.oeschemaid=oeschema.oeschemaid )\ninner join connectioninfo c on (c.connectioninfoid=oeschema.connectioninfoid)\nwhere tags.tagtype<>2 \ngroup by concat (c.servertype,\"-\",c.name)\n) A\ngroup by Databasename,DataAssetsType\nunion ALL\nselect Type as \"Database Connection/Report Group\",DataAssetsType as \"Data Assests Type\",max(Numberoftables) as \"Numberoftables/Reports\",max(Numberoftableswithdescription) as \"Number of tables/Reports with Tags\",\nmax(Numberoftablesdags) as  \"Number of tables/Reports with DAG\",\ntruncate(((max(Numberoftableswithdescription)+max(Numberoftablesdags))/max(Numberoftables))*100,1) as \"% of tables/Reports with Tags\",\nmax(Numberoftablecolumns) as \"Number of tablecolumns/reportcolumns\",\nmax(Numberoftablecolumnswithdescription) as \"Number of tablecolumns/reportcolumn with Tags\",\ntruncate(coalesce(max(Numberoftablecolumnswithdescription)/max(Numberoftablecolumns),0)*100,1) as \"% of columns with Tags\"\nfrom\n(select c.name \"Type\" ,\"Report Group\" as \"DataAssetsType\",count(*) \"Numberoftables\",0 as  \"Numberoftableswithdescription\",0 as \"Numberoftablesdags\",0 as \"Numberoftablecolumns\",0 as \"Numberoftablecolumnswithdescription\"\nfrom chart \ninner join domain on (chart.domainid=domain.domainid)\ninner join connectioninfo c  on (c.connectioninfoid=domain.connectioninfoid)\ngroup by c.name\nunion all\nselect ci.name \"Type\" ,\"Report Group\" as \"DataAssetsType\",0 as \"Numberoftables\",count(*) as  \"Numberoftableswithdescription\",0 as \"Numberoftablesdags\",0 as \"Numberoftablecolumns\",0 as \"Numberoftablecolumnswithdescription\"\nFROM tags  \nINNER JOIN charttag ON tags.tagid=charttag.tagid                          \nINNER JOIN chart ON(chart.chartid=charttag.chartid)                            \nJOIN domain D ON(chart.domainid = D.domainid)                          \nJOIN connectioninfo ci ON(D.connectioninfoid = ci.connectioninfoid) \nwhere tags.tagtype<>2   \ngroup by ci.name\nunion all\nselect ci.name \"Type\" ,\"Report Group\" as \"DataAssetsType\",0 as \"Numberoftables\",0 as  \"Numberoftableswithdescription\",count(*) as \"Numberoftablesdags\",0 as \"Numberoftablecolumns\",0 as \"Numberoftablecolumnswithdescription\"\nFROM tags  \nINNER JOIN charttag ON tags.tagid=charttag.tagid                          \nINNER JOIN chart ON(chart.chartid=charttag.chartid)\ninner join dataassetgroup dag on (dag.tagid=charttag.tagid )                            \nJOIN domain D ON(chart.domainid = D.domainid)                          \nJOIN connectioninfo ci ON(D.connectioninfoid = ci.connectioninfoid) \ngroup by ci.name\n\nunion ALL\nselect c.name \"Type\",\"Report Group\" as \"DataAssetsType\",0 as \"Numberoftables\",0 as  \"Numberoftableswithdescription\",0 as \"Numberoftablesdags\",count(*) as \"Numberoftablecolumns\",0 as \"Numberoftablecolumnswithdescription\"\nfrom chart \ninner join chartchild on (chart.chartid=chartchild.chartid)\ninner join domain on (chart.domainid=domain.domainid)\ninner join connectioninfo c  on (c.connectioninfoid=domain.connectioninfoid)\ngroup by c.name\nunion ALL\nselect ci.name \"Type\",\"Report Group\" as \"DataAssetsType\",0 as \"Numberoftables\",0 as  \"Numberoftableswithdescription\",0 as \"Numberoftablesdags\",0 as \"Numberoftablecolumns\",count(*) as \"Numberoftablecolumnswithdescription\"\nFROM tags  \nINNER JOIN chartcolumntag ON tags.tagid=chartcolumntag.tagid                          \nINNER JOIN chartchild ON(chartcolumntag.chartcolumnid=chartchild.chartchildid)\ninner join chart on (chart.chartid=chartchild.chartid)                            \nJOIN domain D ON(chart.domainid = D.domainid)                          \nJOIN connectioninfo ci ON(D.connectioninfoid = ci.connectioninfoid) \nwhere tags.tagtype<>2   \ngroup by ci.name) A \ngroup by Type,DataAssetsType\n) C\nwhere DataAssestsType=?', 'select * from (select DataAssetsType ,\ncoalesce(round((sum(case when week(lastupdatetime,0)=week(DATE_SUB(cast(CURRENT_DATE() as date), INTERVAL 0 WEEK),0) then cnt end )-\nsum(case when week(lastupdatetime,0)=week(DATE_SUB(cast(CURRENT_DATE() as date), INTERVAL 1 WEEK),0) then cnt end ))/sum(case when week(lastupdatetime,0)=week(DATE_SUB(cast(CURRENT_DATE() as date), INTERVAL 1 WEEK),0) then cnt end ),1),0) as \"Increment from Last week\"\n from \n(select \"Report Group\" as \"DataAssetsType\",charttag.createdate as lastupdatetime,count(*) as cnt\nFROM tags  \nINNER JOIN charttag ON tags.tagid=charttag.tagid                          \nINNER JOIN chart ON(chart.chartid=charttag.chartid)                            \nJOIN domain D ON(chart.domainid = D.domainid)                          \nJOIN connectioninfo ci ON(D.connectioninfoid = ci.connectioninfoid)                       \ngroup by charttag.createdate\nunion ALL\nselect \"Report Group\" as \"Data Assets Type\",chartcolumntag.createdate as lastupdatetime,count(*) as cnt\nFROM tags  \nINNER JOIN chartcolumntag ON tags.tagid=chartcolumntag.tagid                          \nINNER JOIN chartchild ON(chartcolumntag.chartcolumnid=chartchild.chartchildid)\ninner join chart on (chart.chartid=chartchild.chartid)                            \nJOIN domain D ON(chart.domainid = D.domainid)                          \nJOIN connectioninfo ci ON(D.connectioninfoid = ci.connectioninfoid) \ngroup by chartcolumntag.createdate) A group by DataAssetsType) B\nunion ALL\nselect * from (select DataAssetsType, \ncoalesce(round((coalesce(sum(case when week(lastupdatetime,0)=week(DATE_SUB(cast(CURRENT_DATE() as date), INTERVAL 0 WEEK),0) then cnt end ),0)-\ncoalesce(sum(case when week(lastupdatetime,0)=week(DATE_SUB(cast(CURRENT_DATE() as date), INTERVAL 1 WEEK),0) then cnt end ),0))/coalesce(sum(case when week(lastupdatetime,0)=week(DATE_SUB(cast(CURRENT_DATE() as date), INTERVAL 1 WEEK),0) then cnt end ),0),1),0) as \"Increment from Last week\"\nfrom (\nselect \"Database\" as \"DataAssetsType\",tabletag.createdate as lastupdatetime,count(*) as cnt\nfrom tags\ninner join tabletag  on (tags.tagid=tabletag.tagid )\ninner join oetable on (oetable.oetableid=tabletag.oetableid)\ninner join oeschema on (oetable.oeschemaid=oeschema.oeschemaid )\ninner join connectioninfo c on (c.connectioninfoid=oeschema.connectioninfoid)\ngroup by tabletag.createdate\nunion all\nselect \"Database\" as \"DataAssetsType\",columntag.createdate as lastupdatetime,count(*) as  cnt\nfrom tags\ninner join columntag  on (tags.tagid=columntag.tagid )\ninner join oecolumn on (oecolumn.oecolumnid=columntag.columnid)\ninner join oetable on (oetable.oetableid=oecolumn.oetableid)\ninner join oeschema on (oetable.oeschemaid=oeschema.oeschemaid )\ninner join connectioninfo c on (c.connectioninfoid=oeschema.connectioninfoid)\ngroup by columntag.createdate\n) A\ngroup by DataAssetsType\n) B', '0', 'Invalid input', null);
INSERT INTO `stg_oereportext` VALUES ('1023', 'Report Column Tagged by Report Group', '-1', 'OvalEdge Reports', 'This is a Tag activity report. This report displays the total number of columns in each report that is tagged(organized) and displayed by each report group.', 'SELECT Chart_Tag_Augumeneted.Databasename as `Database`,All_Domains.value AS `Reportgroup`,Chart_Tag_Augumeneted.chartname as `Report`,Chart_Tag_Augumeneted.childname as `Reportcolumn`, Sum(chartcolumntagged) \"#Reportcolumnstagged\" FROM\n(SELECT DISTINCT value FROM domain D          \nJOIN connectioninfo ci ON(D.connectioninfoid = ci.connectioninfoid)) All_Domains       \nLEFT JOIN(SELECT ci.servertype as Databasename,D.value,chart.CHARTNAME as chartname,chartchild.childname as childname,Count(chartcolumntag.tagid) AS \"ChartcolumnTagged\" FROM tags\nINNER JOIN chartcolumntag ON tags.tagid=chartcolumntag.tagid          \nINNER JOIN chartchild  ON(chartchild.chartchildid =chartcolumntag.chartcolumnid)                      \nINNER JOIN chart ON(chartchild.chartid=chart.chartid)                                  \nJOIN domain D  ON(chart.domainid = D.domainid)                                \nJOIN connectioninfo ci ON(D.connectioninfoid = ci.connectioninfoid)                    \nGROUP BY   ci.servertype,D.value,chart.CHARTNAME,chartchild.childname) Chart_Tag_Augumeneted                      \nON(All_Domains.value=Chart_Tag_Augumeneted.value)                      \nGROUP BY  Chart_Tag_Augumeneted.Databasename,All_Domains.value,Chart_Tag_Augumeneted.chartname,Chart_Tag_Augumeneted.childname                 \nHAVING Sum(chartcolumntagged) IS NOT NULL', 'SELECT Chart_Tag_Augumeneted.databasename as `Database`,Chart_Tag_Augumeneted.value as `Reportgroup`,Chart_Tag_Augumeneted.chartname as `Report`,\nChart_Tag_Augumeneted.childname as `Reportcolumn`,Chart_Tag_Augumeneted.tagname as Associatedtags,Chart_Tag_Augumeneted.tagcreatedby as Tagcreatedby,\nDATE_FORMAT(Chart_Tag_Augumeneted.tagcreateddate ,\"%Y-%m-%d %h:%i %p\") as Tagcreateddate,Chart_Tag_Augumeneted.tagusedby as TagAssociatedby,\nDATE_FORMAT(Chart_Tag_Augumeneted.taguseddated ,\"%Y-%m-%d %h:%i %p\") as TagAssociateddate FROM\n(SELECT DISTINCT value FROM domain D          \nJOIN connectioninfo ci ON(D.connectioninfoid = ci.connectioninfoid)) All_Domains       \nLEFT JOIN(SELECT ci.servertype as databasename,D.value,chart.chartname,chartchild.childname,tags.tagname,tags.userid as tagcreatedby,tags.createdate as tagcreateddate,\nchartcolumntag.createdby as tagusedby,chartcolumntag.createdate as taguseddated, Count(chartcolumntag.tagid) AS \"ChartTagged\" FROM tags\nINNER JOIN chartcolumntag ON tags.tagid=chartcolumntag.tagid          \nINNER JOIN chartchild  ON(chartchild.chartchildid =chartcolumntag.chartcolumnid)                      \nINNER JOIN chart ON(chartchild.chartid=chart.chartid)                                  \nJOIN domain D  ON(chart.domainid = D.domainid)                                \nJOIN connectioninfo ci ON(D.connectioninfoid = ci.connectioninfoid)\nGROUP BY   ci.servertype,D.value,chart.chartname,chartchild.childname,tags.tagname,tags.userid,tags.createdate,chartcolumntag.createdby,chartcolumntag.createdate) Chart_Tag_Augumeneted                      \nON(All_Domains.value=Chart_Tag_Augumeneted.value) \n  where  All_Domains.value=? \nGROUP BY  Chart_Tag_Augumeneted.databasename,Chart_Tag_Augumeneted.value,Chart_Tag_Augumeneted.chartname,Chart_Tag_Augumeneted.childname,\nChart_Tag_Augumeneted.tagname,Chart_Tag_Augumeneted.tagcreatedby,Chart_Tag_Augumeneted.tagcreateddate,Chart_Tag_Augumeneted.tagusedby,\nChart_Tag_Augumeneted.taguseddated\nHaving sum(ChartTagged) is not null', 'SELECT All_Domains.value AS Domainname, Sum(chartcolumntagged) as \"#Reportcolumnstagged\" FROM\n(SELECT DISTINCT value FROM domain D          \nJOIN connectioninfo ci ON(D.connectioninfoid = ci.connectioninfoid)) All_Domains       \nLEFT JOIN(SELECT D.value,tags.tagname,Count(DISTINCT chartchild.chartchildid) AS \"ChartcolumnTagged\" FROM tags\nINNER JOIN chartcolumntag ON tags.tagid=chartcolumntag.tagid          \nINNER JOIN chartchild  ON(chartchild.chartchildid =chartcolumntag.chartcolumnid)                      \nINNER JOIN chart ON(chartchild.chartid=chart.chartid)                                  \nJOIN domain D  ON(chart.domainid = D.domainid)                                \nJOIN connectioninfo ci ON(D.connectioninfoid = ci.connectioninfoid)                    \nGROUP BY   D.value,tags.tagname) Chart_Tag_Augumeneted                      \nON(All_Domains.value=Chart_Tag_Augumeneted.value)                      \nGROUP BY  All_Domains.value                      \nHAVING Sum(chartcolumntagged) IS NOT NULL\nOrder By Sum(chartcolumntagged) DESC', '0', 'Invalid input', null);
INSERT INTO `stg_oereportext` VALUES ('1024', 'Search Audit', '-1', 'OvalEdge Reports', 'Global Search Audit Report.', 'select searchterm as Searchterm,concat(DATE_ADD(cast(date as date),INTERVAL(1-DAYOFWEEK(cast(date as date)))DAY),\" to \",DATE_ADD(cast(date as date),INTERVAL(7-DAYOFWEEK(cast(date as date)))DAY)) as Week,\nround((coalesce(sum(case when  date>=date_add(curdate(), interval  -WEEKDAY(curdate())-1 day)\nand date <=date_add(date_add(curdate(), interval  -WEEKDAY(curdate())-1 day), interval 6 day) then cnt end ),0) +\ncoalesce(sum(case when  date>=date_add(date_add(curdate(), interval  -WEEKDAY(curdate())-1 day), interval -1 week)\nand date < date_add(curdate(), interval  -WEEKDAY(curdate())-1 day) then cnt end ),0) +\ncoalesce(sum(case when  date>=date_add(date_add(curdate(), interval  -WEEKDAY(curdate())-1 day), interval -2 week)\nand date < date_add(date_add(curdate(), interval  -WEEKDAY(curdate())-1 day), interval -1 week) then cnt end ),0) +\ncoalesce(sum(case when  date>=date_add(date_add(curdate(), interval  -WEEKDAY(curdate())-1 day), interval -3 week)\nand date < date_add(date_add(curdate(), interval  -WEEKDAY(curdate())-1 day), interval -2 week) then cnt end ),0)),0) as \"Most Searchedterm in 4 WEEKS\"\n from (select date,sum(cnt)as cnt,searchterm from \n(SELECT  date_range.date as date,searchterm,\ncount(searchaudit.searchauditid) cnt FROM (\nSELECT a.date\nFROM (\n    SELECT Curdate() - INTERVAL (a.a + (10 * b.a) + (100 * c.a)) day\n                AS date\n        FROM (\n            SELECT 0 AS a\n                UNION ALL\n                SELECT 1\n                UNION ALL\n                SELECT 2\n                UNION ALL\n                SELECT 3\n                UNION ALL\n                SELECT 4\n                UNION ALL\n                SELECT 5\n                UNION ALL\n                SELECT 6\n                UNION ALL\n                SELECT 7\n                UNION ALL\n                SELECT 8\n               UNION ALL\n                SELECT 9\n               \n                \n            ) AS a\n            CROSS JOIN (\n                SELECT 0 AS a\n                    UNION ALL\n                SELECT 1\n                UNION ALL\n                SELECT 2\n                UNION ALL\n                SELECT 3\n                UNION ALL\n                SELECT 4\n                UNION ALL\n                SELECT 5\n                UNION ALL\n                SELECT 6\n                UNION ALL\n                SELECT 7\n                UNION ALL\n                SELECT 8\n               UNION ALL\n                SELECT 9\n                                      \n            ) AS b\n           CROSS JOIN (\n                SELECT 0 AS a\n                    UNION ALL\n                SELECT 1\n                UNION ALL\n                SELECT 2\n                UNION ALL\n                SELECT 3\n                UNION ALL\n                SELECT 4\n                UNION ALL\n                SELECT 5\n                UNION ALL\n                SELECT 6\n                UNION ALL\n                SELECT 7\n                UNION ALL\n                SELECT 8\n               UNION ALL\n                SELECT 9\n                  \n                ) AS c\n            ) AS a\n  ) AS date_range\n  left JOIN  searchaudit \non date_range.date =cast(searchaudit.searchdate as date)\nwhere searchterm is not null\ngroup by searchterm,date_range.date\n) A\ngroup by date,searchterm\n) X\ngroup by searchterm ,concat(DATE_ADD(cast(date as date),INTERVAL(1-DAYOFWEEK(cast(date as date)))DAY),\" to \",DATE_ADD(cast(date as date),INTERVAL(7-DAYOFWEEK(cast(date as date)))DAY))\norder by round((coalesce(sum(case when  date>=date_add(curdate(), interval  -WEEKDAY(curdate())-1 day)\nand date <=date_add(date_add(curdate(), interval  -WEEKDAY(curdate())-1 day), interval 6 day) then cnt end ),0) +\ncoalesce(sum(case when  date>=date_add(date_add(curdate(), interval  -WEEKDAY(curdate())-1 day), interval -1 week)\nand date < date_add(curdate(), interval  -WEEKDAY(curdate())-1 day) then cnt end ),0) +\ncoalesce(sum(case when  date>=date_add(date_add(curdate(), interval  -WEEKDAY(curdate())-1 day), interval -2 week)\nand date < date_add(date_add(curdate(), interval  -WEEKDAY(curdate())-1 day), interval -1 week) then cnt end ),0) +\ncoalesce(sum(case when  date>=date_add(date_add(curdate(), interval  -WEEKDAY(curdate())-1 day), interval -3 week)\nand date < date_add(date_add(curdate(), interval  -WEEKDAY(curdate())-1 day), interval -2 week) then cnt end ),0)),0) desc', 'select `user`.userid,user.title,group_concat(distinct roles.AUTHORITY) Userrole,coalesce(assobjectid,\" \") as assobjectid,coalesce(assobject,\" \") as assobject,searchterm,cast(cast(searchdate as date) as char) as Searchday,count(searchauditid) \"#Times Searched\"  FROM (\nSELECT a.date\nFROM (\n    SELECT Curdate() - INTERVAL (a.a + (10 * b.a) + (100 * c.a)) day\n                AS date\n        FROM (\n            SELECT 0 AS a\n                UNION ALL\n                SELECT 1\n                UNION ALL\n                SELECT 2\n                UNION ALL\n                SELECT 3\n                UNION ALL\n                SELECT 4\n                UNION ALL\n                SELECT 5\n                UNION ALL\n                SELECT 6\n                UNION ALL\n                SELECT 7\n                UNION ALL\n                SELECT 8\n               UNION ALL\n                SELECT 9\n               \n                \n            ) AS a\n            CROSS JOIN (\n                SELECT 0 AS a\n                    UNION ALL\n                SELECT 1\n                UNION ALL\n                SELECT 2\n                UNION ALL\n                SELECT 3\n                UNION ALL\n                SELECT 4\n                UNION ALL\n                SELECT 5\n                UNION ALL\n                SELECT 6\n                UNION ALL\n                SELECT 7\n                UNION ALL\n                SELECT 8\n               UNION ALL\n                SELECT 9\n                                      \n            ) AS b\n           CROSS JOIN (\n                SELECT 0 AS a\n                    UNION ALL\n                SELECT 1\n                UNION ALL\n                SELECT 2\n                UNION ALL\n                SELECT 3\n                UNION ALL\n                SELECT 4\n                UNION ALL\n                SELECT 5\n                UNION ALL\n                SELECT 6\n                UNION ALL\n                SELECT 7\n                UNION ALL\n                SELECT 8\n               UNION ALL\n                SELECT 9\n                  \n                ) AS c\n            ) AS a\n  ) AS date_range\n   LEFT JOIN  searchaudit\non date_range.date =cast(searchaudit.searchdate as date)\n join user on (`user`.userid= searchaudit.userid)\njoin user_role ur on (ur.userid=`user`.userid)\njoin roles  on (roles.roleid=ur.roleid)\nwhere concat(DATE_ADD(cast(date_range.date as date),INTERVAL(1-DAYOFWEEK(cast(date_range.date as date)))DAY),\" to \",DATE_ADD(cast(date_range.date as date),INTERVAL(7-DAYOFWEEK(cast(date_range.date as date)))DAY))=?\ngroup by `user`.userid,user.title,coalesce(assobjectid,\" \"),coalesce(assobject,\" \"),searchterm,cast(cast(searchdate as date) as char)\norder by count(searchauditid) desc', 'select date as week ,sum(cnt) as \"#SearchTerms\" from ((SELECT  concat(DATE_ADD(cast(date_range.date as date),INTERVAL(1-DAYOFWEEK(cast(date_range.date as date)))DAY),\" to \",DATE_ADD(cast(date_range.date as date),INTERVAL(7-DAYOFWEEK(cast(date_range.date as date)))DAY)) AS date,\ncount( searchauditid ) cnt FROM (\nSELECT a.date\nFROM (\n    SELECT Curdate() - INTERVAL (a.a + (10 * b.a) + (100 * c.a)) day\n                AS date\n        FROM (\n            SELECT 0 AS a\n                UNION ALL\n                SELECT 1\n                UNION ALL\n                SELECT 2\n                UNION ALL\n                SELECT 3\n                UNION ALL\n                SELECT 4\n                UNION ALL\n                SELECT 5\n                UNION ALL\n                SELECT 6\n                UNION ALL\n                SELECT 7\n                UNION ALL\n                SELECT 8\n               UNION ALL\n                SELECT 9\n            ) AS a\n            CROSS JOIN (\n                SELECT 0 AS a\n                    UNION ALL\n                    SELECT 1\n                    UNION ALL\n                    SELECT 2\n                    UNION ALL\n                    SELECT 3\n                    UNION ALL\n                    SELECT 4\n                    UNION ALL\n                    SELECT 5\n                    UNION ALL\n                    SELECT 6\n                    UNION ALL\n                    SELECT 7\n                    UNION ALL\n                    SELECT 8\n                    UNION ALL\n                    SELECT 9\n            ) AS b\n           CROSS JOIN (\n                SELECT 0 AS a\n                    UNION ALL\n                    SELECT 1\n                    UNION ALL\n                    SELECT 2\n                    UNION ALL\n                    SELECT 3\n                  UNION ALL\n                    SELECT 4\n                    UNION ALL\n                    SELECT 5\n                    UNION ALL\n                    SELECT 6\n                    UNION ALL\n                    SELECT 7\n                    UNION ALL\n                    SELECT 8\n                    UNION ALL\n                    SELECT 9\n                ) AS c\n            ) AS a\n  ) AS date_range\n   LEFT JOIN  searchaudit \non date_range.date =cast(searchaudit.searchdate as date)\ngroup by concat(DATE_ADD(cast(date_range.date as date),INTERVAL(1-DAYOFWEEK(cast(date_range.date as date)))DAY),\" to \",DATE_ADD(cast(date_range.date as date),INTERVAL(7-DAYOFWEEK(cast(date_range.date as date)))DAY)))\n) A\ngroup by date \norder by date desc \nlimit 4', 