INSERT INTO `stg_oereportext` VALUES ('1025', 'Table Endorsement Chart', '-1', 'OvalEdge Reports', 'This is an endorsement activity report for the tables, This report displays all the tables which are endorsed.', 'select ot.oetableid as TABLEID, ot.type as TYPE, concat(os.schemaname, \".\", ot.tablename) as `TABLE NAME`, if(o.endorsementtype = \"STAR\", concat(o.endorsementtype, \" - \", o.rating), o.endorsementtype) as `ENDORSEMENT TYPE`, o.message as `MESSAGE` \n		from oetable ot join objectendorsement o join oeschema os \n		where o.objectid = ot.oetableid and os.oeschemaid = ot.oeschemaid and o.objecttype = \"OETABLE\"', 'select ot.oetableid as TABLEID, ot.type as TYPE, concat(os.schemaname, \".\", ot.tablename) as `TABLE NAME`, if(o.endorsementtype = \"STAR\", concat(o.endorsementtype, \" - \", o.rating), o.endorsementtype) as `ENDORSEMENT TYPE`, o.message as `MESSAGE`\n		from oetable ot join objectendorsement o join oeschema os\n		where o.objectid = ot.oetableid and os.oeschemaid = ot.oeschemaid and o.objecttype = \"OETABLE\" and o.endorsementtype = ?', 'select endorsementtype, count(endorsementtype) as count from objectendorsement o where o.objecttype = \"OETABLE\" group by endorsementtype', '0', 'Invalid Input', null);
INSERT INTO `stg_oereportext` VALUES ('1026', 'Annotations Summary Report by Type', '-1', 'OvalEdge Reports', ' Annotation Documentation  Summary by User   report gives theinformation about the total no of  Schemas,Tables,columns,file,filecolumn,chart,chartcolumn,Queryand Businessglossary   have been Annonated  by any of the users .', 'select backlink.Type,\ncount(CASE WHEN upper(mainobject)=\"GLOSSARY\" THEN associatedobjectid  END ) AS \"#TermAnnotations\",\ncount(CASE WHEN upper(mainobject)=\"OESCHEMA\" THEN associatedobjectid  END) AS \"#SchemaAnnotations\",\ncount(CASE WHEN upper(mainobject)=\"OETABLE\" THEN associatedobjectid  END) AS \"#TablesAnnotations\",\ncount(CASE WHEN upper(mainobject)=\"OECOLUMN\" THEN associatedobjectid  END) AS \"#ColumnAnnotations\",\ncount(CASE WHEN upper(mainobject)in (\"OEFILE\",\"FILE\") THEN associatedobjectid  END) AS \"#FileAnnotations\",\ncount(CASE WHEN upper(mainobject)=\"OEFILECOLUMN\" THEN associatedobjectid  END) AS \"#FilecolumnAnnotations\",\ncount(CASE WHEN upper(mainobject) in (\"CHART\",\"OECHART\") THEN associatedobjectid  END) AS \"#ReportAnnotations\",\ncount(CASE WHEN upper(mainobject) = (\"CHARTCHILD\") THEN associatedobjectid  END) AS \"#ReportcolumnAnnotations\",\ncount(CASE WHEN upper(mainobject) in (\"DATASET\",\"OEQUERY\") THEN associatedobjectid END) AS \"#QueryAnnotations\",\ncount(CASE WHEN upper(mainobject) = (\"OESTORY\") THEN associatedobjectid END) AS \"#DataStoryAnnotations\",\ncount(CASE WHEN upper(mainobject) = (\"PROJECT\") THEN associatedobjectid END) AS \"#ProjectAnnotations\",\ncount(CASE WHEN upper(mainobject) = (\"GOVERNPOLICY\") THEN associatedobjectid END) AS \"#CertificationPolicyAnnotations\"\nfrom backlink\ngroup by backlink.type\nunion all\nselect \"Comment\" as type,\ncount(CASE WHEN upper(ca.objecttype)=\"GLOSSARY\" THEN ca.objectid  END ) AS \"#TermAnnotations\",\ncount(CASE WHEN upper(ca.objecttype)=\"OESCHEMA\" THEN ca.objectid   END) AS \"#SchemaAnnotations\",\ncount(CASE WHEN upper(ca.objecttype)=\"OETABLE\" THEN ca.objectid  END) AS \"#TablesAnnotations\",\ncount(CASE WHEN upper(ca.objecttype)=\"OECOLUMN\" THEN ca.objectid   END) AS \"#ColumnAnnotations\",\ncount(CASE WHEN upper(ca.objecttype)in (\"OEFILE\",\"FILE\") THEN ca.objectid   END) AS \"#FileAnnotations\",\ncount(CASE WHEN upper(ca.objecttype)=\"OEFILECOLUMN\" THEN ca.objectid   END) AS \"#FilecolumnAnnotations\",\ncount(CASE WHEN upper(ca.objecttype) in (\"CHART\",\"OECHART\") THEN ca.objectid   END) AS \"#ReportAnnotations\",\ncount(CASE WHEN upper(ca.objecttype) = (\"CHARTCHILD\") THEN ca.objectid   END) AS \"#ReportcolumnAnnotations\",\ncount(CASE WHEN upper(ca.objecttype) in (\"DATASET\",\"OEQUERY\") THEN ca.objectid  END) AS \"#QueryAnnotations\",\ncount(CASE WHEN upper(ca.objecttype) = (\"OESTORY\") THEN ca.objectid  END) AS \"#DataStoryAnnotations\",\ncount(CASE WHEN upper(ca.objecttype) = (\"PROJECT\") THEN ca.objectid  END) AS \"#ProjectAnnotations\",\ncount(CASE WHEN upper(ca.objecttype) = (\"GPRULE\") THEN ca.objectid END) AS \"#CertificationPolicyAnnotations\" from\ncomment inner join\n comment ca on comment.commentid=ca.commentid and comment.commenttype =\"COLLABORATION_ALERT\" group by type', 'select * from (select DISTINCT * from (select  Z.type,Z.mainobject,Z.objectname, if(Y.associatedobject IS NULL, \"\",Y.associatedobject) as associatedobject, \nif(Y.ascobjectname IS NULL,\"\",Y.ascobjectname) as ascobjectname from (\nselect backlink.type,backlink.mainobject,s.schemaname as objectname,backlink.mainobjectid,if(backlink.associatedobject IS NULL, \"\", \"SCHEMA\") as associatedobject ,backlink.associatedobjectid from backlink\nleft join oeschema s on (s.oeschemaid=backlink.mainobjectid )\nwhere  upper(backlink.mainobject)=\"OESCHEMA\"\nUNION All\nselect backlink.type,backlink.mainobject,t.tablename as objectname,backlink.mainobjectid,if(backlink.associatedobject IS NULL, \"\", \"TABLE\") as associatedobject ,backlink.associatedobjectid from backlink\nleft join oetable t on (t.oetableid=backlink.mainobjectid)\nwhere upper(backlink.mainobject)=\"OETABLE\"\nUNION All\nselect backlink.type,backlink.mainobject,c.columnname as objectname,backlink.mainobjectid,if(backlink.associatedobject IS NULL, \"\", \"TABLE COLUMN\") as associatedobject ,backlink.associatedobjectid from backlink\nleft join oecolumn c on (c.oecolumnid=backlink.mainobjectid)\nwhere upper(backlink.mainobject)=\"OECOLUMN\"\nUNION All\nselect backlink.type,backlink.mainobject,ch.CHARTNAME as objectname,backlink.mainobjectid,if(backlink.associatedobject IS NULL, \"\", \"REPORT\") as associatedobject,backlink.associatedobjectid from backlink\nleft join chart ch on (ch.CHARTID=backlink.mainobjectid)\nwhere upper(backlink.mainobject) in (\"CHART\",\"OECHART\")\nUNION All\nselect backlink.type,backlink.mainobject,cch.childname as objectname,backlink.mainobjectid,if(backlink.associatedobject IS NULL, \"\", \"REPORT COLUMN\") as associatedobject,backlink.associatedobjectid from backlink\nleft join chartchild cch on (cch.chartchildid=backlink.mainobjectid)\nwhere upper(backlink.mainobject) = (\"CHARTCHILD\")\nUNION All\nselect backlink.type,backlink.mainobject,f.filename as objectname,backlink.mainobjectid,if(backlink.associatedobject IS NULL, \"\", \"FILE\") as associatedobject,backlink.associatedobjectid from backlink\nleft join file f on (f.fileid=backlink.mainobjectid)\nwhere upper(backlink.mainobject) = (\"OEFILE\")\nUNION All\nselect backlink.type,backlink.mainobject,fc.columnname as objectname,backlink.mainobjectid,if(backlink.associatedobject IS NULL, \"\", \"FILE COLUMN\") as associatedobject,backlink.associatedobjectid from backlink\nleft join filecolumn fc on (fc.filecolumnid=backlink.mainobjectid)\nwhere upper(backlink.mainobject) = (\"OEFILECOLUMN\")\nUNION All\nselect backlink.type,backlink.mainobject,d.datasetname as objectname,backlink.mainobjectid,if(backlink.associatedobject IS NULL, \"\", \"QUERY\") as associatedobject,backlink.associatedobjectid from backlink\nleft join dataset d on (d.datasetid=backlink.mainobjectid)\nwhere upper(backlink.mainobject) in (\"DATASET\",\"OEQUERY\")\nUNION All\nselect backlink.type,backlink.mainobject,b.name as objectname,backlink.mainobjectid,if(backlink.associatedobject IS NULL, \"\", \"BUSINESS GLOSSARY\") as associatedobject,backlink.associatedobjectid from backlink\nleft join businessglossary b on (b.businessglossaryid=backlink.mainobjectid)\nwhere upper(backlink.mainobject) = \"GLOSSARY\"\nUNION All\nselect backlink.type,backlink.mainobject,sl.name as objectname,backlink.mainobjectid,if(backlink.associatedobject IS NULL, \"\", \"DATASTORY\") as associatedobject,backlink.associatedobjectid from backlink\nleft join storyline sl on (sl.id=backlink.mainobjectid)\nwhere upper(backlink.mainobject) = \"OESTORY\"\nUNION All\nselect backlink.type,backlink.mainobject,pr.projectname as objectname,backlink.mainobjectid,if(backlink.associatedobject IS NULL, \"\", \"PROJECT\") as associatedobject,backlink.associatedobjectid from backlink\nleft join project pr on (pr.projectid=backlink.mainobjectid)\nwhere upper(backlink.mainobject) = \"PROJECT\"\nUNION All\nselect backlink.type,backlink.mainobject,gpy.governpolicyname as objectname,backlink.mainobjectid,if(backlink.associatedobject IS NULL, \"\", \"CERTIFICATION POLICY\") as associatedobject,backlink.associatedobjectid from backlink\nleft join governpolicy gpy on (gpy.governpolicyid=backlink.mainobjectid)\nwhere upper(backlink.mainobject) = \"GOVERNPOLICY\"\n)Z\nleft join\n(select backlink.type,backlink.mainobject,ascs.schemaname as ascobjectname,backlink.mainobjectid,if(backlink.associatedobject IS NULL, \"\", \"SCHEMA\") as associatedobject,backlink.associatedobjectid from backlink\nleft join oeschema ascs on (ascs.oeschemaid=backlink.associatedobjectid)\nwhere upper(backlink.associatedobject)=\"OESCHEMA\"\nUNION All\nselect backlink.type,backlink.mainobject,asct.tablename as ascobjectname,backlink.mainobjectid,if(backlink.associatedobject IS NULL, \"\", \"TABLE\") as associatedobject,backlink.associatedobjectid from backlink\nleft join oetable asct on (asct.oetableid=backlink.associatedobjectid)\nwhere upper(backlink.associatedobject)=\"OETABLE\"\nUNION All\nselect backlink.type,backlink.mainobject,ascc.columnname as ascobjectname,backlink.mainobjectid,if(backlink.associatedobject IS NULL, \"\", \"TABLE COLUMN\") as associatedobject,backlink.associatedobjectid from backlink\nleft join oecolumn ascc on (ascc.oecolumnid=backlink.associatedobjectid)\nwhere upper(backlink.associatedobject)=\"OECOLUMN\"\nUNION All\nselect backlink.type,backlink.mainobject,ascch.CHARTNAME as ascobjectname,backlink.mainobjectid,if(backlink.associatedobject IS NULL, \"\", \"REPORT\") as associatedobject,backlink.associatedobjectid from backlink\nleft join chart ascch on (ascch.CHARTID=backlink.associatedobjectid)\nwhere upper(backlink.associatedobject) in (\"CHART\",\"OECHART\")\nUNION All\nselect backlink.type,backlink.mainobject,asccch.childname as ascobjectname,backlink.mainobjectid,if(backlink.associatedobject IS NULL, \"\", \"REPORT COLUMN\") as associatedobject,backlink.associatedobjectid from backlink\nleft join chartchild asccch on (asccch.chartchildid=backlink.associatedobjectid)\nwhere upper(backlink.associatedobject) = (\"CHARTCHILD\")\nUNION All\nselect backlink.type,backlink.mainobject,ascf.filename as ascobjectname,backlink.mainobjectid,if(backlink.associatedobject IS NULL, \"\", \"FILE\") as associatedobject,backlink.associatedobjectid from backlink\nleft join file ascf on (ascf.fileid=backlink.associatedobjectid)\nwhere upper(backlink.associatedobject) = (\"OEFILE\")\nUNION All\nselect backlink.type,backlink.mainobject,ascfc.columnname as ascobjectname,backlink.mainobjectid,if(backlink.associatedobject IS NULL, \"\", \"FILE COLUMN\") as associatedobject,backlink.associatedobjectid from backlink\nleft join filecolumn ascfc on (ascfc.filecolumnid=backlink.associatedobjectid)\nwhere upper(backlink.associatedobject) = (\"OEFILECOLUMN\")\nUNION All\nselect backlink.type,backlink.mainobject,ascd.datasetname as ascobjectname,backlink.mainobjectid,if(backlink.associatedobject IS NULL, \"\", \"QUERY\") as associatedobject,backlink.associatedobjectid from backlink\nleft join dataset ascd on (ascd.datasetid=backlink.associatedobjectid)\nwhere upper(backlink.associatedobject) in (\"DATASET\",\"OEQUERY\")\nUNION All\nselect backlink.type,backlink.mainobject,ascb.name as ascobjectname,backlink.mainobjectid,if(backlink.associatedobject IS NULL, \"\", \"BUSINESS GLOSSARY\") as associatedobject,backlink.associatedobjectid from backlink\nleft join businessglossary ascb on (ascb.businessglossaryid=backlink.associatedobjectid)\nwhere upper(backlink.associatedobject) = \"GLOSSARY\"\nUNION All\nselect backlink.type,backlink.mainobject,ascsl.name as ascobjectname,backlink.mainobjectid,if(backlink.associatedobject IS NULL, \"\", \"DATASTORY\") as associatedobject,backlink.associatedobjectid from backlink\nleft join storyline ascsl on (ascsl.id=backlink.associatedobjectid)\nwhere upper(backlink.associatedobject) = \"OESTORY\"\nUNION All \nselect backlink.type,backlink.mainobject,proj.projectname as ascobjectname,backlink.mainobjectid,if(backlink.associatedobject IS NULL, \"\", \"PROJECT\") as associatedobject,backlink.associatedobjectid from backlink\nleft join project proj on (proj.projectid=backlink.associatedobjectid)\nwhere upper(backlink.associatedobject) = \"PROJECT\"\nUNION All \nselect backlink.type,backlink.mainobject,gp.governpolicyname as ascobjectname,backlink.mainobjectid,if(backlink.associatedobject IS NULL, \"\", \"CERTIFICATION POLICY\") as associatedobject,backlink.associatedobjectid from backlink\nleft join governpolicy gp on (gp.governpolicyid=backlink.associatedobjectid)\nwhere upper(backlink.associatedobject) = \"GOVERNPOLICY\"\n)Y\non Y.associatedobjectid=Z.associatedobjectid\nand Y.mainobjectid=Z.mainobjectid)X\nUnion ALL\nselect  A.type,A.mainobject,A.objectname,A.objecttype as associatedobject,A.ascobjectname from\n(select \"Comment\" as type,\"\" as mainobject,\"\" as objectname,comt.schemaname as ascobjectname,\"\",if(comment.objecttype IS NULL, \"\", \"SCHEMA\") as objecttype,comment.objectid from `comment`\ninner join comment ca on ca.commentid=comment.commentid and comment.commenttype =\"COLLABORATION_ALERT\"\nleft join oeschema comt on (comt.oeschemaid=comment.objectid )\nwhere upper(comment.objecttype)=\"OESCHEMA\"\nUNION All\nselect \"Comment\" as type,\"\" as mainobject,\"\" as objectname,comt.tablename as ascobjectname,\"\",if(comment.objecttype IS NULL, \"\", \"TABLE\") as objecttype,comment.objectid from `comment`\ninner join comment ca on ca.commentid=comment.commentid and comment.commenttype =\"COLLABORATION_ALERT\"\nleft join oetable comt on (comt.oetableid=comment.objectid )\nwhere upper(comment.objecttype)=\"OETABLE\"\nUNION All\nselect \"Comment\" as type,\"\" as mainobject,\"\" as objectname,comc.columnname as ascobjectname,\"\",if(comment.objecttype IS NULL, \"\", \"TABLE COLUMN\") as objecttype,comment.objectid from `comment`\ninner join comment ca on ca.commentid=comment.commentid and comment.commenttype =\"COLLABORATION_ALERT\"\nleft join oecolumn comc on (comc.oecolumnid=comment.objectid )\nwhere upper(comment.objecttype)=\"OECOLUMN\"\nUNION All\nselect \"Comment\" as type,\"\" as mainobject,\"\" as objectname,comch.CHARTNAME as ascobjectname,\"\",if(comment.objecttype IS NULL, \"\", \"REPORT\") as objecttype,comment.objectid from `comment`\ninner join comment ca on ca.commentid=comment.commentid and comment.commenttype =\"COLLABORATION_ALERT\"\nleft join chart comch on (comch.CHARTID=comment.objectid )\nwhere upper(comment.objecttype)in (\"CHART\",\"OECHART\")\nUNION All\nselect \"Comment\" as type,\"\" as mainobject,\"\" as objectname,comcch.CHARTNAME as ascobjectname,\"\",if(comment.objecttype IS NULL, \"\", \"REPORT COLUMN\") as objecttype,comment.objectid from `comment`\ninner join comment ca on ca.commentid=comment.commentid and comment.commenttype =\"COLLABORATION_ALERT\"\nleft join chart comcch on (comcch.CHARTID=comment.objectid )\nwhere upper(comment.objecttype)in (\"CHARTCHILD\")\nUNION All\nselect \"Comment\" as type,\"\" as mainobject,\"\" as objectname,comf.filename as ascobjectname,\"\",if(comment.objecttype IS NULL, \"\", \"FILE\") as objecttype,comment.objectid from `comment`\ninner join comment ca on ca.commentid=comment.commentid and comment.commenttype =\"COLLABORATION_ALERT\"\nleft join file comf on (comf.fileid=comment.objectid )\nwhere upper(comment.objecttype) = (\"OEFILE\")\nUNION All\nselect \"Comment\" as type,\"\" as mainobject,\"\" as objectname,comfc.columnname as ascobjectname,\"\",if(comment.objecttype IS NULL, \"\", \"FILE COLUMN\") as objecttype,comment.objectid from `comment`\ninner join comment ca on ca.commentid=comment.commentid and comment.commenttype =\"COLLABORATION_ALERT\"\nleft join filecolumn comfc on (comfc.filecolumnid=comment.objectid )\nwhere upper(comment.objecttype) = (\"OEFILECOLUMN\")\nUNION All\nselect \"Comment\" as type,\"\" as mainobject,\"\" as objectname,comd.datasetname as ascobjectname,\"\",if(comment.objecttype IS NULL, \"\", \"QUERY\") as objecttype,comment.objectid from `comment`\ninner join comment ca on ca.commentid=comment.commentid and comment.commenttype =\"COLLABORATION_ALERT\"\nleft join dataset comd on (comd.datasetid=comment.objectid )\nwhere upper(comment.objecttype) in (\"DATASET\",\"OEQUERY\")\nUNION All\nselect \"Comment\" as type,\"\" as mainobject,\"\" as objectname,combg.name as ascobjectname,\"\",if(comment.objecttype IS NULL, \"\", \"BUSINESS GLOSSARY\") as objecttype,comment.objectid from `comment`\ninner join comment ca on ca.commentid=comment.commentid and comment.commenttype =\"COLLABORATION_ALERT\"\nleft join businessglossary combg on (combg.businessglossaryid=comment.objectid )\nwhere upper(comment.objecttype) = \"GLOSSARY\"\nUNION All\nselect \"Comment\" as type,\"\" as mainobject,\"\" as objectname,comsl.name as ascobjectname,\"\",if(comment.objecttype IS NULL, \"\", \"DATA STORY\") as objecttype,comment.objectid from `comment`\ninner join comment ca on ca.commentid=comment.commentid and comment.commenttype =\"COLLABORATION_ALERT\"\nleft join storyline comsl on (comsl.id=comment.objectid )\nwhere upper(comment.objecttype) = \"OESTORY\"\nUNION All\nselect \"Comment\" as type,\"\" as mainobject,\"\" as objectname,pj.projectname as ascobjectname,\"\",if(comment.objecttype IS NULL, \"\", \"PROJECT\") as objecttype,comment.objectid from `comment`\ninner join comment ca on ca.commentid=comment.commentid and comment.commenttype =\"COLLABORATION_ALERT\"\nleft join project pj on (pj.projectid=comment.objectid )\nwhere upper(comment.objecttype) = \"PROJECT\"\nUNION All\nselect \"Comment\" as type,\"\" as mainobject,\"\" as objectname,gpo.governpolicyname as ascobjectname,\"\",if(comment.objecttype IS NULL, \"\", \"CERTIFICATION POLICY\") as objecttype,comment.objectid from `comment`\ninner join comment ca on ca.commentid=comment.commentid and comment.commenttype =\"COLLABORATION_ALERT\"\nleft join governpolicy gpo on (gpo.governpolicyid=comment.objectid )\nwhere upper(comment.objecttype) = \"GPRULE\")A)B\nwhere B.type= ?', 'select Type,sum(TermAnnotations+SchemaAnnotations+TablesAnnotations+ColumnAnnotations+FileAnnotations+FilecolumnAnnotations+ReportAnnotations\n+ReportcolumnAnnotations+QueryAnnotations+DataStoryAnnotations+ProjectAnnotations+CertificationPolicyAnnotations)as Total from\n(select backlink.type,count(CASE WHEN upper(mainobject)=\"GLOSSARY\" THEN associatedobjectid  END ) AS \"TermAnnotations\",\ncount(CASE WHEN upper(mainobject)=\"OESCHEMA\" THEN associatedobjectid  END) AS \"SchemaAnnotations\",\ncount(CASE WHEN upper(mainobject)=\"OETABLE\" THEN associatedobjectid  END) AS \"TablesAnnotations\",\ncount(CASE WHEN upper(mainobject)=\"OECOLUMN\" THEN associatedobjectid  END) AS \"ColumnAnnotations\",\ncount(CASE WHEN upper(mainobject)in (\"OEFILE\",\"FILE\") THEN associatedobjectid  END) AS \"FileAnnotations\",\ncount(CASE WHEN upper(mainobject)=\"OEFILECOLUMN\" THEN associatedobjectid  END) AS \"FilecolumnAnnotations\",\ncount(CASE WHEN upper(mainobject) in (\"CHART\",\"OECHART\") THEN associatedobjectid  END) AS \"ReportAnnotations\",\ncount(CASE WHEN upper(mainobject) = (\"CHARTCHILD\") THEN associatedobjectid  END) AS \"ReportcolumnAnnotations\",\ncount(CASE WHEN upper(mainobject) in (\"DATASET\",\"OEQUERY\") THEN associatedobjectid END) AS \"QueryAnnotations\",\ncount(CASE WHEN upper(mainobject) = (\"OESTORY\") THEN associatedobjectid END) AS \"DataStoryAnnotations\",\ncount(CASE WHEN upper(mainobject) = (\"PROJECT\") THEN associatedobjectid END) AS \"ProjectAnnotations\",\ncount(CASE WHEN upper(mainobject) = (\"GOVERNPOLICY\") THEN associatedobjectid END) AS \"CertificationPolicyAnnotations\"\nfrom backlink\ngroup by backlink.type\nunion all\nselect \"Comment\" as type,\ncount(CASE WHEN upper(ca.objecttype)=\"GLOSSARY\" THEN ca.objectid  END ) AS \"#TermAnnotations\",\ncount(CASE WHEN upper(ca.objecttype)=\"OESCHEMA\" THEN ca.objectid   END) AS \"#SchemaAnnotations\",\ncount(CASE WHEN upper(ca.objecttype)=\"OETABLE\" THEN ca.objectid  END) AS \"#TablesAnnotations\",\ncount(CASE WHEN upper(ca.objecttype)=\"OECOLUMN\" THEN ca.objectid   END) AS \"#ColumnAnnotations\",\ncount(CASE WHEN upper(ca.objecttype)in (\"OEFILE\",\"FILE\") THEN ca.objectid   END) AS \"#FileAnnotations\",\ncount(CASE WHEN upper(ca.objecttype)=\"OEFILECOLUMN\" THEN ca.objectid   END) AS \"#FilecolumnAnnotations\",\ncount(CASE WHEN upper(ca.objecttype) in (\"CHART\",\"OECHART\") THEN ca.objectid   END) AS \"#ReportAnnotations\",\ncount(CASE WHEN upper(ca.objecttype) = (\"CHARTCHILD\") THEN ca.objectid   END) AS \"#ReportcolumnAnnotations\",\ncount(CASE WHEN upper(ca.objecttype) in (\"DATASET\",\"OEQUERY\") THEN ca.objectid  END) AS \"#QueryAnnotations\",\ncount(CASE WHEN upper(ca.objecttype) = (\"OESTORY\") THEN ca.objectid  END) AS \"#DataStoryAnnotations\",\ncount(CASE WHEN upper(ca.objecttype) = (\"PROJECT\") THEN ca.objectid END) AS \"#ProjectAnnotations\",\ncount(CASE WHEN upper(ca.objecttype) = (\"GPRULE\") THEN ca.objectid END) AS \"#CertificationPolicyAnnotations\" from\ncomment inner join\n comment ca on comment.commentid=ca.commentid and comment.commenttype =\"COLLABORATION_ALERT\" group by type)as A group by type', '0', 'Invalid input', null);
INSERT INTO `stg_oereportext` VALUES ('1027', ' Documented and Undocumented Tables - Technical Description', '-1', 'OvalEdge Reports', 'This report shows the total count of tables which are having Technical descriptions. All the tables which are having technical descriptions are considered as Documented Tables and tables which are not having technical descriptions are Undocumented Tables.', 'select Status,servertype,schemaname,tablename,TDDocumented as \"TechnicalDescription\" from (select \"Documented\" as Status,ci.servertype,s.schemaname,t.tablename,wikiobjectid,sum(case when techtext <> \" \"  then  1  else 0 end ) \"TDDocumented\"\n from wiki join oetable t on (t.oetableid=wiki.wikiobjectid )\njoin oeschema s on (t.oeschemaid = s.oeschemaid) \njoin connectioninfo ci on (s.connectioninfoid = ci.connectioninfoid)\nwhere wikiobject=\"oetable\"  and  t.active=1\ngroup by  ci.servertype,s.schemaname,t.tablename,wiki.wikiobjectid\nunion all\nselect \"UnDocumented\" as Status,ci.servertype,s.schemaname,t.tablename,oetableid,0\n from oetable t join oeschema s on (t.oeschemaid = s.oeschemaid) \njoin connectioninfo ci on (s.connectioninfoid = ci.connectioninfoid)\nwhere oetableid not in (select wikiobjectid from wiki where wikiobject=\"oetable\") and t.active=1\n) A', 'select Status,servertype,schemaname,tablename,coalesce(TDDocumented,\" \") as \"TechnicalDescription\"  from(select \"Documented\" as Status,ci.servertype,s.schemaname,t.tablename,wikiobjectid,MAX(case when techtext  <> \" \"  then  techtext  end ) \"TDDocumented\"\n from wiki join oetable t on (t.oetableid=wiki.wikiobjectid)\njoin oeschema s on (t.oeschemaid = s.oeschemaid) \njoin connectioninfo ci on (s.connectioninfoid = ci.connectioninfoid)\nwhere wikiobject=\"oetable\"  and  t.active=1\ngroup by  ci.servertype,s.schemaname,t.tablename,wiki.wikiobjectid\nunion all\nselect \"UnDocumented\" as Status,ci.servertype,s.schemaname,t.tablename,oetableid,\"\"\n from oetable t join oeschema s on (t.oeschemaid = s.oeschemaid) \njoin connectioninfo ci on (s.connectioninfoid = ci.connectioninfoid)\nwhere t.oetableid not in (select wikiobjectid from wiki where wikiobject=\"oetable\") and  t.active=1\n) A\nwhere A.Status=?', 'select Status,DocumentedObject from (select \"Documented\" as Status,count(distinct wikiobjectid) as \"DocumentedObject\" from wiki \nwhere wikiobject=\"oetable\"  and techtext  <> \" \"  and techtext is not null and wikiobjectid in \n(select t.oetableid\nfrom  oetable t  \njoin oeschema s on (t.oeschemaid = s.oeschemaid) \njoin connectioninfo ci on (s.connectioninfoid = ci.connectioninfoid) where   t.active=1)\nunion all\nselect \"UnDocumented\" as Status,count(TotalTables) as \"UnDocumentObject\"\nfrom  (select t.oetableid as \"TotalTables\"\nfrom oetable t join oeschema s on (t.oeschemaid = s.oeschemaid) \njoin connectioninfo ci on (s.connectioninfoid = ci.connectioninfoid) where  t.active=1\n) A\nwhere A.TotalTables not in \n(select  wikiobjectid \"DocumentedTables\" from wiki \nwhere wikiobject=\"oetable\"   and  wikiobjectid in \n(select t.oetableid\nfrom oetable t join oeschema s on (t.oeschemaid = s.oeschemaid) \njoin connectioninfo ci on (s.connectioninfoid = ci.connectioninfoid) where t.active=1))\norder by DocumentedObject DESC) tmp', '0', 'Invalid input', 'donut');
INSERT INTO `stg_oereportext` VALUES ('1028', 'Documented and Undocumented Tables - Business Description', '-1', 'OvalEdge Reports', 'This report shows the total count of tables which are having Business descriptions. All the tables which are having business descriptions are considered as Documented Tables and tables which are not having business descriptions are Undocumented Tables.', 'select Status,servertype,schemaname,tablename,BDDocumented as \"BusinessDescription\" from (select \"Documented\" as Status,ci.servertype,s.schemaname,t.tablename,wikiobjectid,sum(case when wikitext <> \" \"  then  1  else 0 end ) \"BDDocumented\"\n from wiki join oetable t on (t.oetableid=wiki.wikiobjectid)\njoin oeschema s on (t.oeschemaid = s.oeschemaid) \njoin connectioninfo ci on (s.connectioninfoid = ci.connectioninfoid)\nwhere wikiobject=\"oetable\" and t.active=1\ngroup by  ci.servertype,s.schemaname,t.tablename,wiki.wikiobjectid\nunion all\nselect \"UnDocumented\" as Status,ci.servertype,s.schemaname,t.tablename,oetableid,0\n from oetable t join oeschema s on (t.oeschemaid = s.oeschemaid) \njoin connectioninfo ci on (s.connectioninfoid = ci.connectioninfoid)\nwhere oetableid not in (select wikiobjectid from wiki where wikiobject=\"oetable\") and t.active=1\n) A', 'select Status,servertype,schemaname,tablename,coalesce(BDDocumented,\" \") as \"BusinessDescription\"  from(select \"Documented\" as Status,ci.servertype,s.schemaname,t.tablename,wikiobjectid,MAX(case when wikitext  <> \" \"  then  wikitext  end ) \"BDDocumented\"\n from wiki join oetable t on (t.oetableid=wiki.wikiobjectid)\njoin oeschema s on (t.oeschemaid = s.oeschemaid) \njoin connectioninfo ci on (s.connectioninfoid = ci.connectioninfoid)\nwhere wikiobject=\"oetable\"  and wikitext is not null and wikitext<>\"\" and t.active=1\ngroup by  ci.servertype,s.schemaname,t.tablename,wiki.wikiobjectid\nunion all\nselect \"UnDocumented\" as Status,ci.servertype,s.schemaname,t.tablename,oetableid,\"\"\n from oetable t join oeschema s on (t.oeschemaid = s.oeschemaid) \njoin connectioninfo ci on (s.connectioninfoid = ci.connectioninfoid)\nwhere t.oetableid not in (select wikiobjectid from wiki where wikiobject=\"oetable\" and wikitext is not null ) and t.active=1\n) A\nwhere A.Status=?', 'select Status,DocumentedObject from (select \"Documented\" as Status,count(distinct wikiobjectid) as \"DocumentedObject\" from wiki \nwhere wikiobject=\"oetable\"  and wikitext  <> \" \"  and wikiobjectid in \n(select t.oetableid\nfrom  oetable t  \njoin oeschema s on (t.oeschemaid = s.oeschemaid) \njoin connectioninfo ci on (s.connectioninfoid = ci.connectioninfoid)where t.active=1)\nunion all\nselect \"UnDocumented\" as Status,count(TotalTables) as \"UnDocumentObject\"\nfrom  (select t.oetableid as \"TotalTables\"\nfrom oetable t join oeschema s on (t.oeschemaid = s.oeschemaid) \njoin connectioninfo ci on (s.connectioninfoid = ci.connectioninfoid) where t.active=1\n) A\nwhere A.TotalTables not in \n(select  wikiobjectid \"DocumentedColumns\" from wiki \nwhere wikiobject=\"oetable\"  and wikitext is not null and wikitext <>\"\"  and  wikiobjectid in \n(select t.oetableid\nfrom oetable t join oeschema s on (t.oeschemaid = s.oeschemaid) \njoin connectioninfo ci on (s.connectioninfoid = ci.connectioninfoid)where t.active=1))\norder by DocumentedObject DESC) tmp', '0', 'Invalid input', 'donut');
INSERT INTO `stg_oereportext` VALUES ('1029', 'Documented and Undocumented Columns - Technical Description', '-1', 'OvalEdge Reports', 'This report shows the total count of table columns which are having Technical descriptions. All the table columns which are having technical descriptions are considered as Documented Columns and table columns which are not having technical descriptions are Undocumented Columns.', 'select Status,servertype,schemaname,tablename,columnname\nfrom (select \"Documented\" as Status,ci.servertype,s.schemaname,t.tablename,wikiobjectid,oecolumn.columnname\n from wiki join oecolumn on (oecolumn.oecolumnid=wiki.wikiobjectid)\njoin oetable t on (oecolumn.oetableid = t.oetableid ) \njoin oeschema s on (t.oeschemaid = s.oeschemaid) \njoin connectioninfo ci on (s.connectioninfoid = ci.connectioninfoid)\nwhere wikiobject=\"oecolumn\" and techtext <>\" \" and techtext is not null and  oecolumn.active=1\nunion all\nselect \"UnDocumented\" as Status,ci.servertype,s.schemaname,t.tablename,oecolumnid,oecolumn.columnname\n from oecolumn join oetable t on (oecolumn.oetableid = t.oetableid ) \njoin oeschema s on (t.oeschemaid = s.oeschemaid) \njoin connectioninfo ci on (s.connectioninfoid = ci.connectioninfoid)\nwhere oecolumnid not in (select wikiobjectid from wiki where wikiobject=\"oecolumn\" and techtext <>\" \" and techtext is not null) and  oecolumn.active=1\n) A', 'select Status,servertype,schemaname,tablename,columnname,coalesce(TDDocumented,\" \") as \"TechnicalDescription\" \nfrom(select \"Documented\" as Status,ci.servertype,s.schemaname,t.tablename,wikiobjectid,oecolumn.columnname,Techtext \"TDDocumented\"\n from wiki join oecolumn on (oecolumn.oecolumnid=wiki.wikiobjectid)\njoin oetable t on (oecolumn.oetableid = t.oetableid ) \njoin oeschema s on (t.oeschemaid = s.oeschemaid) \njoin connectioninfo ci on (s.connectioninfoid = ci.connectioninfoid)\nwhere wikiobject=\"oecolumn\" and techtext <>\" \" and techtext is not null and  oecolumn.active=1\nunion all\nselect \"UnDocumented\" as Status,ci.servertype,s.schemaname,t.tablename,oecolumnid,oecolumn.columnname,\"\"\n from oecolumn join oetable t on (oecolumn.oetableid = t.oetableid ) \njoin oeschema s on (t.oeschemaid = s.oeschemaid) \njoin connectioninfo ci on (s.connectioninfoid = ci.connectioninfoid)\nwhere oecolumnid not in (select wikiobjectid from wiki where wikiobject=\"oecolumn\" and techtext <>\" \" and techtext is not null) and  oecolumn.active=1\n) A\nwhere A.Status=?', 'select \"Documented\" as Status,count(distinct wikiobjectid) as \"DocumentedObject\" from wiki \nwhere wikiobject=\"oecolumn\" and techtext <>\" \" and techtext is not null  and wikiobjectid in \n(select c.oecolumnid\nfrom oecolumn c \njoin oetable t on (c.oetableid = t.oetableid ) \njoin oeschema s on (t.oeschemaid = s.oeschemaid) \njoin connectioninfo ci on (s.connectioninfoid = ci.connectioninfoid)where c.active=1)\nunion all\nselect \"UnDocumented\" as Status,count(TotalColumns) as \"UnDocumentObject\"\nfrom  (select c.oecolumnid as \"TotalColumns\"\nfrom oecolumn c \njoin oetable t on (c.oetableid = t.oetableid ) \njoin oeschema s on (t.oeschemaid = s.oeschemaid) \njoin connectioninfo ci on (s.connectioninfoid = ci.connectioninfoid)where c.active=1\n) A\nwhere A.TotalColumns not in \n(select  wikiobjectid \"DocumentedColumns\" from wiki \nwhere wikiobject=\"oecolumn\"  and techtext is not null and techtext <>\" \"  and wikiobjectid in \n(select c.oecolumnid\nfrom oecolumn c \njoin oetable t on (c.oetableid = t.oetableid ) \njoin oeschema s on (t.oeschemaid = s.oeschemaid) \njoin connectioninfo ci on (s.connectioninfoid = ci.connectioninfoid)where c.active=1))\norder by DocumentedObject DESC', '0', 'Invalid input', 'donut');
INSERT INTO `stg_oereportext` VALUES ('1030', 'Documented and Undocumented Columns - Business Description', '-1', 'OvalEdge Reports', 'This report shows the total count of table columns which are having Business descriptions. All the table columns which are having business descriptions are considered as Documented Columns and table columns which are not having business descriptions are Undocumented Columns.', 'select Status,servertype,schemaname,tablename,columnname\nfrom (select \"Documented\" as Status,ci.servertype,s.schemaname,t.tablename,wikiobjectid,oecolumn.columnname\n from wiki join oecolumn on (oecolumn.oecolumnid=wiki.wikiobjectid)\njoin oetable t on (oecolumn.oetableid = t.oetableid ) \njoin oeschema s on (t.oeschemaid = s.oeschemaid) \njoin connectioninfo ci on (s.connectioninfoid = ci.connectioninfoid)\nwhere wikiobject=\"oecolumn\" and wikitext <>\" \" and oecolumn.active=1\nunion all\nselect \"UnDocumented\" as Status,ci.servertype,s.schemaname,t.tablename,oecolumnid,oecolumn.columnname\n from oecolumn join oetable t on (oecolumn.oetableid = t.oetableid ) \njoin oeschema s on (t.oeschemaid = s.oeschemaid) \njoin connectioninfo ci on (s.connectioninfoid = ci.connectioninfoid)\nwhere oecolumnid not in (select wikiobjectid from wiki where wikiobject=\"oecolumn\" and wikitext <>\" \") and oecolumn.active=1\n) A', 'select Status,servertype,schemaname,tablename,columnname,coalesce(BDDocumented,\" \") as \"BusinessDescription\" \nfrom(select \"Documented\" as Status,ci.servertype,s.schemaname,t.tablename,wikiobjectid,oecolumn.columnname,wikitext \"BDDocumented\"\n from wiki join oecolumn on (oecolumn.oecolumnid=wiki.wikiobjectid)\njoin oetable t on (oecolumn.oetableid = t.oetableid ) \njoin oeschema s on (t.oeschemaid = s.oeschemaid) \njoin connectioninfo ci on (s.connectioninfoid = ci.connectioninfoid)\nwhere wikiobject=\"oecolumn\" and wikitext <>\" \" and oecolumn.active=1\n\nunion all\nselect \"UnDocumented\" as Status,ci.servertype,s.schemaname,t.tablename,oecolumnid,oecolumn.columnname,\"\"\n from oecolumn join oetable t on (oecolumn.oetableid = t.oetableid ) \njoin oeschema s on (t.oeschemaid = s.oeschemaid) \njoin connectioninfo ci on (s.connectioninfoid = ci.connectioninfoid)\nwhere oecolumnid not in (select wikiobjectid from wiki where wikiobject=\"oecolumn\" and wikitext <>\" \" ) and oecolumn.active=1\n) A\nwhere A.Status=?', 'select \"Documented\" as Status,count(distinct wikiobjectid) as \"DocumentedObject\" from wiki \nwhere wikiobject=\"oecolumn\" and wikitext <>\" \"   and wikiobjectid in \n(select c.oecolumnid\nfrom oecolumn c \njoin oetable t on (c.oetableid = t.oetableid ) \njoin oeschema s on (t.oeschemaid = s.oeschemaid) \njoin connectioninfo ci on (s.connectioninfoid = ci.connectioninfoid) where c.active=1) \nunion all\nselect \"UnDocumented\" as Status,count(TotalColumns) as \"UnDocumentObject\"\nfrom  (select c.oecolumnid as \"TotalColumns\"\nfrom oecolumn c \njoin oetable t on (c.oetableid = t.oetableid ) \njoin oeschema s on (t.oeschemaid = s.oeschemaid) \njoin connectioninfo ci on (s.connectioninfoid = ci.connectioninfoid) where c.active=1\n) A\nwhere A.TotalColumns not in \n(select  wikiobjectid \"DocumentedColumns\" from wiki \nwhere wikiobject=\"oecolumn\"  and wikitext is not null  and wikiobjectid in \n(select c.oecolumnid\nfrom oecolumn c \njoin oetable t on (c.oetableid = t.oetableid ) \njoin oeschema s on (t.oeschemaid = s.oeschemaid) \njoin connectioninfo ci on (s.connectioninfoid = ci.connectioninfoid) where c.active=1))\norder by DocumentedObject DESC', '0', 'Invalid input', 'donut');
INSERT INTO `stg_oereportext` VALUES ('1031', 'Tables with Zero Row Count', '-1', 'OvalEdge Reports', 'This report shows the list of all tables which are having zero row counts.', 'select ci.servertype as Servertype,ci.name as Connectionname,schemaname as Schemaname,count(oetableid) \"Tables with Zero Records\"  from oetable \ninner join oeschema on (oeschema.oeschemaid=oetable.oeschemaid)\njoin connectioninfo ci on (ci.connectioninfoid=oeschema.connectioninfoid)\nwhere rowcount=0 and analyzedstatus=\"Profiled\" and oetable.active=1\ngroup by ci.servertype,Connectionname,Schemaname\norder by count(oetableid) desc', 'select ci.servertype as Servertype,ci.name as Connectionname,schemaname as Schemaname,oetable.tablename  from oetable \ninner join oeschema on (oeschema.oeschemaid=oetable.oeschemaid)\njoin connectioninfo ci on (ci.connectioninfoid=oeschema.connectioninfoid)\nwhere rowcount=0 and analyzedstatus=\"Profiled\" and oetable.active=1\nand ci.name=?', 'select ci.name ObjectName,count(oetableid) \"Tables with Zero Records\"  from oetable \ninner join oeschema on (oeschema.oeschemaid=oetable.oeschemaid)\njoin connectioninfo ci on (ci.connectioninfoid=oeschema.connectioninfoid)\nwhere rowcount=0 and analyzedstatus=\"Profiled\" and oetable.active=1\ngroup by ci.name\norder by count(oetableid) desc', '0', 'Invalid input', null);
INSERT INTO `stg_oereportext` VALUES ('1032', 'Tables with no Relationship', '-1', 'OvalEdge Reports', 'This report shows the list of all tables which are not having any relationship with other tables.', 'select A.servertype,A.Connectionname,A.Schemaname,sum(A.TablesWithNoRelation) as \"TablesWithNoRelation\" from (\nselect connectioninfo.servertype as Servertype,connectioninfo.name as Connectionname,schemaname as Schemaname,\ncount(distinct oetable.oetableid)-count(distinct case when lefttableid =oetableid or righttableid=  oetableid  then oetable.oetableid end ) as  \"TablesWithNoRelation\"\nfrom oetable left join oeschema on (oeschema.oeschemaid=oetable.oeschemaid)\nleft join connectioninfo on (oeschema.connectioninfoid=connectioninfo.connectioninfoid)\nleft join tablerelation tr on (oetable.oetableid=tr.lefttableid or oetable.oetableid=righttableid)\ngroup by connectioninfo.servertype ,connectioninfo.name,schemaname\n) A group by A.servertype,A.Connectionname, A.Schemaname order by TablesWithNoRelation desc', 'select connectioninfo.servertype as Servertype,connectioninfo.name as Connectionname,schemaname as Schemaname,tablename as Tablename\nfrom oetable left join oeschema on (oeschema.oeschemaid=oetable.oeschemaid)\nleft join connectioninfo on (oeschema.connectioninfoid=connectioninfo.connectioninfoid)\nleft join tablerelation tr on (oetable.oetableid=tr.lefttableid or oetable.oetableid=righttableid)\nwhere (case when lefttableid =oetableid or righttableid=  oetableid then \"Yes\" else \"No\" end )=\"No\"\nand  connectioninfo.name=?', 'select A.Conntype as \"DatabaseName\",sum(A.TablesWithNoRelation) as \"TablesWithNoRelation\" from (\nselect connectioninfo.name as Conntype,\ncount(distinct oetable.oetableid)-count(distinct case when lefttableid =oetableid or righttableid=  oetableid  then oetable.oetableid end ) as  \"TablesWithNoRelation\"\nfrom oetable left join oeschema on (oeschema.oeschemaid=oetable.oeschemaid)\nleft join connectioninfo on (oeschema.connectioninfoid=connectioninfo.connectioninfoid)\nleft join tablerelation tr on (oetable.oetableid=tr.lefttableid or oetable.oetableid=righttableid)\ngroup by connectioninfo.name\n) A group by A.Conntype order by TablesWithNoRelation desc', '0', 'Invalid input', null);
INSERT INTO `stg_oereportext` VALUES ('1033', 'RACI - Membership per user - Custom', '-1', 'OvalEdge Reports', 'This report depicts user membership in the RACI panel for business glossary terms.', 'select user.userid as Username,count(distinct object_id ) \"#Terms\",count(object_id ) \"#RACI Term\"\nfrom user join govern_matrix gm on (gm.assignee=user.USERID)  \njoin govern_matrix_model_role gmr on (gm.role_id=gmr.id)\nwhere object_type=\"glossary\"\ngroup by  user.userid  order by count(distinct object_id ) desc ', 'select tmp.userid as username,tmp.rolename as Membership,tmp.Term from (\nselect user.userid,concat(gd.domain,\".\",bg.name ) as Term,rolename as rolename \nfrom user join govern_matrix gm on (gm.assignee=user.USERID)\njoin govern_matrix_model_role gmr on (gmr.id=gm.role_id)\njoin businessglossary bg on (bg.businessglossaryid=gm.object_id and object_type=\"glossary\")\njoin globaldomain  gd on (gd.globaldomainid=bg.globaldomainid)) tmp\nwhere tmp.userid=?', 'select user.userid as Username,count(distinct object_id ) \"RACI\"\nfrom user join govern_matrix gm on (gm.assignee=user.USERID)  \njoin govern_matrix_model_role gmr on (gm.role_id=gmr.id)\nwhere object_type=\"glossary\"\ngroup by  user.userid  order by count(distinct object_id ) desc', '0', 'Invalid input', null);
INSERT INTO `stg_oereportext` VALUES ('1034', 'Terms without RACI - Custom', '-1', 'OvalEdge Reports', 'This report shows the business glossary terms that are missing user membership in each of the RACI panel.', 'select gd.domain,count(bg.name) \"No of Terms WithOut RACI\" from (select object_id,group_concat(distinct role_id)  role_id from govern_matrix   where object_type=\"glossary\"\ngroup by object_id) tmp  join businessglossary bg on (bg.businessglossaryid=tmp.object_id)\njoin globaldomain gd on (gd.globaldomainid=bg.globaldomainid)\nwhere role_id not like \"1,2,3,4%\"\ngroup by gd.domain   order by count(bg.name) desc', 'select tmp.domain,Terms as Term,missing as \"Missing RACI\",status,description,steward,approvedby  from (\nselect gd.domain,bg.name Terms,bg.status,bg.description,bg.steward,bg.approvedby,missing from (select s.object_id,\ngroup_concat(distinct case when m.role_id is not null then s.rolename end) \"rolename\",\n         group_concat(distinct case when m.role_id is null then s.rolename end) \"missing\"\nfrom\n(select distinct  gm.object_id,gmr.id,rolename from govern_matrix  gm  cross join govern_matrix_model_role  gmr\nwhere object_type=\"glossary\"\n) s\nleft join govern_matrix m on m.object_id=s.object_id  and m.role_id=s.id\ngroup by s.object_id) tmp  join businessglossary bg on (bg.businessglossaryid=tmp.object_id)\njoin globaldomain gd on (gd.globaldomainid=bg.globaldomainid)\n) tmp\nwhere  missing is not null and  tmp.domain=?', 'select gd.domain,count(bg.name) \"No of Terms WithOut RACI\" from (select object_id,group_concat(distinct role_id)  role_id from govern_matrix   where object_type=\"glossary\"\ngroup by object_id) tmp  join businessglossary bg on (bg.businessglossaryid=tmp.object_id)\njoin globaldomain gd on (gd.globaldomainid=bg.globaldomainid)\nwhere role_id not like \"1,2,3,4%\"\ngroup by gd.domain   order by count(bg.name) desc', '0', 'Invalid input', null);
INSERT INTO `stg_oereportext` VALUES ('1035', 'Top 20 Terms - Custom', '-1', 'OvalEdge Reports', 'This report displays the top 20 terms used by all user groups. On mouse hover, an aggregate count of all views are displayed. On click of a particular term graph, a detailed view of how many users have viewed along with the no.of views for each user.', 'select bg.name as termname,\nsum(numberlooked) as totalviewed from objectuserstats O\njoin businessglossary  bg on O.objectid = bg.businessglossaryid\nwhere O.objecttype=\"glossary\"\ngroup  by bg.name\norder by totalviewed desc limit 20', 'select * from (select O.userid,bg.name as termname,\nsum(numberlooked) as totalviewed from objectuserstats O\njoin businessglossary  bg on O.objectid = bg.businessglossaryid\nwhere O.objecttype=\"glossary\"\ngroup by O.userid,bg.name\norder by totalviewed desc) tmp\nwhere tmp.termname=? order by totalviewed desc', 'select bg.name as termname,\nsum(numberlooked) as totalviewed from objectuserstats O\njoin businessglossary  bg on O.objectid = bg.businessglossaryid\nwhere O.objecttype=\"glossary\"\ngroup  by bg.name\norder by totalviewed desc limit 20', '0', 'Invalid input', null);
INSERT INTO `stg_oereportext` VALUES ('1036', 'Terms Without Associations - Custom', '-1', 'OvalEdge Reports', 'The report details the terms which are not associated with any object, fields and queries, per Domain. Filters show the terms belonging to Domain. ', 'select gd.domain,count(bg.name) as \"No of Terms WithOut Data Association\"\nfrom businessglossary bg join globaldomain gd ON (gd.globaldomainid = bg.globaldomainid)\nwhere businessglossaryid not in (select  bg.businessglossaryid  from oetable ot\njoin oeschema os on os.oeschemaid = ot.oeschemaid\njoin connectioninfo con on os.connectioninfoid = con.connectioninfoid\njoin businessglossary bg on ot.businessglossaryid=bg.businessglossaryid\njoin globaldomain gd on (gd.globaldomainid = bg.globaldomainid)\nunion all\nselect bg.businessglossaryid  from  oecolumn oc\njoin oetable ot  on (oc.oetableid=ot.oetableid)\njoin oeschema os on os.oeschemaid = ot.oeschemaid\njoin connectioninfo con on os.connectioninfoid = con.connectioninfoid\njoin businessglossary bg on oc.businessglossaryid=bg.businessglossaryid\njoin globaldomain gd ON (gd.globaldomainid = bg.globaldomainid)\nunion all\nselect bg.businessglossaryid\nfrom oeschema os\njoin connectioninfo ci on os.connectioninfoid = ci.connectioninfoid  \njoin businessglossary bg on os.businessglossaryid=bg.businessglossaryid\njoin globaldomain gd on gd.globaldomainid=bg.globaldomainid\nunion all\nselect bg.businessglossaryid  \nfrom file f\njoin filefolder ff on f.filefolderid = ff.filefolderid\njoin connectioninfo ci on ff.connectioninfoid = ci.connectioninfoid\njoin businessglossary bg on f.businessglossaryid=bg.businessglossaryid\njoin globaldomain gd ON (gd.globaldomainid = bg.globaldomainid)  \nunion all\nselect  bg.businessglossaryid\n from filecolumn fc join file f on fc.fileid = f.fileid\njoin filefolder ff on ff.filefolderid = f.filefolderid\njoin connectioninfo con on ff.connectioninfoid = con.connectioninfoid\njoin businessglossary bg on fc.businessglossaryid=bg.businessglossaryid\njoin globaldomain gd ON (gd.globaldomainid = bg.globaldomainid)  \nunion all\nselect bg.businessglossaryid  \nfrom chart c\njoin domain d on c.DOMAINID = d.DOMAINID\njoin connectioninfo ci on ci.connectioninfoid = d.connectioninfoid\njoin businessglossary bg on c.businessglossaryid=bg.businessglossaryid\njoin globaldomain gd ON (gd.globaldomainid = bg.globaldomainid)  \nunion all\nselect bg.businessglossaryid  \nfrom chartchild ch join chart c ON c.CHARTID = ch.chartid\njoin domain d on c.DOMAINID = d.DOMAINID\njoin connectioninfo ci on ci.connectioninfoid = d.connectioninfoid\njoin businessglossary bg on ch.businessglossaryid=bg.businessglossaryid\njoin globaldomain gd ON (gd.globaldomainid = bg.globaldomainid)\nunion all\nselect bg.businessglossaryid\nfrom dataset d join oeschema os on os.schemaname = d.userselecteddb\njoin connectioninfo con on os.connectioninfoid = con.connectioninfoid and os.connectioninfoid = d.connectioninfoid\njoin businessglossary bg on d.businessglossaryid=bg.businessglossaryid\njoin globaldomain gd ON (gd.globaldomainid = bg.globaldomainid)  )\ngroup by gd.domain order by count(bg.name) desc', 'select tmp.domain,Term as Term,status,description,steward,approvedby  from (\nselect gd.domain,bg.name as Term,bg.status,bg.description,bg.steward,bg.approvedby\nfrom businessglossary bg\njoin globaldomain gd ON (gd.globaldomainid = bg.globaldomainid)\nwhere businessglossaryid not in\n(select  bg.businessglossaryid  from oetable ot\njoin oeschema os on os.oeschemaid = ot.oeschemaid\njoin connectioninfo con on os.connectioninfoid = con.connectioninfoid\njoin businessglossary bg on ot.businessglossaryid=bg.businessglossaryid\njoin globaldomain gd ON (gd.globaldomainid = bg.globaldomainid)\nunion all\nselect bg.businessglossaryid  from  oecolumn oc\njoin oetable ot  on (oc.oetableid=ot.oetableid)\njoin oeschema os on os.oeschemaid = ot.oeschemaid\njoin connectioninfo con on os.connectioninfoid = con.connectioninfoid\njoin businessglossary bg on oc.businessglossaryid=bg.businessglossaryid\njoin globaldomain gd ON (gd.globaldomainid = bg.globaldomainid)\nunion all\nselect bg.businessglossaryid\nfrom oeschema os\njoin connectioninfo ci on os.connectioninfoid = ci.connectioninfoid  \njoin businessglossary bg on os.businessglossaryid=bg.businessglossaryid\njoin globaldomain gd on gd.globaldomainid=bg.globaldomainid\nunion all\nselect bg.businessglossaryid  \nfrom file f\njoin filefolder ff on f.filefolderid = ff.filefolderid\njoin connectioninfo ci on ff.connectioninfoid = ci.connectioninfoid\njoin businessglossary bg on f.businessglossaryid=bg.businessglossaryid\njoin globaldomain gd ON (gd.globaldomainid = bg.globaldomainid)  \nunion all\nselect  bg.businessglossaryid\n from filecolumn fc join file f on fc.fileid = f.fileid\njoin filefolder ff on ff.filefolderid = f.filefolderid\njoin connectioninfo con on ff.connectioninfoid = con.connectioninfoid\njoin businessglossary bg on fc.businessglossaryid=bg.businessglossaryid\njoin globaldomain gd ON (gd.globaldomainid = bg.globaldomainid)  \nunion all\nselect bg.businessglossaryid  \nfrom chart c\njoin domain d on c.DOMAINID = d.DOMAINID\njoin connectioninfo ci on ci.connectioninfoid = d.connectioninfoid\njoin businessglossary bg on c.businessglossaryid=bg.businessglossaryid\njoin globaldomain gd ON (gd.globaldomainid = bg.globaldomainid)  \nunion all\nselect bg.businessglossaryid  \nfrom chartchild ch join chart c ON c.CHARTID = ch.chartid\njoin domain d on c.DOMAINID = d.DOMAINID\njoin connectioninfo ci on ci.connectioninfoid = d.connectioninfoid\njoin businessglossary bg on ch.businessglossaryid=bg.businessglossaryid\njoin globaldomain gd ON (gd.globaldomainid = bg.globaldomainid)\nunion all\nselect bg.businessglossaryid\nfrom dataset d join oeschema os on os.schemaname = d.userselecteddb\njoin connectioninfo con on os.connectioninfoid = con.connectioninfoid and os.connectioninfoid = d.connectioninfoid\njoin businessglossary bg on d.businessglossaryid=bg.businessglossaryid\njoin globaldomain gd ON (gd.globaldomainid = bg.globaldomainid)  )\n) tmp\nwhere  tmp.domain=?', 'select gd.domain,count(bg.name) as \"No of Terms WithOut Data Association\"\nfrom businessglossary bg\njoin globaldomain gd ON (gd.globaldomainid = bg.globaldomainid)\nwhere businessglossaryid not in\n(select  bg.businessglossaryid  from oetable ot\njoin oeschema os on os.oeschemaid = ot.oeschemaid\njoin connectioninfo con on os.connectioninfoid = con.connectioninfoid\njoin businessglossary bg on ot.businessglossaryid=bg.businessglossaryid\njoin globaldomain gd ON (gd.globaldomainid = bg.globaldomainid)\nunion all\nselect bg.businessglossaryid  from  oecolumn oc\njoin oetable ot  on (oc.oetableid=ot.oetableid)\njoin oeschema os on os.oeschemaid = ot.oeschemaid\njoin connectioninfo con on os.connectioninfoid = con.connectioninfoid\njoin businessglossary bg on oc.businessglossaryid=bg.businessglossaryid\njoin globaldomain gd ON (gd.globaldomainid = bg.globaldomainid)\nunion all\nselect bg.businessglossaryid\nfrom oeschema os\njoin connectioninfo ci on os.connectioninfoid = ci.connectioninfoid  \njoin businessglossary bg on os.businessglossaryid=bg.businessglossaryid\njoin globaldomain gd on gd.globaldomainid=bg.globaldomainid\nunion all\nselect bg.businessglossaryid  \nfrom file f\njoin filefolder ff on f.filefolderid = ff.filefolderid\njoin connectioninfo ci on ff.connectioninfoid = ci.connectioninfoid\njoin businessglossary bg on f.businessglossaryid=bg.businessglossaryid\njoin globaldomain gd ON (gd.globaldomainid = bg.globaldomainid)  \nunion all\nselect  bg.businessglossaryid\n from filecolumn fc join file f on fc.fileid = f.fileid\njoin filefolder ff on ff.filefolderid = f.filefolderid\njoin connectioninfo con on ff.connectioninfoid = con.connectioninfoid\njoin businessglossary bg on fc.businessglossaryid=bg.businessglossaryid\njoin globaldomain gd ON (gd.globaldomainid = bg.globaldomainid)  \nunion all\nselect bg.businessglossaryid  \nfrom chart c\njoin domain d on c.DOMAINID = d.DOMAINID\njoin connectioninfo ci on ci.connectioninfoid = d.connectioninfoid\njoin businessglossary bg on c.businessglossaryid=bg.businessglossaryid\njoin globaldomain gd ON (gd.globaldomainid = bg.globaldomainid)  \nunion all\nselect bg.businessglossaryid  \nfrom chartchild ch join chart c ON c.CHARTID = ch.chartid\njoin domain d on c.DOMAINID = d.DOMAINID\njoin connectioninfo ci on ci.connectioninfoid = d.connectioninfoid\njoin businessglossary bg on ch.businessglossaryid=bg.businessglossaryid\njoin globaldomain gd ON (gd.globaldomainid = bg.globaldomainid)\nunion all\nselect bg.businessglossaryid\nfrom dataset d join oeschema os on os.schemaname = d.userselecteddb\njoin connectioninfo con on os.connectioninfoid = con.connectioninfoid and os.connectioninfoid = d.connectioninfoid\njoin businessglossary bg on d.businessglossaryid=bg.businessglossaryid\njoin globaldomain gd ON (gd.globaldomainid = bg.globaldomainid)  )\ngroup by gd.domain order by count(bg.name) desc', '0', 'Invalid input', null);
INSERT INTO `stg_oereportext` VALUES ('1037', 'PII Terms - Custom', '-1', 'OvalEdge Reports', 'This report shows how many terms within a domain are classified as PII.', 'select Domain, count(Term) as \"#PII terms\" from\n(select gd.domain as Domain, bg.businessglossaryid as bgid, bg.name as Term, (case when bg.mask=1 then \"Yes\" else \"No\" end ) as Masked,(case when bg.rstrct=1 then \"Yes\" else \"No\" end ) Restricted,\nCASE\n	when dc.classification1 = \"PII Flag\" and bg.classification1 = 1 then 1\n	when dc.classification2 = \"PII Flag\" and bg.classification2 = 1 then 1\n	when dc.classification3 = \"PII Flag\" and bg.classification3 = 1 then 1\n	when dc.classification4 = \"PII Flag\" and bg.classification4 = 1 then 1\n	when dc.classification5 = \"PII Flag\" and bg.classification5 = 1 then 1\n	when dc.classification6 = \"PII Flag\" and bg.classification6 = 1 then 1\n	when dc.classification7 = \"PII Flag\" and bg.classification7 = 1 then 1\n	when dc.classification8 = \"PII Flag\" and bg.classification8 = 1 then 1\n	when dc.classification9 = \"PII Flag\" and bg.classification9 = 1 then 1\n	when dc.classification10 = \"PII Flag\" and bg.classification9 = 1 then 1\n	ELSE 0 \nEND as satisfied\nfrom  businessglossary bg  join globaldomain gd  on (bg.globaldomainid=gd.globaldomainid)\njoin dataclassification dc on (dc.globaldomainid=bg.globaldomainid)\nwhere \"PII Flag\" in (dc.classification1,dc.classification2,dc.classification3,dc.classification4,dc.classification5,dc.classification6,dc.classification7,dc.classification8,\ndc.classification9,dc.classification10) ) a \njoin businessglossary bg on a.bgid = bg.businessglossaryid\nwhere a.satisfied = 1\ngroup by a.domain order by count(bg.businessglossaryid) desc', 'select * from ( select Domain, Term, Masked, Restricted from\n(select gd.domain as Domain, bg.businessglossaryid as bgid, bg.name as Term, (case when bg.mask=1 then \"Yes\" else \"No\" end ) as Masked,(case when bg.rstrct=1 then \"Yes\" else \"No\" end ) Restricted,\nCASE\n	when dc.classification1 = \"PII Flag\" and bg.classification1 = 1 then 1\n	when dc.classification2 = \"PII Flag\" and bg.classification2 = 1 then 1\n	when dc.classification3 = \"PII Flag\" and bg.classification3 = 1 then 1\n	when dc.classification4 = \"PII Flag\" and bg.classification4 = 1 then 1\n	when dc.classification5 = \"PII Flag\" and bg.classification5 = 1 then 1\n	when dc.classification6 = \"PII Flag\" and bg.classification6 = 1 then 1\n	when dc.classification7 = \"PII Flag\" and bg.classification7 = 1 then 1\n	when dc.classification8 = \"PII Flag\" and bg.classification8 = 1 then 1\n	when dc.classification9 = \"PII Flag\" and bg.classification9 = 1 then 1\n	when dc.classification10 = \"PII Flag\" and bg.classification9 = 1 then 1\n	ELSE 0 \nEND as satisfied\nfrom  businessglossary bg  join globaldomain gd  on (bg.globaldomainid=gd.globaldomainid)\njoin dataclassification dc on (dc.globaldomainid=bg.globaldomainid)\nwhere \"PII Flag\" in (dc.classification1,dc.classification2,dc.classification3,dc.classification4,dc.classification5,dc.classification6,dc.classification7,dc.classification8,\ndc.classification9,dc.classification10) ) a \njoin businessglossary bg on a.bgid = bg.businessglossaryid\nwhere a.satisfied = 1 ) tmp\nwhere tmp.Domain=?', 'select Domain, count(Term) as \"#PII Terms\" from\n(select gd.domain as Domain, bg.businessglossaryid as bgid, bg.name as Term, (case when bg.mask=1 then \"Yes\" else \"No\" end ) as Masked,(case when bg.rstrct=1 then \"Yes\" else \"No\" end ) Restricted,\nCASE\n	when dc.classification1 = \"PII Flag\" and bg.classification1 = 1 then 1\n	when dc.classification2 = \"PII Flag\" and bg.classification2 = 1 then 1\n	when dc.classification3 = \"PII Flag\" and bg.classification3 = 1 then 1\n	when dc.classification4 = \"PII Flag\" and bg.classification4 = 1 then 1\n	when dc.classification5 = \"PII Flag\" and bg.classification5 = 1 then 1\n	when dc.classification6 = \"PII Flag\" and bg.classification6 = 1 then 1\n	when dc.classification7 = \"PII Flag\" and bg.classification7 = 1 then 1\n	when dc.classification8 = \"PII Flag\" and bg.classification8 = 1 then 1\n	when dc.classification9 = \"PII Flag\" and bg.classification9 = 1 then 1\n	when dc.classification10 = \"PII Flag\" and bg.classification9 = 1 then 1\n	ELSE 0 \nEND as satisfied\nfrom  businessglossary bg  join globaldomain gd  on (bg.globaldomainid=gd.globaldomainid)\njoin dataclassification dc on (dc.globaldomainid=bg.globaldomainid)\nwhere \"PII Flag\" in (dc.classification1,dc.classification2,dc.classification3,dc.classification4,dc.classification5,dc.classification6,dc.classification7,dc.classification8,\ndc.classification9,dc.classification10) ) a \njoin businessglossary bg on a.bgid = bg.businessglossaryid\nwhere a.satisfied = 1\ngroup by a.domain order by count(bg.businessglossaryid) desc', '0', 'Invalid input', null);
INSERT INTO `stg_oereportext` VALUES ('1038', 'BHG-Null Count', '-1', 'OvalEdge Reports', 'This report shows total count of table / file objects having null values. ', 'select DatabaseName as \"Database Name\",Connectiontype as  \"Connection type\", SchemaName as \"SchemaName/Folder Name\",sum(Countofobjects)  as \"Count of objects having Null values\" from (select ci.name as \"DatabaseName\",ci.servertype as \"Connectiontype\",os.schemaname as \"SchemaName\", count(distinct ot.oetableid) as \"Countofobjects\" from oecolumn oc\n join oetable ot on (oc.oetableid=ot.oetableid)\njoin oeschema os on (os.oeschemaid=ot.oeschemaid)\njoin connectioninfo ci on (ci.connectioninfoid=os.connectioninfoid)\nwhere oc.nullcount <>0\ngroup by ci.name,ci.servertype,ot.tablename, os.schemaname\nunion all\nselect ci.name as \"DatabaseName\",ci.servertype as \"Connectiontype\",ff.filefolder as \"SchemaName\" ,count(distinct f.fileid) as \"Countofobjects\" from filecolumn fc\njoin file f on (fc.fileid=f.fileid)\njoin filefolder ff on (ff.filefolderid=f.filefolderid)\njoin connectioninfo ci on (ci.connectioninfoid=ff.connectioninfoid)\nwhere fc.nullcount<>0\ngroup by ci.name,ci.servertype,f.filename, ff.filefolder ) tmp\ngroup by DatabaseName,Connectiontype, SchemaName\n', 'select * from (select ci.name as \"Database Name\",ci.servertype as \"Connection type\",ot.tablename as \"TableName/Filename\",oc.columnname as \"Tablecolumn/Filecolumn\", \"Table\" as DataObject,sum(oc.nullcount) as \"Null Count\" from oecolumn oc\n join oetable ot on (oc.oetableid=ot.oetableid)\njoin oeschema os on (os.oeschemaid=ot.oeschemaid)\njoin connectioninfo ci on (ci.connectioninfoid=os.connectioninfoid)\nwhere oc.nullcount <>0\ngroup by ci.name,ci.servertype,ot.tablename,oc.columnname\nunion all\nselect ci.name as \"Database Name\",ci.servertype as \"Connection type\",f.filename as \"TableName/Filename\" ,fc.columnname as \"Tablecolumn/Filecolumn\",\"File\" as DataObject,sum(fc.nullcount) as \"Null Count\" from filecolumn fc\njoin file f on (fc.fileid=f.fileid)\njoin filefolder ff on (ff.filefolderid=f.filefolderid)\njoin connectioninfo ci on (ci.connectioninfoid=ff.connectioninfoid)\nwhere fc.nullcount<>0\ngroup by ci.name,ci.servertype,f.filename,fc.columnname\n) tmp where DataObject=?', 'select \"Table\" Object ,count(distinct ot.oetableid) as \"#Objects\" from oecolumn oc\n join oetable ot on (oc.oetableid=ot.oetableid)\njoin oeschema os on (os.oeschemaid=ot.oeschemaid)\njoin connectioninfo ci on (ci.connectioninfoid=os.connectioninfoid)\nwhere oc.nullcount <>0\nunion all\nselect \"File\" Object ,Count(distinct f.fileid) as \"#Objects\" from filecolumn fc\njoin file f on (fc.fileid=f.fileid)\njoin filefolder ff on (ff.filefolderid=f.filefolderid)\njoin connectioninfo ci on (ci.connectioninfoid=ff.connectioninfoid)\nwhere fc.nullcount<>0', '0', 'Invalid input', null);

