-- View structure for `oechartownersteward`
-- ----------------------------
DROP VIEW IF EXISTS `oechartownersteward`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `oechartownersteward` AS select `dag`.`owner` AS `owner`,`dag`.`steward` AS `steward`,`c`.`CHARTID` AS `chartid` from (`chart` `c` join `dataassetgroup` `dag` on(((`c`.`DOMAINID` = `dag`.`objectid`) and (`dag`.`objecttype` = 'oedomain')))) where (not(`c`.`CHARTID` in (select `ct`.`CHARTID` from (`dataassetgroup` `dag` join `charttag` `ct` on((`ct`.`TAGID` = `dag`.`tagid`)))))) union select `dag`.`owner` AS `owner`,`dag`.`steward` AS `steward`,`ct`.`CHARTID` AS `chartid` from (`dataassetgroup` `dag` join `charttag` `ct` on((`ct`.`TAGID` = `dag`.`tagid`)));

-- ----------------------------
-- View structure for `oefileownersteward`
-- ----------------------------
DROP VIEW IF EXISTS `oefileownersteward`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `oefileownersteward` AS select `dag`.`owner` AS `owner`,`dag`.`steward` AS `steward`,`f`.`fileid` AS `fileid` from (`file` `f` join `dataassetgroup` `dag` on(((`f`.`filefolderid` = `dag`.`objectid`) and (`dag`.`objecttype` = 'filefolder')))) where (not(`f`.`fileid` in (select `ft`.`fileid` from (`dataassetgroup` `dag` join `filetag` `ft` on((`ft`.`TAGID` = `dag`.`tagid`)))))) union select `dag`.`owner` AS `owner`,`dag`.`steward` AS `steward`,`ft`.`fileid` AS `fileid` from (`dataassetgroup` `dag` join `filetag` `ft` on((`ft`.`TAGID` = `dag`.`tagid`)));

