-- ----------------------------
-- View structure for `oesecurehashtagmask`
-- ----------------------------
DROP VIEW IF EXISTS `oesecurehashtagmask`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `oesecurehashtagmask` AS select `oi`.`object_id_identity` AS `hashtagid`,`usr`.`USERID` AS `userid`,max(`entry`.`mask`) AS `mask` from (((((`acl_object_identity` `oi` join `acl_class` `class` on((`oi`.`object_id_class` = `class`.`id`))) join `acl_entry` `entry` on((`entry`.`acl_object_identity` = `oi`.`id`))) join `acl_sid` `sid` on((`entry`.`sid` = `sid`.`id`))) join `roles` `role` on((`sid`.`sid` = `role`.`AUTHORITY`))) join `user_role` `usr` on((`usr`.`ROLEID` = `role`.`ROLEID`))) where (`class`.`class` = 'com.ovaledge.oasis.domain.HashTag') group by `hashtagid`,`usr`.`USERID`;

