DROP VIEW IF EXISTS `oetableownersteward`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `oetableownersteward` AS select `dag`.`owner` AS `owner`,`dag`.`steward` AS `steward`,`ot`.`oetableid` AS `oetableid` from (`oetable` `ot` join `dataassetgroup` `dag` on(((`ot`.`oeschemaid` = `dag`.`objectid`) and (`dag`.`objecttype` = 'oeschema')))) where (not(`ot`.`oetableid` in (select `tt`.`OETABLEID` from (`dataassetgroup` `dag` join `tabletag` `tt` on((`tt`.`TAGID` = `dag`.`tagid`)))))) union select `dag`.`owner` AS `owner`,`dag`.`steward` AS `steward`,`tt`.`OETABLEID` AS `oetableid` from (`dataassetgroup` `dag` join `tabletag` `tt` on((`tt`.`TAGID` = `dag`.`tagid`)));

